/**
| A hasher class for Sha1.
|
*/
#[derive(Debug, Builder, Getters)]
#[getset(get = "pub")]
pub struct Sha1  {
    s:     [u32; 5],
    buf:   [u8; 64],
    bytes: u64,
}

/**
| One round of SHA-1.
|
*/
#[inline(always)]
pub fn sha1_round(
    a:  u32,
    b:  &mut u32,
    _c: u32,
    _d: u32,
    e:  &mut u32,
    f:  u32,
    k:  u32,
    w:  u32
)
{
    trace!(
        "sha1_round: a={:#010x} b={:#010x} e={:#010x} f={:#010x} k={:#010x} w={:#010x}",
        a,
        *b,
        *e,
        f,
        k,
        w
    );
    *e = e
        .wrapping_add(a.rotate_left(5))
        .wrapping_add(f)
        .wrapping_add(k)
        .wrapping_add(w);
    *b = b.rotate_left(30);
}


#[inline(always)]
pub fn sha1_f1(b: u32, c: u32, d: u32) -> u32 {
    d ^ (b & (c ^ d))
}


#[inline(always)]
pub fn sha1_f2(b: u32, c: u32, d: u32) -> u32 {
    b ^ c ^ d
}


#[inline(always)]
pub fn sha1_f3(b: u32, c: u32, d: u32) -> u32 {
    (b & c) | (d & (b | c))
}


#[inline(always)]
pub fn sha1_left(x: u32) -> u32 {
    (x << 1) | (x >> 31)
}


/**
| Perform a SHA‑1 transformation on one
| 64‑byte block.
*/
#[inline]
pub fn sha1_transform(state: *mut u32, chunk: *const u8) {
    // ──────────────────────────── Constants
    const K1: u32 = 0x5A82_7999;
    const K2: u32 = 0x6ED9_EBA1;
    const K3: u32 = 0x8F1B_BCDC;
    const K4: u32 = 0xCA62_C1D6;

    unsafe {
        trace!(
            "sha1_transform: entry  \
             s0={:#010x} s1={:#010x} s2={:#010x} s3={:#010x} s4={:#010x}",
            *state.add(0),
            *state.add(1),
            *state.add(2),
            *state.add(3),
            *state.add(4)
        );

        // ─────────────── Load working variables
        let mut a = *state.add(0);
        let mut b = *state.add(1);
        let mut c = *state.add(2);
        let mut d = *state.add(3);
        let mut e = *state.add(4);

        // Message schedule as 16‑word rolling window
        let mut w = [0u32; 16];
        for i in 0..16 {
            w[i] = read_be32(chunk.add(i * 4));
        }

        // ─────────────── Main loop (80 rounds)
        for t in 0..80 {
            // Determine f and k per phase
            let (f, k) = match t {
                0..=19 => (sha1_f1(b, c, d), K1),
                20..=39 => (sha1_f2(b, c, d), K2),
                40..=59 => (sha1_f3(b, c, d), K3),
                _ => (sha1_f2(b, c, d), K4),
            };

            // Extend message schedule after the first 16 words
            let wt = if t < 16 {
                w[t]
            } else {
                let val = sha1_left(
                    w[(t + 13) & 15] ^ w[(t + 8) & 15] ^ w[(t + 2) & 15] ^ w[t & 15],
                );
                w[t & 15] = val;
                val
            };

            // Core round operation
            let temp = a
                .rotate_left(5)
                .wrapping_add(f)
                .wrapping_add(e)
                .wrapping_add(k)
                .wrapping_add(wt);

            e = d;
            d = c;
            c = b.rotate_left(30);
            b = a;
            a = temp;

            if cfg!(debug_assertions) && t % 20 == 19 {
                debug!(
                    "round {:02}: a={:#010x} b={:#010x} c={:#010x} d={:#010x} e={:#010x}",
                    t,
                    a,
                    b,
                    c,
                    d,
                    e
                );
            }
        }

        // ─────────────── Add this chunk’s hash to result so far
        *state.add(0) = (*state.add(0)).wrapping_add(a);
        *state.add(1) = (*state.add(1)).wrapping_add(b);
        *state.add(2) = (*state.add(2)).wrapping_add(c);
        *state.add(3) = (*state.add(3)).wrapping_add(d);
        *state.add(4) = (*state.add(4)).wrapping_add(e);

        trace!(
            "sha1_transform: exit   \
             s0={:#010x} s1={:#010x} s2={:#010x} s3={:#010x} s4={:#010x}",
            *state.add(0),
            *state.add(1),
            *state.add(2),
            *state.add(3),
            *state.add(4)
        );
    }
}


#[inline(always)]
pub fn read_be32(p: *const u8) -> u32 {
    u32::from_be_bytes([*p, *p.add(1), *p.add(2), *p.add(3)])
}


#[inline(always)]
pub fn write_be32(p: *mut u8, v: u32) {
    std::ptr::copy_nonoverlapping(v.to_be_bytes().as_ptr(), p, 4);
}


#[inline(always)]
pub fn write_be64(p: *mut u8, v: u64) {
    std::ptr::copy_nonoverlapping(v.to_be_bytes().as_ptr(), p, 8);
}


/**
| Initialize SHA-1 state.
|
*/
#[inline(always)]
pub fn sha1_initialize(s: *mut u32) {
    unsafe {
        *s.add(0) = 0x6745_2301;
        *s.add(1) = 0xEFCD_AB89;
        *s.add(2) = 0x98BA_DCFE;
        *s.add(3) = 0x1032_5476;
        *s.add(4) = 0xC3D2_E1F0;
    }
}

impl Default for Sha1 {

    fn default() -> Self {
        Self {
            s:     [0; 5],
            buf:   [0; 64],
            bytes: 0,
        }
    }
}

impl Sha1 {

    pub fn new() -> Self {
        let mut h = Sha1::default();
        unsafe { sha1_initialize(h.s.as_mut_ptr()) };
        info!("Sha1::new ‑ state initialised");
        h
    }

    pub fn write(&mut self, data: *const u8, len: usize) -> &mut Self {
        unsafe {
            let mut cur = data;
            let end = data.add(len);
            let mut buf_len = (self.bytes % 64) as usize;

            // Finish filling the internal buffer if necessary.
            if buf_len > 0 && buf_len + len >= 64 {
                std::ptr::copy_nonoverlapping(
                    cur,
                    self.buf.as_mut_ptr().add(buf_len),
                    64 - buf_len,
                );
                self.bytes += (64 - buf_len) as u64;
                cur = cur.add(64 - buf_len);
                sha1_transform(self.s.as_mut_ptr(), self.buf.as_ptr());
                buf_len = 0;
            }

            // Process full 64‑byte chunks straight from the input.
            while end.offset_from(cur) as usize >= 64 {
                sha1_transform(self.s.as_mut_ptr(), cur);
                self.bytes += 64;
                cur = cur.add(64);
            }

            // Buffer remaining data.
            if end > cur {
                let remaining = end.offset_from(cur) as usize;
                std::ptr::copy_nonoverlapping(
                    cur,
                    self.buf.as_mut_ptr().add(buf_len),
                    remaining,
                );
                self.bytes += remaining as u64;
            }
        }
        self
    }

    pub fn finalize(&mut self, hash_out: &mut [u8; SHA1_OUTPUT_SIZE]) {
        // Build padding (1000 0000 …).
        let mut pad = [0u8; 64];
        pad[0] = 0x80;

        let mut sizedesc = [0u8; 8];
        unsafe { write_be64(sizedesc.as_mut_ptr(), self.bytes << 3) };

        // Pad so that final length (including 8‑byte length) ≡ 0 mod 64.
        let pad_len = 1 + ((119 - (self.bytes % 64)) % 64) as usize;
        self.write(pad.as_ptr(), pad_len);
        self.write(sizedesc.as_ptr(), 8);

        // Produce the final digest in big‑endian.
        for (i, chunk) in hash_out.chunks_exact_mut(4).enumerate() {
            chunk.copy_from_slice(&self.s[i].to_be_bytes());
        }

        debug!("Sha1::finalize ‑ digest computed");
    }

    pub fn reset(&mut self) -> &mut Self {
        self.bytes = 0;
        unsafe { sha1_initialize(self.s.as_mut_ptr()) };
        info!("Sha1::reset ‑ state cleared");
        self
    }
}


