/**
| Value types encoded as the last component of
| internal keys.
|
| DO NOT CHANGE THESE ENUM VALUES: they are
| embedded in the on-disk data structures.
*/
#[repr(u8)]
#[derive(Copy, Clone, Debug, PartialEq, Eq, PartialOrd, Ord)]
pub enum ValueType {
    TypeDeletion,
    TypeValue,
}

pub trait Key {
    fn key(&self) -> Slice;
}


pub trait Value {
    fn value(&self) -> Slice;
}

pub type SequenceNumber = u64;

#[derive(Setters,Getters)]
#[getset(get="pub",set="pub")]
pub struct ParsedInternalKey  {
    user_key: Slice,
    sequence: SequenceNumber,
    ty:       ValueType,
}


/**
| Modules in this directory should keep internal
| keys wrapped inside the following class instead
| of plain strings so that we do not incorrectly
| use string comparisons instead of an
| InternalKeyComparator.
*/
#[derive(Clone)]
pub struct InternalKey  {
    rep: String,
}


/**
| Filter policy wrapper that converts
| from internal keys to user keys
|
*/
pub struct InternalFilterPolicy  {
    user_policy: *const dyn FilterPolicy,
}


/**
| A comparator for internal keys that
| uses a specified comparator for the
| user key portion and breaks ties by decreasing
| sequence number.
|
*/
pub struct InternalKeyComparator  {
    user_comparator: *const dyn SliceComparator,
}


/**
| A helper class useful for DBImpl::Get()
|
*/
pub struct LookupKey  {
    start:  *const u8,
    kstart: *const u8,
    end:    *const u8,
    space:  [u8; 200],
    buf:    Vec<u8>,
}

/**
| Pack sequence number and value type into a single
| 64-bit integer.
|
| We leave eight bits for the type.
*/
pub fn pack_sequence_and_type(seq: u64, t: ValueType) -> u64 {
    debug!(
        "pack_sequence_and_type: seq={}, ty={:?}",
        seq,
        t
    );
    assert!(
        seq <= MAX_SEQUENCE_NUMBER,
        "sequence {} exceeds MAX_SEQUENCE_NUMBER {}",
        seq,
        MAX_SEQUENCE_NUMBER
    );
    let tag = t as u64;
    assert!(
        tag <= VALUE_TYPE_FOR_SEEK as u64,
        "value type tag {} exceeds VALUE_TYPE_FOR_SEEK",
        tag
    );
    (seq << 8) | tag
}


/**
| Return the length of the encoding of
| "key".
|
*/
#[inline]
pub fn internal_key_encoding_length(k: &ParsedInternalKey) -> usize {
    let user_len = *k.user_key.size();
    let len = user_len + 8;
    trace!(
        "internal_key_encoding_length: user_key_len={} total_len={}",
        user_len,
        len
    );
    len
}


/**
| Attempt to parse an internal key from
| "internal_key".  On success, stores the parsed
| data in "*result", and returns true.
|
| On error, returns false, leaves "*result" in an
| undefined state.
*/
#[inline]
pub fn parse_internal_key(internal_key_: &Slice, result: *mut ParsedInternalKey) -> bool {
    let n = *internal_key_.size();
    trace!("parse_internal_key: len={}", n);
    if n < 8 {
        debug!(
            "parse_internal_key: too short ({} bytes)",
            n
        );
        return false;
    }

    assert!(
        !result.is_null(),
        "parse_internal_key: result pointer must not be null"
    );

    unsafe {
        let data = *internal_key_.data();
        let num = decode_fixed64_le(data.add(n - 8));
        let c = (num & 0xff) as u8;
        let seq = num >> 8;

        match ValueType::from_tag(c) {
            Some(ty) if (c as u64) <= (VALUE_TYPE_FOR_SEEK as u64) => {
                (*result).sequence = seq;
                (*result).ty = ty;
                (*result).user_key = Slice::from_ptr_len(data, n - 8);
                true
            }
            _ => {
                debug!(
                    "parse_internal_key: invalid tag={}, seq={}",
                    c,
                    seq
                );
                false
            }
        }
    }
}


/**
| Append the serialization of "key" to
| *result.
|
*/
pub fn append_internal_key(result: *mut String, k: &ParsedInternalKey) {
    trace!(
        "append_internal_key: seq={}, ty={:?}, user_key_len={}",
        k.sequence,
        k.ty,
        *k.user_key.size()
    );
    assert!(
        !result.is_null(),
        "append_internal_key: result pointer must not be null"
    );

    unsafe {
        let s: &mut String = &mut *result;
        let vec: &mut Vec<u8> = s.as_mut_vec();
        let user_slice = &k.user_key;
        let key_len = *user_slice.size();

        if key_len > 0 {
            let data = *user_slice.data();
            let bytes = std::slice::from_raw_parts(data, key_len);
            vec.extend_from_slice(bytes);
        }

        let packed = pack_sequence_and_type(k.sequence, k.ty);
        let tag = encode_fixed64_le(packed);
        vec.extend_from_slice(&tag);
    }
}


pub fn encode_fixed64_le(value: u64) -> [u8; 8] {
    let mut buf = [0u8; 8];
    for i in 0..8 {
        buf[i] = ((value >> (8 * i)) & 0xff) as u8;
    }
    buf
}


pub fn decode_fixed64_le(ptr: *const u8) -> u64 {
    let mut result = 0u64;
    for i in 0..8 {
        let b = *ptr.add(i) as u64;
        result |= b << (8 * i);
    }
    result
}


pub fn put_varint32_vec(dst: &mut Vec<u8>, mut v: u32) {
    while v >= 0x80 {
        dst.push(((v & 0x7f) as u8) | 0x80);
        v >>= 7;
    }
    dst.push(v as u8);
}


pub fn slice_as_bytes(s: &Slice) -> &[u8] {
    unsafe {
        let len = *s.size();
        if len == 0 {
            &[]
        } else {
            let data = *s.data();
            std::slice::from_raw_parts(data, len)
        }
    }
}


pub fn bytewise_compare(a: &[u8], b: &[u8]) -> i32 {
    use core::cmp::Ordering;
    match a.cmp(b) {
        Ordering::Less => -1,
        Ordering::Equal => 0,
        Ordering::Greater => 1,
    }
}


pub fn shorten_separator_user_keys(start: &[u8], limit: &[u8]) -> Option<Vec<u8>> {
    use core::cmp::min;

    let min_len = min(start.len(), limit.len());
    let mut diff_index = 0usize;

    while diff_index < min_len && start[diff_index] == limit[diff_index] {
        diff_index += 1;
    }

    if diff_index >= min_len {
        return None;
    }

    let diff_byte = start[diff_index];
    let limit_byte = limit[diff_index];

    if diff_byte < 0xff && diff_byte + 1 < limit_byte {
        let mut tmp = Vec::with_capacity(diff_index + 1);
        tmp.extend_from_slice(&start[..diff_index]);
        tmp.push(diff_byte + 1);
        Some(tmp)
    } else {
        None
    }
}


pub fn find_short_successor_user_key(key: &mut Vec<u8>) -> bool {
    for i in 0..key.len() {
        if key[i] != 0xff {
            key[i] = key[i].wrapping_add(1);
            key.truncate(i + 1);
            return true;
        }
    }
    false
}


pub fn escape_for_debug(input: &[u8]) -> String {
    let mut out = String::new();
    for &b in input {
        match b {
            b'\\' => out.push_str("\\\\"),
            b'"'  => out.push_str("\\\""),
            b'\n' => out.push_str("\\n"),
            b'\r' => out.push_str("\\r"),
            b'\t' => out.push_str("\\t"),
            0x20..=0x7e => out.push(b as char),
            _ => {
                out.push_str("\\x");
                out.push_str(&format!("{:02x}", b));
            }
        }
    }
    out
}


/// Helper: construct a null trait-object pointer to a SliceComparator.
///
/// We never dereference this; it is used only to exercise the
/// "no user comparator provided" code paths.
pub fn null_slice_comparator() -> *const dyn SliceComparator {
    unsafe {
        std::mem::transmute::<(usize, usize), *const dyn SliceComparator>((0, 0))
    }
}


/// Helper: decode a varint32 from a byte slice.
/// Returns (value, bytes_consumed).
pub fn decode_varint32(mut src: &[u8]) -> (u32, usize) {
    let mut result: u32 = 0;
    let mut shift: u32 = 0;
    let mut consumed: usize = 0;

    loop {
        assert!(
            !src.is_empty(),
            "decode_varint32: input exhausted before termination bit"
        );
        let b = src[0] as u32;
        src = &src[1..];
        consumed += 1;

        result |= (b & 0x7f) << shift;
        if (b & 0x80) == 0 {
            break;
        }
        shift += 7;
        assert!(shift < 32, "decode_varint32: shift overflow");
    }

    (result, consumed)
}


/**
| Returns the user key portion of an internal
| key.
|
*/
#[inline]
pub fn extract_user_key(internal_key_: &Slice) -> Slice {
    let n = *internal_key_.size();
    trace!("extract_user_key: internal_len={}", n);
    assert!(n >= 8, "internal key too short: {}", n);
    unsafe {
        let data = *internal_key_.data();
        Slice::from_ptr_len(data, n - 8)
    }
}

