    fn key_may_match(&self, k: &Slice, f: &Slice) -> bool {
        trace!("InternalFilterPolicy::key_may_match");
        unsafe {
            if self.user_policy.is_null() {
                return false;
            }
            let user_key = extract_user_key(k);
            (*self.user_policy).key_may_match(&user_key, f)
        }
    }
}

