/**
| Lower-level versions of Put... that write
| directly into a character buffer and return
| a pointer just past the last byte written.
|
| REQUIRES: dst has enough space for the value
| being written
*/
pub fn encode_varint32(dst: *mut u8, v: u32) -> *mut u8 {
    unsafe {
        trace!(
            ptr = ?dst,
            value = v,
            "encode_varint32: encoding 32-bit varint"
        );
        let mut ptr = dst;
        const B: u32 = 128;

        if v < (1 << 7) {
            *ptr = v as u8;
            ptr = ptr.add(1);
        } else if v < (1 << 14) {
            *ptr = (v | B) as u8;
            ptr = ptr.add(1);
            *ptr = (v >> 7) as u8;
            ptr = ptr.add(1);
        } else if v < (1 << 21) {
            *ptr = (v | B) as u8;
            ptr = ptr.add(1);
            *ptr = ((v >> 7) | B) as u8;
            ptr = ptr.add(1);
            *ptr = (v >> 14) as u8;
            ptr = ptr.add(1);
        } else if v < (1 << 28) {
            *ptr = (v | B) as u8;
            ptr = ptr.add(1);
            *ptr = ((v >> 7) | B) as u8;
            ptr = ptr.add(1);
            *ptr = ((v >> 14) | B) as u8;
            ptr = ptr.add(1);
            *ptr = (v >> 21) as u8;
            ptr = ptr.add(1);
        } else {
            *ptr = (v | B) as u8;
            ptr = ptr.add(1);
            *ptr = ((v >> 7) | B) as u8;
            ptr = ptr.add(1);
            *ptr = ((v >> 14) | B) as u8;
            ptr = ptr.add(1);
            *ptr = ((v >> 21) | B) as u8;
            ptr = ptr.add(1);
            *ptr = (v >> 28) as u8;
            ptr = ptr.add(1);
        }

        trace!(
            end_ptr = ?ptr,
            "encode_varint32: finished encoding varint32"
        );
        ptr
    }
}


pub fn encode_varint64(dst: *mut u8, v: u64) -> *mut u8 {
    unsafe {
        trace!(
            ptr = ?dst,
            value = v,
            "encode_varint64: encoding 64-bit varint"
        );
        let mut ptr = dst;
        let mut val = v;
        const B: u8 = 128;

        while val >= B as u64 {
            *ptr = (val as u8) | B;
            ptr = ptr.add(1);
            val >>= 7;
        }

        *ptr = val as u8;
        ptr = ptr.add(1);

        trace!(
            end_ptr = ?ptr,
            "encode_varint64: finished encoding varint64"
        );
        ptr
    }
}


/**
| Pointer-based variants of GetVarint...  These
| either store a value in *v and return a pointer
| just past the parsed value, or return nullptr
| on error.  These routines only look at bytes in
| the range [p..limit-1]
*/
#[inline]
pub fn get_varint_32ptr(p: *const u8, limit: *const u8, value: *mut u32) -> *const u8 {
    unsafe {
        trace!(
            ptr = ?p,
            limit = ?limit,
            "get_varint_32ptr: attempting fast-path varint32 decode"
        );
        if (p as usize) < (limit as usize) {
            let result = *p as u32;
            if (result & 0x80) == 0 {
                *value = result;
                let next = p.add(1);
                trace!(
                    result,
                    next = ?next,
                    "get_varint_32ptr: decoded single-byte varint32"
                );
                return next;
            }
        }
        debug!("get_varint_32ptr: falling back to multi-byte decoder");
        get_varint_32ptr_fallback(p, limit, value)
    }
}


/**
| Internal routine for use by fallback
| path of GetVarint32Ptr
|
*/
pub fn get_varint_32ptr_fallback(p: *const u8, limit: *const u8, value: *mut u32) -> *const u8 {
    unsafe {
        trace!(
            ptr = ?p,
            limit = ?limit,
            "get_varint_32ptr_fallback: starting fallback varint32 decode"
        );
        let mut result: u32 = 0;
        let mut shift: u32 = 0;
        let mut current = p;

        while shift <= 28 && (current as usize) < (limit as usize) {
            let byte = *current as u32;
            current = current.add(1);

            if (byte & 0x80) != 0 {
                result |= (byte & 0x7f) << shift;
            } else {
                result |= byte << shift;
                *value = result;
                trace!(
                    result,
                    next = ?current,
                    "get_varint_32ptr_fallback: successfully decoded varint32"
                );
                return current;
            }

            shift += 7;
        }

        warn!(
            "get_varint_32ptr_fallback: reached limit or overflow while decoding varint32"
        );
        core::ptr::null()
    }
}


/**
| Standard Get... routines parse a value
| from the beginning of a Slice and advance
| the slice past the parsed value.
|
*/
pub fn get_varint32(input: *mut Slice, value: *mut u32) -> bool {
    unsafe {
        trace!(
            input_ptr = ?input,
            "get_varint32: attempting to decode varint32 from Slice"
        );
        let base_ptr: *const u8 = *(*input).data();
        let size: usize = *(*input).size();
        let limit = base_ptr.add(size);

        let p = get_varint_32ptr(base_ptr, limit, value);
        if p.is_null() {
            warn!("get_varint32: decode failed, returning false");
            return false;
        }

        let consumed = p.offset_from(base_ptr) as usize;
        (*input).remove_prefix(consumed);
        debug!(
            consumed,
            remaining = *(*input).size(),
            "get_varint32: successfully decoded varint32 and advanced slice"
        );
        true
    }
}


