pub fn get_length_prefixed_slice(input: *mut Slice, result: *mut Slice) -> bool {
    unsafe {
        trace!(
            input_ptr = ?input,
            "get_length_prefixed_slice: starting decode from Slice"
        );

        let mut len32: u32 = 0;
        if !get_varint32(input, &mut len32 as *mut u32) {
            warn!(
                "get_length_prefixed_slice: failed to decode length varint32 from Slice"
            );
            return false;
        }

        let len = len32 as usize;
        let remaining_before = *(*input).size();

        if remaining_before < len {
            warn!(
                requested_len = len,
                remaining_before,
                "get_length_prefixed_slice: Slice shorter than declared length"
            );
            return false;
        }

        let base = *(*input).data();
        *result = Slice::from_ptr_len(base, len);
        (*input).remove_prefix(len);

        debug!(
            len,
            remaining_after = *(*input).size(),
            "get_length_prefixed_slice: successfully decoded length-prefixed slice"
        );
        true
    }
}


pub fn get_length_prefixed_slice_with_limit(p: *const u8, limit: *const u8, result: *mut Slice) -> *const u8 {
    unsafe {
        trace!(
            ptr = ?p,
            limit = ?limit,
            "get_length_prefixed_slice_with_limit: starting decode"
        );
        let mut len32: u32 = 0;
        let mut current = get_varint_32ptr(p, limit, &mut len32 as *mut u32);
        if current.is_null() {
            warn!(
                "get_length_prefixed_slice_with_limit: failed to decode length varint32"
            );
            return core::ptr::null();
        }

        let len = len32 as usize;
        let remaining = (limit as usize).wrapping_sub(current as usize);
        if remaining < len {
            warn!(
                len,
                remaining,
                "get_length_prefixed_slice_with_limit: not enough bytes for slice"
            );
            return core::ptr::null();
        }

        *result = Slice::from_ptr_len(current, len);
        let next = current.add(len);
        trace!(
            len,
            next = ?next,
            "get_length_prefixed_slice_with_limit: successfully decoded slice"
        );
        next
    }
}


pub fn slice_to_utf8(slice: &Slice) -> String {
    unsafe {
        let data: *const u8 = *slice.data();
        let len: usize = *slice.size();
        if data.is_null() || len == 0 {
            trace!(
                len,
                "slice_to_utf8: empty or null-backed slice, returning empty string"
            );
            return String::new();
        }
        let bytes = core::slice::from_raw_parts(data, len);
        let s = String::from_utf8_lossy(bytes).into_owned();
        trace!(
            len,
            preview = %s,
            "slice_to_utf8: converted slice to UTF-8 string"
        );
        s
    }
}


/**
| Lower-level versions of Get... that
| read directly from a character buffer
| without any bounds checking.
|
*/
#[inline]
pub fn decode_fixed32(ptr: *const u8) -> u32 {
    unsafe {
        let b0 = *ptr.add(0) as u32;
        let b1 = *ptr.add(1) as u32;
        let b2 = *ptr.add(2) as u32;
        let b3 = *ptr.add(3) as u32;
        let value = b0 | (b1 << 8) | (b2 << 16) | (b3 << 24);
        trace!(
            ptr = ?ptr,
            value,
            "decode_fixed32: decoded 32-bit little-endian value"
        );
        value
    }
}


#[inline]
pub fn decode_fixed64(ptr: *const u8) -> u64 {
    unsafe {
        let b0 = *ptr.add(0) as u64;
        let b1 = *ptr.add(1) as u64;
        let b2 = *ptr.add(2) as u64;
        let b3 = *ptr.add(3) as u64;
        let b4 = *ptr.add(4) as u64;
        let b5 = *ptr.add(5) as u64;
        let b6 = *ptr.add(6) as u64;
        let b7 = *ptr.add(7) as u64;
        let value = b0
            | (b1 << 8)
            | (b2 << 16)
            | (b3 << 24)
            | (b4 << 32)
            | (b5 << 40)
            | (b6 << 48)
            | (b7 << 56);
        trace!(
            ptr = ?ptr,
            value,
            "decode_fixed64: decoded 64-bit little-endian value"
        );
        value
    }
}


pub fn put_length_prefixed_slice(dst: *mut String, value: &Slice) {
    unsafe {
        let len: usize = *value.size();
        trace!(
            len,
            ptr = ?dst,
            "put_length_prefixed_slice: appending length-prefixed slice"
        );
        if len > u32::MAX as usize {
            error!(
                len,
                "put_length_prefixed_slice: slice length exceeds u32::MAX"
            );
            return;
        }

        put_varint32(dst, len as u32);

        if len == 0 {
            debug!(
                "put_length_prefixed_slice: empty slice, nothing more to append"
            );
            return;
        }

        let data_ptr: *const u8 = *value.data();
        let s: &mut String = &mut *dst;
        let vec = s.as_mut_vec();
        let bytes = core::slice::from_raw_parts(data_ptr, len);
        vec.extend_from_slice(bytes);
        debug!(
            new_len = vec.len(),
            "put_length_prefixed_slice: buffer length after append"
        );
    }
}


/**
| Returns the length of the varint32 or
| varint64 encoding of "v"
|
*/
pub fn varint_length(mut v: u64) -> i32 {
    let mut len: i32 = 1;
    trace!(
        value = v,
        "varint_length: computing varint length for value"
    );
    while v >= 128 {
        v >>= 7;
        len += 1;
    }
    debug!(len, "varint_length: computed varint length");
    len
}



