pub type LimbArr5 = [u32; 5];


pub type LimbArr4 = [u32; 4];

/// 32‑bit × 32‑bit → 64‑bit multiply (matches the C `((uint64_t)(a)*(b))`).
#[macro_export]
macro_rules! mul32x32_64 { {
    ($a:expr, $b:expr) => {
        (($a as u64).wrapping_mul($b as u64))
    };
} }


/// Small helper: emit a `TRACE` event whose `step` field is fixed and all
/// remaining tokens are forwarded verbatim to `tracing::trace!`.  This lets
/// you write e.g.  
/// `trace_step!("c‑prop‑0", { c, h_before = h0, h_after = h[0] });`
#[macro_export]
macro_rules! trace_step { {
    ($step:expr, { $($field:tt)* }) => {
        tracing::trace!(
            step = $step,
            $($field)*
        );
    };
} }

#[inline(always)]
pub fn read_le32(input: &[u8]) -> u32 {
    debug_assert!(input.len() >= 4);
    let val = u32::from_le_bytes(input[0..4].try_into().expect("slice len == 4"));
    tracing::trace!(bytes = ?&input[0..4], val, "read_le32");
    val
}


#[inline(always)]
pub fn write_le32(dst: &mut [u8], v: u64) {
    debug_assert!(dst.len() >= 4);
    dst[..4].copy_from_slice(&(v as u32).to_le_bytes());
    tracing::trace!(bytes = ?&dst[..4], val = (v as u32), "write_le32");
}


#[inline(always)]
pub(crate) fn final_carry_and_sub_p(h: &mut [u32; 5]) {
    // 1. finish the single carry‑propagation pass
    propagate_26bit_carries_once(h);

    // 2. compute g = h + 5  and  g_minus_p = g − p
    let (_g, g_minus_p, borrow) = compute_g_plus5_minus_p(h);

    // 3. constant‑time selection
    //    borrow == 0  →  g wrapped past p  →  take g_minus_p
    //    borrow == 1  →  no wrap           →  keep the original h
    let mask = borrow.wrapping_sub(1);      // 0xFFFF_FFFF if borrow==0 else 0
    ct_select_limbs(h, &g_minus_p, mask);   // one *single* selection, no g

    tracing::debug!(h_reduced = ?*h, "final_carry_and_sub_p: finished");
}


/// Propagate carries once.
///
/// * `h` – mutable reference to the 5‑limb vector.
/// This routine is **constant–time** with respect to the limb values
/// (no data‑dependent branching).
#[inline]
pub fn propagate_26bit_carries_once(h: &mut [u32; 5]) {
    // ------------------------------------------------------------------
    // Step 0 : initial state
    // ------------------------------------------------------------------
    trace_step!("prop‑start", { h = ?h });

    // ------------------------------------------------------------------
    // Limb‑by‑limb carries: 0 → 1 → 2 → 3 → 4
    // ------------------------------------------------------------------
    // —— carry out of limb 0 ——
    let c = h[0] >> 26;
    let h0_before = h[0];
    h[0] &= MASK;
    h[1] = h[1].wrapping_add(c);
    trace_step!("prop‑c0", { c, h0_before, h0_after = h[0] });

    // —— carry out of limb 1 ——
    let c = h[1] >> 26;
    let h1_before = h[1];
    h[1] &= MASK;
    h[2] = h[2].wrapping_add(c);
    trace_step!("prop‑c1", { c, h1_before });

    // —— carry out of limb 2 ——
    let c = h[2] >> 26;
    let h2_before = h[2];
    h[2] &= MASK;
    h[3] = h[3].wrapping_add(c);
    trace_step!("prop‑c2", { c, h2_before });

    // —— carry out of limb 3 ——
    let c = h[3] >> 26;
    let h3_before = h[3];
    h[3] &= MASK;
    h[4] = h[4].wrapping_add(c);
    trace_step!("prop‑c3", { c, h3_before });

    // ------------------------------------------------------------------
    // Top‑limb wrap‑around (2^130 ≡ 5 mod p)
    // ------------------------------------------------------------------
    let c = h[4] >> 26;                 // overflow from limb 4
    let h4_before = h[4];
    h[4] &= MASK;
    h[0] = h[0].wrapping_add(c.wrapping_mul(5));
    trace_step!("prop‑c4", { c, h4_before });

    // ------------------------------------------------------------------
    // One extra fix‑up carry for h[0]  →  h[1]
    // (after adding 5·c, h0 may now be ≥ 2^26).
    // A single extra pass is sufficient: 5 ≤ 2^26, so the carry is ≤ 4.
    // ------------------------------------------------------------------
    let c = h[0] >> 26;
    let h0_before_fix = h[0];
    h[0] &= MASK;
    h[1] = h[1].wrapping_add(c);
    trace_step!("prop‑c5", { c, h0_before_fix });

    // ------------------------------------------------------------------
    // Done
    // ------------------------------------------------------------------
    trace_step!("prop‑done", { h = ?h });
}


#[inline(always)]
pub fn accumulate_block(h: &mut LimbArr5, block: &[u8; 16], add_high_bit: bool) {
    let h_before = *h;
    tracing::trace!(
        h_before = ?h_before,
        block     = ?block,
        add_high_bit,
        "accumulate_block: start"
    );

    let t0 = read_le32(&block[0..]);
    let t1 = read_le32(&block[4..]);
    let t2 = read_le32(&block[8..]);
    let t3 = read_le32(&block[12..]);
    tracing::debug!(t0, t1, t2, t3, "accumulate_block: parsed little‑endian words");

    h[0] = h[0].wrapping_add(t0 & 0x3ffffff);
    h[1] = h[1].wrapping_add((((t1 as u64) << 32 | t0 as u64) >> 26) as u32 & 0x3ffffff);
    h[2] = h[2].wrapping_add((((t2 as u64) << 32 | t1 as u64) >> 20) as u32 & 0x3ffffff);
    h[3] = h[3].wrapping_add((((t3 as u64) << 32 | t2 as u64) >> 14) as u32 & 0x3ffffff);
    h[4] = h[4].wrapping_add((t3 >> 8) | if add_high_bit { 1 << 24 } else { 0 });

    let h_after = *h;
    tracing::trace!(h_after = ?h_after, "accumulate_block: finished");
}


/// After `final_carry_and_sub_p` we have five 26‑bit limbs `h[0..=4]`,
/// each < 2²⁶.  This function
///
/// 1. Packs them into the four 32‑bit words of the reference
///    implementation (truncating → u32 immediately, *exactly* as Donna did).
/// 2. Adds the 128‑bit pad `s`, propagating carries word‑wise.
/// 3. Writes the resulting 16‑byte tag (little‑endian).
/// Extra **trace‑level** instrumentation has been added so that every
/// intermediate value, carry and final tag can be inspected when tests are
/// executed with `RUST_LOG=trace`.
#[inline(always)]
pub(crate) fn add_pad_serialize(h: [u32; 5], key: &[u8; 32]) -> [u8; 16] {
    //--------------------------------------------------------------------
    // 0.  Trace inputs
    //--------------------------------------------------------------------
    tracing::trace!(
        ?h,
        pad_hi = ?&key[16..],
        "add_pad_serialize: start"
    );

    //--------------------------------------------------------------------
    // 1.  Radix‑26 → radix‑32 packing (immediate u32 truncation)
    //--------------------------------------------------------------------
    let mut f0: u32 = (h[0])          | (h[1] << 26);
    let mut f1: u32 = (h[1] >> 6)     | (h[2] << 20);
    let mut f2: u32 = (h[2] >> 12)    | (h[3] << 14);
    let mut f3: u32 = (h[3] >> 18)    | (h[4] <<  8);

    tracing::debug!(
        f0_raw = f0,
        f1_raw = f1,
        f2_raw = f2,
        f3_raw = f3,
        "add_pad_serialize: packed (truncated) words"
    );

    //--------------------------------------------------------------------
    // 2.  Add the 128‑bit pad – carry‑propagate word‑wise (constant‑time)
    //--------------------------------------------------------------------
    let s0 = u32::from_le_bytes(key[16..20].try_into().unwrap()) as u64;
    let s1 = u32::from_le_bytes(key[20..24].try_into().unwrap()) as u64;
    let s2 = u32::from_le_bytes(key[24..28].try_into().unwrap()) as u64;
    let s3 = u32::from_le_bytes(key[28..32].try_into().unwrap()) as u64;

    tracing::debug!(s0, s1, s2, s3, "add_pad_serialize: pad words");

    let mut acc: u64 = f0 as u64 + s0;
    f0 = acc as u32;
    let mut c = acc >> 32;
    tracing::trace!(step = "add_pad", acc, carry_out = c, f0);

    acc = f1 as u64 + s1 + c;
    f1 = acc as u32;
    c   = acc >> 32;
    tracing::trace!(step = "add_pad", acc, carry_out = c, f1);

    acc = f2 as u64 + s2 + c;
    f2 = acc as u32;
    c   = acc >> 32;
    tracing::trace!(step = "add_pad", acc, carry_out = c, f2);

    acc = f3 as u64 + s3 + c;
    f3 = acc as u32;
    tracing::trace!(step = "add_pad", acc, carry_out_final = (acc >> 32), f3);

    //--------------------------------------------------------------------
    // 3.  Serialise tag
    //--------------------------------------------------------------------
    let mut tag = [0u8; 16];
    tag[ 0.. 4].copy_from_slice(&f0.to_le_bytes());
    tag[ 4.. 8].copy_from_slice(&f1.to_le_bytes());
    tag[ 8..12].copy_from_slice(&f2.to_le_bytes());
    tag[12..16].copy_from_slice(&f3.to_le_bytes());

    tracing::debug!(?tag, "add_pad_serialize: finished");
    tag
}


#[inline(always)]
pub fn multiply_and_reduce(h: &mut LimbArr5, r: &LimbArr5, s: &LimbArr4) {
    let h_in = *h;
    tracing::trace!(h_in = ?h_in, ?r, ?s, "multiply_and_reduce: start");

    // 64‑bit products before any carries
    let mut t = [0u64; 5];
    t[0] = mul32x32_64!(h[0], r[0])
        + mul32x32_64!(h[1], s[3])
        + mul32x32_64!(h[2], s[2])
        + mul32x32_64!(h[3], s[1])
        + mul32x32_64!(h[4], s[0]);
    trace_step!("mul‑raw‑prod", { t = ?t });
    t[1] = mul32x32_64!(h[0], r[1])
        + mul32x32_64!(h[1], r[0])
        + mul32x32_64!(h[2], s[3])
        + mul32x32_64!(h[3], s[2])
        + mul32x32_64!(h[4], s[1]);
    trace_step!("mul‑raw‑prod", { t = ?t });
    t[2] = mul32x32_64!(h[0], r[2])
        + mul32x32_64!(h[1], r[1])
        + mul32x32_64!(h[2], r[0])
        + mul32x32_64!(h[3], s[3])
        + mul32x32_64!(h[4], s[2]);
    trace_step!("mul‑raw‑prod", { t = ?t });
    t[3] = mul32x32_64!(h[0], r[3])
        + mul32x32_64!(h[1], r[2])
        + mul32x32_64!(h[2], r[1])
        + mul32x32_64!(h[3], r[0])
        + mul32x32_64!(h[4], s[3]);
    trace_step!("mul‑raw‑prod", { t = ?t });
    t[4] = mul32x32_64!(h[0], r[4])
        + mul32x32_64!(h[1], r[3])
        + mul32x32_64!(h[2], r[2])
        + mul32x32_64!(h[3], r[1])
        + mul32x32_64!(h[4], r[0]);
    trace_step!("mul‑raw‑prod", { t = ?t });

    tracing::debug!(?t, "multiply_and_reduce: raw 64‑bit products");

    // propagate carries & fold back into h  (Donna reference flow)
    h[0] = (t[0] as u32) & 0x3ffffff;
    trace_step!("acc‑update",  { h = ?*h });
    let mut c = t[0] >> 26;
    t[1] += c;
    trace_step!("mul‑raw‑prod", { t = ?t });
    h[1] = (t[1] as u32) & 0x3ffffff;
    trace_step!("acc‑update",  { h = ?*h });
    c = t[1] >> 26;
    t[2] += c;
    trace_step!("mul‑raw‑prod", { t = ?t });
    h[2] = (t[2] as u32) & 0x3ffffff;
    trace_step!("acc‑update",  { h = ?*h });
    c = t[2] >> 26;
    t[3] += c;
    trace_step!("mul‑raw‑prod", { t = ?t });
    h[3] = (t[3] as u32) & 0x3ffffff;
    trace_step!("acc‑update",  { h = ?*h });
    c = t[3] >> 26;
    t[4] += c;
    trace_step!("mul‑raw‑prod", { t = ?t });
    h[4] = (t[4] as u32) & 0x3ffffff;
    trace_step!("acc‑update",  { h = ?*h });
    c = t[4] >> 26;
    h[0] = h[0].wrapping_add((c as u32) * 5);
    trace_step!("acc‑update",  { h = ?*h });

    // **Do *not* mask h[1] here** — the original algorithm leaves h₁
    // potentially above 2²⁶ so that the next round can fold carries
    // naturally.  Masking early was the root cause of the RFC 7539
    // vector mismatch.
    if h[0] >= (1 << 26) {
        let carry_out = h[0] >> 26;
        h[0] &= 0x3ffffff;
        trace_step!("acc‑update",  { h = ?*h });
        h[1] = h[1].wrapping_add(carry_out);
        trace_step!("acc‑update",  { h = ?*h });
    }

    tracing::debug!(h_out = ?*h, "multiply_and_reduce: finished");
}


/// In constant time replace *h* with *g* **iff** `select_mask == 0xFFFF_FFFF`.
///
/// `select_mask` is typically derived from the sign‑bit of the last limb
/// produced by `compute_g_plus5_minus_p`.
#[inline(always)]
pub fn ct_select_limbs(h: &mut LimbArr5, g: &LimbArr5, select_mask: u32) {
    let nmask = !select_mask;
    h[0] = (h[0] & nmask) | (g[0] & select_mask);
    h[1] = (h[1] & nmask) | (g[1] & select_mask);
    h[2] = (h[2] & nmask) | (g[2] & select_mask);
    h[3] = (h[3] & nmask) | (g[3] & select_mask);
    h[4] = (h[4] & nmask) | (g[4] & select_mask);
}


/// Expand the 32‑byte *r*‖*s* key into *r₀…r₄* plus the
/// “ℓ = r · 5” helpers *s₁…s₄* (Donna naming).  
/// Returns `(r, s)` where  
///  * `r` – five 26‑bit limbs  
///  * `s` – `r[1..=4] * 5`
#[inline(always)]
pub fn expand_key(key: &[u8; POLY1305_KEYLEN]) -> (LimbArr5, LimbArr4) {
    tracing::trace!(key = ?key, "expand_key: start");

    // raw little‑endian words
    let mut t0 = read_le32(&key[0..]);
    let mut t1 = read_le32(&key[4..]);
    let mut t2 = read_le32(&key[8..]);
    let mut t3 = read_le32(&key[12..]);
    tracing::debug!(t0, t1, t2, t3, "expand_key: raw 32‑bit words");

    // clamp into five 26‑bit limbs
    let mut r = [0u32; 5];
    r[0] = t0 & 0x3ffffff;
    t0 = (t0 >> 26) | (t1 << 6);
    r[1] = t0 & 0x3ffff03;
    t1 = (t1 >> 20) | (t2 << 12);
    r[2] = t1 & 0x3ffc0ff;
    t2 = (t2 >> 14) | (t3 << 18);
    r[3] = t2 & 0x3f03fff;
    t3 >>= 8;
    r[4] = t3 & 0x00fffff;
    tracing::debug!(?r, "expand_key: clamped r‑limbs");

    // multipliers r[i] * 5
    let s = [r[1] * 5, r[2] * 5, r[3] * 5, r[4] * 5];
    tracing::debug!(?s, "expand_key: s‑multipliers (r[i] * 5)");

    (r, s)
}


/// -----------------------------------------------------------------------------
/// [poly1305] public API (refactored driver)
///
/// Compute a Poly1305 one‑time authenticator.
/// * `out` – where the 16‑byte tag is written  
/// * `msg` – message to authenticate  
/// * `key` – 32‑byte (r ‖ s) key; **must be unique per‑message**
/// The implementation is a direct, constant‑time translation of the original
/// Bitcoin Core `poly1305-donna-unrolled.c` routine, with carries, clamps and
/// reductions preserved exactly.  Logging is limited to high‑level progress so
/// that the function remains branch‑predictable.
pub fn poly1305_auth(out: &mut [u8; POLY1305_TAGLEN], msg: &[u8], key: &[u8; POLY1305_KEYLEN]) {
    use core::convert::TryInto;

    tracing::info!(msg_len = msg.len(), "poly1305_auth: start");

    let (r, s) = expand_key(key);
    let mut h: LimbArr5 = [0; 5];

    // full 16‑byte blocks
    let mut m = msg;
    let mut blk_idx = 0usize;
    while m.len() >= 16 {
        let block: &[u8; 16] = m[..16].try_into().unwrap();
        tracing::trace!(blk_idx, block = ?*block, "poly1305_auth: process full block");
        accumulate_block(&mut h, block, true);
        multiply_and_reduce(&mut h, &r, &s);
        blk_idx += 1;
        m = &m[16..];
    }

    // tail (≤ 15 B)
    if !m.is_empty() {
        let mut tail = [0u8; 16];
        tail[..m.len()].copy_from_slice(m);
        tail[m.len()] = 1;
        tracing::trace!(tail_len = m.len(), tail = ?tail, "poly1305_auth: process tail");
        accumulate_block(&mut h, &tail, false);
        multiply_and_reduce(&mut h, &r, &s);
    }

    final_carry_and_sub_p(&mut h);
    tracing::debug!(h_final = ?h, "poly1305_auth: after final carry reduction");

    *out = add_pad_serialize(h, &key);
    tracing::info!(tag = ?out, "poly1305_auth: finished");
}


/// Compute  
///    * `g        = h + 5`  
///    * `g_minus_p = g − p`   (with _p = 2¹³⁰ − 5_)  
/// in radix‑2²⁶ and return the borrow flag from the subtraction.
///
/// * `borrow == 0` → `g ≥ p`  (no wrap)  
/// * `borrow == 1` → `g <  p`  (wrap happened)
#[inline(always)]
pub(crate) fn compute_g_plus5_minus_p(h: &[u32; 5]) -> ([u32; 5], [u32; 5], u32) {
    //--------------------------------------------------------------------
    // 0.  Trace input
    //--------------------------------------------------------------------
    tracing::trace!(?h, "compute_g_plus5_minus_p: start");

    //--------------------------------------------------------------------
    // 1.  g = h + 5      (radix‑26 addition with carry chain)
    //--------------------------------------------------------------------
    let mut g = *h;
    let mut carry: u64 = 5;

    for (idx, limb) in g.iter_mut().enumerate() {
        carry  += *limb as u64;
        *limb   = (carry & 0x3ffffff) as u32;
        carry >>= 26;
        trace_step!("add+carry", { idx, limb_val = *limb, carry_after = carry });
    }

    // A carry can be at most 1 here.  2¹³⁰ ≡ 5 (mod p) ⇒ fold it back.
    if carry != 0 {
        trace_step!("fold_2^130", { carry });
        g[0] = g[0].wrapping_add(5);

        // Propagate at most one extra carry further (never spills past h₂).
        let mut c = g[0] >> 26;
        g[0] &= 0x3ffffff;
        let mut idx = 1;
        while c != 0 && idx < 5 {
            let tmp  = g[idx] as u64 + c as u64;
            g[idx]   = (tmp & 0x3ffffff) as u32;
            c        = (tmp >> 26) as u32;
            trace_step!("fold_prop", { idx, limb_val = g[idx], c_after = c });
            idx += 1;
        }
    }

    tracing::debug!(?g, "compute_g_plus5_minus_p: after h+5");

    //--------------------------------------------------------------------
    // 2.  g_minus_p = g − p        (radix‑26 subtraction with borrows)
    //--------------------------------------------------------------------
    let mut g_minus_p = [0u32; 5];
    let mut borrow: i64 = 0;

    for i in 0..5 {
        let diff        = g[i] as i64 - P_RADIX26[i] as i64 - borrow;
        g_minus_p[i]    = (diff & 0x3ffffff) as u32;
        borrow          = (diff >> 63) & 1;          // 1 if diff < 0
        trace_step!("sub_step", { i, diff, borrow, limb_out = g_minus_p[i] });
    }

    tracing::debug!(
        ?g_minus_p,
        borrow,
        "compute_g_plus5_minus_p: finished"
    );

    (g, g_minus_p, borrow as u32)
}



