We have a rust crate named 'bitcoinleveldb-filter' 0.1.19. 

Here is the interface:
pub trait FilterPolicy {}


pub trait CreateFilter {
    fn create_filter(
        &self,
        keys:  *const Slice,
        n:     i32,
        dst:   &mut Vec<u8>
    );
}


pub trait KeyMayMatch {
    fn key_may_match(&self, key: &Slice, filter: &Slice) -> bool;
}

/// A `FilterBlockBuilder` constructs all the filters for a table.
/// We remove `#[derive(Debug, Builder)]` to avoid compile errors.
#[derive(Getters, Setters)]
pub struct FilterBlockBuilder  {
    policy:         Box<dyn FilterPolicy>,
    keys:           Vec<u8>,
    start:          Vec<usize>,
    result:         Vec<u8>,
    tmp_keys:       Vec<Slice>,
    filter_offsets: Vec<u32>,
}


/// A `FilterBlockReader` parses the filter block stored in `contents` and
/// answers queries via `key_may_match`.
#[derive(Getters, Setters)]
pub struct FilterBlockReader  {
    policy:  Box<dyn FilterPolicy>,
    data:    Arc<[u8]>,
    offset:  usize,
    num:     usize,
    base_lg: usize,
    valid:   bool,
}

/// Appends a 32-bit little-endian integer to `dst`.
pub fn put_fixed32(dst: &mut Vec<u8>, value: u32) {
    trace!("put_fixed32 -> value={}", value);
    let bytes = value.to_le_bytes();
    dst.extend_from_slice(&bytes);
}


/// Reads a 32-bit little-endian integer from the first 4 bytes of `src`.
/// If `src` has fewer than 4 bytes, behavior is undefined (the caller ensures correctness).
pub fn decode_fixed32(src: &[u8]) -> u32 {
    let arr: [u8; 4] = src[0..4].try_into().unwrap();
    u32::from_le_bytes(arr)
}


/// Provide a function to create a Bloom filter policy with `bits_per_key`,
/// but the user’s code has just a stub. We replicate that:
pub fn new_bloom_filter_policy(_bits_per_key_: i32) -> Box<dyn FilterPolicy> {
    unimplemented!("new_bloom_filter_policy is not yet implemented");
}

impl FilterBlockBuilder {

    pub fn new(policy: Box<dyn FilterPolicy>) -> Self {
        info!("FilterBlockBuilder::new invoked");
        Self {
            policy,
            keys: Vec::new(),
            start: Vec::new(),
            result: Vec::new(),
            tmp_keys: Vec::new(),
            filter_offsets: Vec::new(),
        }
    }

    /// Start a filter region for the block at `block_offset`.
    pub fn start_block(&mut self, block_offset: u64) {
        trace!("start_block at offset={}", block_offset);
        let filter_index = (block_offset / FILTER_BASE as u64) as usize;
        assert!(
            filter_index >= self.filter_offsets.len(),
            "Block offset must not go backwards"
        );
        // Generate empty filters for any blocks we skipped
        while filter_index > self.filter_offsets.len() {
            self.generate_filter();
        }
    }

    /// Add a key to the current filter batch
    pub fn add_key(&mut self, key_: &Slice) {
        trace!("add_key with length={}", *key_.size());
        self.start.push(self.keys.len());
        let key_data = unsafe {
            // Must cast to *const u8, length = *key_.size()
            std::slice::from_raw_parts(*key_.data() as *const u8, *key_.size())
        };
        self.keys.extend_from_slice(key_data);
    }

    /// Finish building all filters. Returns a `Slice` over our result buffer.
    pub fn finish(&mut self) -> Slice {
        trace!("FilterBlockBuilder::finish invoked");
        // If there are pending keys, flush them
        if !self.start.is_empty() {
            self.generate_filter();
        }
        // Append array of per-filter offsets
        let array_offset = self.result.len();
        for &off in &self.filter_offsets {
            put_fixed32(&mut self.result, off);
        }
        // Append the start-of-offset-array, then the base_lg
        put_fixed32(&mut self.result, array_offset as u32);
        self.result.push(FILTER_BASE_LG as u8);

        info!("Finished building filter block, size={}", self.result.len());
        Slice::from_ptr_len(self.result.as_ptr(), self.result.len())
    }
}

impl FilterBlockReader {

    pub fn new(policy: Box<dyn FilterPolicy>, contents: &Slice) -> Self {
        info!("FilterBlockReader::new with size={}", *contents.size());

        let src = unsafe {
            // Must cast to *const u8, length = *contents.size()
            std::slice::from_raw_parts(*contents.data() as *const u8, *contents.size())
        };

        let data: Arc<[u8]> = Arc::from(src);

        let n = data.len();

        if n < 5 {
            warn!("Data too short (<5 bytes). Mark invalid");
            return Self {
                policy,
                data,
                offset: 0,
                num: 0,
                base_lg: 0,
                valid: false,
            };
        }

        let base_lg = data[n - 1] as usize;

        let last_word = decode_fixed32(&data[n - 5..n - 1]) as usize;

        if last_word > n - 5 {
            warn!("Offset array start out of range. Mark invalid");
            return Self {
                policy,
                data,
                offset: 0,
                num: 0,
                base_lg,
                valid: false,
            };
        }

        let offset = last_word;
        let num = (n - 5 - last_word) / 4;

        info!("Parsed FilterBlockReader: offset={}, num={}, base_lg={}", offset, num, base_lg);
        Self {
            policy,
            data,
            offset,
            num,
            base_lg,
            valid: true,
        }
    }

    /// Return true if `key` may be present in the filter for block at `block_offset`.
    pub fn key_may_match(&self, block_offset: u64, key: &Slice) -> bool {
        trace!("key_may_match offset={}, key_len={}", block_offset, *key.size());
        if !self.valid {
            trace!("Invalid filter => treat as possible match");
            return true;
        }

        let index = (block_offset >> self.base_lg) as usize;
        if index < self.num {
            // read offset array to find filter region
            let idx1 = self.offset + index * 4;
            let idx2 = idx1 + 4;
            let start = decode_fixed32(&self.data[idx1..idx2]) as usize;

            let idx3 = idx2;
            let idx4 = idx3 + 4;
            let limit = decode_fixed32(&self.data[idx3..idx4]) as usize;

            // The filter data is [start..limit]
            if start <= limit && limit <= self.offset {
                let filter_data = &self.data[start..limit];
                let filter_slice = Slice::from_ptr_len(
                    filter_data.as_ptr(),
                    filter_data.len(),
                );
                return self.policy.key_may_match(key, &filter_slice);
            } else if start == limit {
                // Empty filter
                return false;
            }
        }
        // If out-of-range, treat as potential match
        true
    }
}


