Here is the interface:
/**
| Abstract handle to particular state of a DB.
|
| A Snapshot is an immutable object and can
| therefore be safely accessed from multiple
| threads without any external synchronization.
*/
pub trait Snapshot {}

/**
| Snapshots are kept in a doubly-linked list in
| the DB.
|
| Each SnapshotImpl corresponds to a particular
| sequence number.
*/
#[derive(Getters,Setters)]
#[getset(get="pub",set="pub")]
pub struct SnapshotImpl  {
    prev:            *mut SnapshotImpl,
    next:            *mut SnapshotImpl,
    sequence_number: SequenceNumber,
    list:            *mut SnapshotList,
}


#[derive(Getters,Setters)]
#[getset(get="pub",set="pub")]
pub struct SnapshotList  {
    head: Box<SnapshotImpl>,
}

impl SnapshotList {

    pub fn oldest(&self) -> *mut SnapshotImpl {
        debug!("requesting oldest SnapshotImpl from SnapshotList");

        debug_assert!(
            !self.empty(),
            "SnapshotList::oldest called on an empty list"
        );

        let head_ptr: *const SnapshotImpl = self.head_const_ptr();

        let oldest_ptr: *mut SnapshotImpl = unsafe {
            let head_ref: &SnapshotImpl = &*head_ptr;
            *head_ref.next()
        };

        trace!(
            oldest_ptr = ?oldest_ptr,
            "returning oldest SnapshotImpl pointer"
        );

        oldest_ptr
    }

    pub fn newest(&self) -> *mut SnapshotImpl {
        debug!("requesting newest SnapshotImpl from SnapshotList");

        debug_assert!(
            !self.empty(),
            "SnapshotList::newest called on an empty list"
        );

        let head_ptr: *const SnapshotImpl = self.head_const_ptr();

        let newest_ptr: *mut SnapshotImpl = unsafe {
            let head_ref: &SnapshotImpl = &*head_ptr;
            *head_ref.prev()
        };

        trace!(
            newest_ptr = ?newest_ptr,
            "returning newest SnapshotImpl pointer"
        );

        newest_ptr
    }
}

impl Snapshot for SnapshotImpl {}

impl SnapshotImpl {

    pub fn new(sequence_number: SequenceNumber) -> Self {
        debug!(
            "creating SnapshotImpl with sequence_number={}",
            sequence_number
        );

        SnapshotImpl {
            prev: core::ptr::null_mut(),
            next: core::ptr::null_mut(),
            sequence_number,
            #[cfg(not(NDEBUG))]
            list: core::ptr::null_mut(),
        }
    }
}

impl Default for SnapshotList {

    fn default() -> Self {
        debug!("initializing empty SnapshotList (heap-pinned sentinel)");

        // Allocate the sentinel on the heap so its address will not change if `SnapshotList` moves.
        let mut list = SnapshotList {
            head: Box::new(SnapshotImpl::new(0 as SequenceNumber)),
        };

        // SAFELY obtain a mutable raw pointer to the heap-allocated head
        let head_ptr: *mut SnapshotImpl = &mut *list.head;

        unsafe {
            // Self-link the sentinel
            (*head_ptr).set_prev(head_ptr);
            (*head_ptr).set_next(head_ptr);
        }

        trace!(
            head_ptr = ?head_ptr,
            "initialized SnapshotList head pointers (stable heap address)"
        );

        list
    }
}

impl SnapshotList {

    /// Returns a mutable raw pointer to the dummy head node of this list.
    ///
    /// The head node is heap-allocated to ensure a **stable address**
    /// even if `SnapshotList` is moved. This avoids self-reference invalidation.
    pub fn head_mut_ptr(&mut self) -> *mut SnapshotImpl {
        let ptr: *mut SnapshotImpl = &mut *self.head;
        trace!(
            head_ptr = ?ptr,
            "SnapshotList::head_mut_ptr returning mutable raw pointer to list head"
        );
        ptr
    }

    /// Returns a const raw pointer to the dummy head node of this list.
    pub fn head_const_ptr(&self) -> *const SnapshotImpl {
        let ptr: *const SnapshotImpl = &*self.head;
        trace!(
            head_ptr = ?ptr,
            "SnapshotList::head_const_ptr returning const raw pointer to list head"
        );
        ptr
    }
}

impl SnapshotList {

    /// Removes a SnapshotImpl from this list.
    ///
    /// The snapshot must have been created by calling New() on this list.
    /// The snapshot pointer should not be const, because its memory is
    /// deallocated. However, that would force us to change
    /// DB::ReleaseSnapshot(), which is in the API, and currently takes a const
    /// Snapshot.
    pub fn delete(&mut self, snapshot: *const SnapshotImpl) {
        debug!(snapshot_ptr = ?snapshot, "deleting SnapshotImpl from SnapshotList");

        unsafe {
            if snapshot.is_null() {
                warn!("SnapshotList::delete called with null snapshot pointer");
                return;
            }

            let head_addr: *const SnapshotImpl = self.head_const_ptr();

            debug_assert!(
                snapshot != head_addr,
                "SnapshotList::delete must not be called on the list head"
            );

            let snapshot_mut: *mut SnapshotImpl = snapshot as *mut SnapshotImpl;

            #[cfg(not(NDEBUG))]
            {
                let expected_list: *mut SnapshotList = self as *mut SnapshotList;
                let actual_list: *mut SnapshotList = *(*snapshot_mut).list();
                debug_assert_eq!(
                    actual_list,
                    expected_list,
                    "SnapshotList::delete: snapshot list pointer does not match"
                );
            }

            let prev_ptr: *mut SnapshotImpl = *(*snapshot_mut).prev();
            let next_ptr: *mut SnapshotImpl = *(*snapshot_mut).next();

            debug_assert!(
                !prev_ptr.is_null(),
                "SnapshotList::delete: prev_ptr is null"
            );
            debug_assert!(
                !next_ptr.is_null(),
                "SnapshotList::delete: next_ptr is null"
            );

            {
                let prev_ref: &mut SnapshotImpl = &mut *prev_ptr;
                prev_ref.set_next(next_ptr);
            }

            {
                let next_ref: &mut SnapshotImpl = &mut *next_ptr;
                next_ref.set_prev(prev_ptr);
            }

            {
                let snapshot_ref: &mut SnapshotImpl = &mut *snapshot_mut;

                #[cfg(not(NDEBUG))]
                {
                    snapshot_ref.set_list(core::ptr::null_mut());
                }

                snapshot_ref.set_prev(core::ptr::null_mut());
                snapshot_ref.set_next(core::ptr::null_mut());
            }

            trace!(
                snapshot_mut_ptr = ?snapshot_mut,
                prev_ptr = ?prev_ptr,
                next_ptr = ?next_ptr,
                "unlinked SnapshotImpl from SnapshotList, deallocating"
            );

            drop(Box::from_raw(snapshot_mut));
        }
    }
}

impl SnapshotList {

    pub fn empty(&self) -> bool {
        let head_ptr: *const SnapshotImpl = self.head_const_ptr();

        let next_ptr: *mut SnapshotImpl = unsafe {
            let head_ref: &SnapshotImpl = &*head_ptr;
            *head_ref.next()
        };

        let is_empty: bool = core::ptr::eq(next_ptr as *const SnapshotImpl, head_ptr);

        trace!(
            head_ptr = ?head_ptr,
            next_ptr = ?next_ptr,
            is_empty,
            "evaluated SnapshotList::empty"
        );

        is_empty
    }
}

impl SnapshotList {

    /// Creates a SnapshotImpl and appends it to the end of the list.
    pub fn new(&mut self, sequence_number: SequenceNumber) -> *mut SnapshotImpl {
        debug!(
            sequence_number = sequence_number,
            "creating new SnapshotImpl in SnapshotList"
        );

        unsafe {
            if !self.empty() {
                let newest_ptr: *mut SnapshotImpl = self.newest();
                let newest_seq_ref: &SequenceNumber = (*newest_ptr).sequence_number();
                debug_assert!(
                    *newest_seq_ref <= sequence_number,
                    "SnapshotList::new must be called with monotonically increasing sequence numbers"
                );
            }

            let snapshot = Box::new(SnapshotImpl::new(sequence_number));

            #[cfg(not(NDEBUG))]
            let mut snapshot = {
                let mut s = snapshot;
                s.set_list(self as *mut SnapshotList);
                s
            };

            #[cfg(NDEBUG)]
            let snapshot = snapshot;

            let snapshot_ptr: *mut SnapshotImpl = Box::into_raw(snapshot);

            // Pointer to the dummy head node (sentinel)
            let head_ptr: *mut SnapshotImpl = self.head_mut_ptr();

            // Previous tail before inserting the new snapshot
            let prev_ptr: *mut SnapshotImpl = {
                let head_ref: &SnapshotImpl = &*head_ptr;
                *head_ref.prev()
            };

            debug_assert!(
                !prev_ptr.is_null(),
                "SnapshotList::new: prev_ptr is null"
            );

            {
                let snapshot_ref: &mut SnapshotImpl = &mut *snapshot_ptr;
                snapshot_ref.set_next(head_ptr);
                snapshot_ref.set_prev(prev_ptr);
            }

            {
                let prev_ref: &mut SnapshotImpl = &mut *prev_ptr;
                prev_ref.set_next(snapshot_ptr);
            }

            {
                let head_ref_mut: &mut SnapshotImpl = &mut *head_ptr;
                head_ref_mut.set_prev(snapshot_ptr);
            }

            trace!(
                snapshot_ptr = ?snapshot_ptr,
                head_ptr = ?head_ptr,
                prev_ptr = ?prev_ptr,
                "linked new SnapshotImpl into SnapshotList"
            );

            snapshot_ptr
        }
    }
}


