/// Decode an ASN starting at `*pos` inside `asmap`.
pub fn decodeasn(asmap: &[bool], pos: &mut usize) -> u32 {
    trace!(pos = *pos, "decodeasn");
    decode_bits(asmap, pos, 1, &ASN_BIT_SIZES)
}


/// Helper that counts the number of set bits in a `u32`.
#[inline]
pub fn count_bits(x: u32) -> u32 {
    x.count_ones()
}


/// Read an ASMAP from `path`, return its bits or an empty vec on failure.
///
/// * The binary on disk is interpreted little‑endian, LSB‑first, exactly like
///   the original C++ implementation.
/// * A full sanity check is executed before the vector is returned.
/// * Robust tracing is provided at every early‑return branch.
pub fn decode_asmap<P: AsRef<Path>>(path: P) -> Vec<bool> {
    let mut bits: Vec<bool> = Vec::new();

    let data = match fs::read(&path) {
        Ok(d) => d,
        Err(e) => {
            error!(%e, path = ?path.as_ref(), "Failed to read ASMAP file from disk");
            return bits;
        }
    };

    info!(
        bytes   = data.len(),
        path    = ?path.as_ref(),
        "Opened ASMAP file from disk"
    );

    for byte in data {
        for bit in 0..8 {
            bits.push(((byte >> bit) & 1) != 0);
        }
    }

    if !sanity_check_as_map(&bits, 128) {
        error!(path = ?path.as_ref(), "Sanity check of ASMAP file failed");
        return Vec::new();
    }

    bits
}


/// Decode a jump offset from `asmap`.
pub fn decode_jump(asmap: &[bool], pos: &mut usize) -> u32 {
    trace!(pos = *pos, "decode_jump");
    decode_bits(asmap, pos, 17, &JUMP_BIT_SIZES)
}


/// Decode a match value (pattern + sentinel bit) from `asmap`.
pub fn decode_match(asmap: &[bool], pos: &mut usize) -> u32 {
    trace!(pos = *pos, "decode_match");
    decode_bits(asmap, pos, 2, &MATCH_BIT_SIZES)
}


/// Interpret `asmap` for the given `ip`.  
/// Returns the mapped ASN or 0 on structural failure (which should be ruled
/// out in production by `sanity_check_as_map`).
pub fn interpret(asmap: &[bool], ip: &[bool]) -> u32 {
    let mut pos: usize = 0;
    let mut bits_left: i32 = ip.len() as i32;
    let mut default_asn: u32 = 0;

    while pos < asmap.len() {
        let opcode = decode_type(asmap, &mut pos);
        debug!(?opcode, pos, bits_left, "interpret: opcode");

        match opcode {
            Instruction::RETURN => {
                let asn = decodeasn(asmap, &mut pos);
                if asn == INVALID {
                    error!("interpret: ASN straddles EOF");
                    break;
                }
                return asn;
            }
            Instruction::JUMP => {
                let jump = decode_jump(asmap, &mut pos);
                if jump == INVALID || bits_left == 0 {
                    error!("interpret: invalid jump or no bits left");
                    break;
                }
                if (jump as usize) >= asmap.len() - pos {
                    error!("interpret: jump past EOF");
                    break;
                }
                let ip_bit = ip[ip.len() - bits_left as usize];
                if ip_bit {
                    pos += jump as usize;
                }
                bits_left -= 1;
            }
            Instruction::MATCH => {
                let m = decode_match(asmap, &mut pos);
                if m == INVALID {
                    error!("interpret: match straddles EOF");
                    break;
                }
                let matchlen = count_bits(m) - 1;
                if bits_left < matchlen as i32 {
                    error!("interpret: not enough input bits for match");
                    break;
                }
                for bit in 0..matchlen {
                    let ip_bit = ip[ip.len() - bits_left as usize];
                    let pattern_bit = ((m >> (matchlen - 1 - bit)) & 1) != 0;
                    if ip_bit != pattern_bit {
                        trace!("interpret: match failed, returning default_asn");
                        return default_asn;
                    }
                    bits_left -= 1;
                }
            }
            Instruction::DEFAULT => {
                default_asn = decodeasn(asmap, &mut pos);
                if default_asn == INVALID {
                    error!("interpret: DEFAULT ASN straddles EOF");
                    break;
                }
            }
        }
    }

    error!("interpret: terminated without RETURN – invalid ASMAP");
    0 // 0 is not a valid ASN
}


/// Exhaustive structural validation, exactly mirroring the original C++ logic.
pub fn sanity_check_as_map(asmap: &[bool], mut bits: i32) -> bool {
    let mut pos: usize = 0;
    let mut jumps: Vec<(u32, i32)> = Vec::with_capacity(bits as usize); // (offset, bits_left)
    let mut prev_opcode = Instruction::JUMP;
    let mut had_incomplete_match = false;

    while pos < asmap.len() {
        let offset = pos as u32;
        if jumps.last().map_or(false, |jump| offset >= jump.0) {
            error!("sanity: jump into middle of previous instruction");
            return false;
        }

        let opcode = decode_type(asmap, &mut pos);
        trace!(?opcode, pos, bits, "sanity: opcode");

        match opcode {
            Instruction::RETURN => {
                if prev_opcode == Instruction::DEFAULT {
                    error!("sanity: RETURN immediately after DEFAULT");
                    return false;
                }
                let asn = decodeasn(asmap, &mut pos);
                if asn == INVALID {
                    error!("sanity: ASN straddles EOF");
                    return false;
                }
                if jumps.is_empty() {
                    // verify padding
                    if asmap.len() - pos > 7 {
                        error!("sanity: excessive padding");
                        return false;
                    }
                    if asmap[pos..].iter().any(|&b| b) {
                        error!("sanity: non‑zero padding bits");
                        return false;
                    }
                    return true;
                } else {
                    let (jump_offset, restored_bits) = jumps.pop().unwrap();
                    if offset != jump_offset {
                        error!("sanity: unreachable code after RETURN");
                        return false;
                    }
                    bits = restored_bits;
                    prev_opcode = Instruction::JUMP;
                }
            }
            Instruction::JUMP => {
                let jump = decode_jump(asmap, &mut pos);
                if jump == INVALID || (jump as usize) > asmap.len() - pos {
                    error!("sanity: invalid jump");
                    return false;
                }
                if bits == 0 {
                    error!("sanity: consuming bits past end of input");
                    return false;
                }
                bits -= 1;
                let jump_offset = pos as u32 + jump;
                if jumps.last().map_or(false, |j| jump_offset >= j.0) {
                    error!("sanity: intersecting jumps");
                    return false;
                }
                jumps.push((jump_offset, bits));
                prev_opcode = Instruction::JUMP;
            }
            Instruction::MATCH => {
                let m = decode_match(asmap, &mut pos);
                if m == INVALID {
                    error!("sanity: MATCH straddles EOF");
                    return false;
                }
                let matchlen = count_bits(m) as i32 - 1;
                if prev_opcode != Instruction::MATCH {
                    had_incomplete_match = false;
                }
                if matchlen < 8 && had_incomplete_match {
                    error!("sanity: multiple short MATCHes in a row");
                    return false;
                }
                had_incomplete_match = matchlen < 8;
                if bits < matchlen {
                    error!("sanity: MATCH consumes past end of input");
                    return false;
                }
                bits -= matchlen;
                prev_opcode = Instruction::MATCH;
            }
            Instruction::DEFAULT => {
                if prev_opcode == Instruction::DEFAULT {
                    error!("sanity: consecutive DEFAULTs");
                    return false;
                }
                let asn = decodeasn(asmap, &mut pos);
                if asn == INVALID {
                    error!("sanity: DEFAULT ASN straddles EOF");
                    return false;
                }
                prev_opcode = Instruction::DEFAULT;
            }
        }
    }

    error!("sanity: reached EOF without RETURN");
    false
}


/// Core bit‑stream decoder, mirroring the C++ implementation in behaviour.
pub fn decode_bits(
    asmap:     &[bool],
    pos:       &mut usize,
    minval:    u8,
    bit_sizes: &[u8]
) -> u32
{
    trace!(start_pos = *pos, "decode_bits: enter");

    let mut val: u32 = minval as u32;

    for (i, bitsize) in bit_sizes.iter().enumerate() {
        let last_size = i + 1 == bit_sizes.len();

        // Read exponent bit unless this is the sentinel (last) size.
        let bit = if !last_size {
            if *pos >= asmap.len() {
                trace!("decode_bits: hit EOF in exponent");
                return INVALID;
            }
            let b = asmap[*pos];
            *pos += 1;
            b
        } else {
            false
        };

        if bit {
            // We set the exponent and continue parsing larger bit‑sizes.
            val += 1u32 << *bitsize;
        } else {
            // Mantissa path: consume `bitsize` bits and finish.
            for b in 0..*bitsize {
                if *pos >= asmap.len() {
                    trace!("decode_bits: hit EOF in mantissa");
                    return INVALID;
                }
                let bit_val = asmap[*pos] as u32;
                *pos += 1;
                val += bit_val << (*bitsize - 1 - b);
            }
            trace!(end_pos = *pos, result = val, "decode_bits: ok");
            return val;
        }
    }

    trace!("decode_bits: reached EOF in exponent without terminator");
    INVALID // Reached EOF in exponent
}


/// Decode an `Instruction` opcode located at `*pos`.
pub fn decode_type(asmap: &[bool], pos: &mut usize) -> Instruction {
    trace!(pos = *pos, "decode_type");
    Instruction::new(decode_bits(asmap, pos, 0, &TYPE_BIT_SIZES))
}

impl Instruction {

    pub fn new(x: u32) -> Self {
        match x {
            0 => Instruction::RETURN,
            1 => Instruction::JUMP,
            2 => Instruction::MATCH,
            3 => Instruction::DEFAULT,
            _ => unreachable!(),
        }
    }
}


