/// Return the length of the C‑string starting at `start`
/// but never examine more than `max_len` bytes.
///
/// When no `NUL` byte is found within `max_len`, the
/// function returns `max_len`.
#[inline]
pub fn strnlen(start: *const u8, max_len: usize) -> usize {
    // Null pointer → programmer error.  Log loudly and
    // return zero to avoid walking random memory.
    if start.is_null() {
        error!(
            target: "compat::strnlen",
            "called with null pointer – returning 0"
        );
        return 0;
    }

    trace!(
        target: "compat::strnlen",
        ptr = ?start,
        max_len,
        "enter strnlen"
    );

    // SAFETY: caller promises the region is readable
    // for `max_len` bytes.
    let bytes: &[u8] = std::slice::from_raw_parts(start, max_len);

    let len = bytes
        .iter()
        .position(|&c| c == 0)
        .unwrap_or(max_len);

    trace!(
        target: "compat::strnlen",
        result = len,
        "exit strnlen"
    );

    len
}


/// Trigger the widening → narrowing conversion path
/// (`char`⇆`wchar_t` in C++).  
/// In Rust we round‑trip via `char` and ensure the
/// original byte is recovered.
#[inline]
pub fn sanity_test_widen(testchar: u8) -> bool {
    trace!(
        target: "compat::glibcxx_sanity",
        func = "sanity_test_widen",
        testchar = testchar
    );

    let wide = char::from(testchar); // widen
    let narrow = wide as u32 as u8;  // narrow

    let ok = narrow == testchar;
    debug!(
        target: "compat::glibcxx_sanity",
        func = "sanity_test_widen",
        result = ok
    );
    ok
}


/// Exercise the container splice/hook machinery
/// by pushing then popping a sequence in a linked
/// list while verifying FIFO/LIFO invariants.
#[inline]
pub fn sanity_test_list(size: u32) -> bool {
    trace!(
        target: "compat::glibcxx_sanity",
        func = "sanity_test_list",
        size = size
    );

    let mut lst: LinkedList<u32> = LinkedList::new();
    for i in 0..size {
        lst.push_back(i + 1);
    }

    if lst.len() as u32 != size {
        return false;
    }

    while let Some(back) = lst.back().copied() {
        if back != lst.len() as u32 {
            return false;
        }
        lst.pop_back();
    }

    true
}


/// Force an out‑of‑bounds panic on an empty string
/// and verify it is correctly caught.
#[inline]
pub fn sanity_test_range_fmt() -> bool {
    trace!(
        target: "compat::glibcxx_sanity",
        func = "sanity_test_range_fmt"
    );

    let ok = std::panic::catch_unwind(std::panic::AssertUnwindSafe(|| {
        let s = String::new();
        // This will panic due to out‑of‑range access,
        // mirroring `std::string::at(1)` in C++.
        let _ = s.chars().nth(1).unwrap();
    }))
    .is_err();

    debug!(
        target: "compat::glibcxx_sanity",
        func = "sanity_test_range_fmt",
        result = ok
    );

    ok
}


/// Run all sanity checks and return **true** iff all
/// individual tests succeed.
#[inline]
pub fn glibcxx_sanity_test() -> bool {
    sanity_test_widen(b'a')
        && sanity_test_list(100)
        && sanity_test_range_fmt()
}


/// Execute `CPUID` with the given `leaf` & `subleaf`
/// and return the raw register values in `a`, `b`,
/// `c`, and `d`.
///
/// # Safety
/// *Exactly* the same contract as the original C++:
/// the function has no pre‑conditions beyond the
/// obvious validity of the output pointers.
/// The wrapper itself is **safe** to call – the
/// `unsafe` boundary is fully contained inside.
#[inline]
pub fn getcpuid(
    leaf:    u32,
    subleaf: u32,
    a:       &mut u32,
    b:       &mut u32,
    c:       &mut u32,
    d:       &mut u32
)
{
    trace!(
        target: "compat::cpuid",
        leaf = leaf,
        subleaf = subleaf,
        "enter getcpuid"
    );

    #[cfg(any(target_arch = "x86", target_arch = "x86_64"))]
    {
        // SAFETY: CPUID is guaranteed to be available
        // on all x86‑64 CPUs and all 32‑bit CPUs that
        // support SSE2 (Bitcoin's minimum baseline).
        unsafe {
            #[cfg(target_arch = "x86")]
            use core::arch::x86::__cpuid_count as __cpuid_count_inner;
            #[cfg(target_arch = "x86_64")]
            use core::arch::x86_64::__cpuid_count as __cpuid_count_inner;

            let r = __cpuid_count_inner(leaf, subleaf);
            *a = r.eax;
            *b = r.ebx;
            *c = r.ecx;
            *d = r.edx;
        }
    }

    #[cfg(not(any(target_arch = "x86", target_arch = "x86_64")))]
    {
        warn!(
            target: "compat::cpuid",
            "getcpuid invoked on unsupported architecture – zeroing outputs"
        );
        *a = 0;
        *b = 0;
        *c = 0;
        *d = 0;
    }

    trace!(
        target: "compat::cpuid",
        eax = *a,
        ebx = *b,
        ecx = *c,
        edx = *d,
        "exit getcpuid"
    );
}


