/// Return `true` if there is buffered data available
/// for immediate, non‑blocking read on `STDIN`.
///
/// On Windows the console subsystem does not expose
/// a polling API that is reliable across redirected
/// input channels, so the function always returns
/// `false` when compiled for that platform.
#[inline]
pub fn stdin_ready() -> bool {
    if !stdin_terminal() {
        debug!(target: "compat::stdin", "STDIN is not a TTY – assuming ready");
        return true;
    }

    #[cfg(target_os = "windows")]
    {
        false
    }

    #[cfg(unix)]
    {
        unix_impl::is_ready()
    }
}

impl NoechoInst {

    /// Disable echo on `STDIN` for the lifetime of
    /// the returned guard.
    #[inline]
    pub fn new() -> Self {
        trace!(target: "compat::stdin", "disabling echo");
        set_stdin_echo(false);
        NoechoInst
    }
}

impl Drop for NoechoInst {

    #[inline]
    fn drop(&mut self) {
        trace!(target: "compat::stdin", "re‑enabling echo");
        set_stdin_echo(true);
    }
}


#[cfg(unix)]
mod unix_impl {
    /// Toggle the `ECHO` flag in the current `termios`
    /// settings for `STDIN`.
    pub fn set_echo(enable: bool) {
        unsafe {
            let mut tty: termios = core::mem::zeroed();
            if tcgetattr(STDIN_FILENO, &mut tty as *mut _) != 0 {
                error!(target: "compat::stdin", "tcgetattr failed");
                return;
            }

            if enable {
                tty.c_lflag |= libc::ECHO;
            } else {
                tty.c_lflag &= !libc::ECHO;
            }

            if tcsetattr(STDIN_FILENO, TCSANOW, &tty as *const _) != 0 {
                error!(target: "compat::stdin", "tcsetattr failed");
            }
        }
    }

    /// Detect whether `STDIN` is attached to a TTY.
    #[inline]
    pub fn is_tty() -> bool {
        unsafe { isatty(STDIN_FILENO) == 1 }
    }

    /// Poll `STDIN` with a zero‑timeout for pending
    /// data.
    pub fn is_ready() -> bool {
        unsafe {
            let mut fds = libc::pollfd {
                fd: STDIN_FILENO,
                events: POLLIN,
                revents: 0,
            };
            poll(&mut fds as *mut _, 1, 0) == 1
        }
    }
}


#[cfg(target_os = "windows")]
mod windows_impl {
    /// Toggle the `ENABLE_ECHO_INPUT` console mode
    /// flag for the process’ `STDIN` handle.
    pub fn set_echo(enable: bool) {
        if let Some(handle) = stdin_handle() {
            unsafe {
                let mut mode: u32 = 0;
                if GetConsoleMode(handle, &mut mode) == 0 {
                    error!(target: "compat::stdin", "GetConsoleMode failed");
                    return;
                }

                if enable {
                    mode |= ENABLE_ECHO_INPUT;
                } else {
                    mode &= !ENABLE_ECHO_INPUT;
                }

                if SetConsoleMode(handle, mode) == 0 {
                    error!(target: "compat::stdin", "SetConsoleMode failed");
                }
            }
        }
    }

    /// Detect whether `STDIN` is attached to a TTY.
    #[inline]
    pub fn is_tty() -> bool {
        unsafe { _isatty(_fileno(STDIN_FILE)) == 1 }
    }
}


