pub type GcsFilterElement = Vec<u8>;


pub type GcsFilterElementSet = std::collections::HashSet<GcsFilterElement,ByteVectorHash>;

/// Filter parameter set (BIP‑158 §3.1).
#[derive(Debug, Clone, Getters, Builder)]
#[builder(setter(into), default)]
#[getset(get = "pub")]
pub struct GcsFilterParams  {
    siphash_k0: u64,
    siphash_k1: u64,
    p:          u8,
    m:          u32,
}


/// Compact probabilistic set (BIP‑158 Golomb‑coded filter).
///
/// This implements a Golomb-coded set as defined in BIP 158. It is a compact,
/// probabilistic data structure for testing set membership.
/// 
#[derive(Builder,Debug, Clone, Getters, Default)]
#[getset(get = "pub")]
#[builder(setter(into))]
pub struct GCSFilter  {
    params:  GcsFilterParams,
    n:       u32,
    f:       u64,
    encoded: Vec<u8>,
}

pub fn golomb_rice_encode<OStream>(bitwriter: &mut BitStreamWriter<OStream>, p: u8, x: u64) where
    OStream: Default + Write
{
    // Write quotient as unary-encoded: q 1's
    // followed by one 0.
    let mut q = x >> p;

    // unary‑encode the quotient: q × ‘1’, then a single ‘0’
    while q > 0 {
        let nbits = if q <= 64 { q as i32 } else { 64 };
        bitwriter.write(!0, nbits);
        q -= nbits as u64;
    }
    bitwriter.write(0, 1);

    // Write the remainder in P bits. Since the
    // remainder is just the bottom P bits of x,
    // there is no need to mask first.
    bitwriter.write(x, p.into());
}


pub fn golomb_rice_decode<IStream>(bitreader: &mut BitStreamReader<IStream>, p: u8) -> u64 where
    IStream: Default + Read
{
    // unary‑decode the quotient
    let mut q = 0u64;
    while bitreader.read(1) == 1 {
        q += 1;
    }

    // read the P‑bit remainder
    let r = bitreader.read(p.into());
    (q << p) + r
}

impl GCSFilter {

    pub fn new_with_encoded_filter(params: &GcsFilterParams, encoded_filter: Vec<u8>) -> Self {
        use std::{cell::RefCell, rc::Rc, sync::Arc};

        let data_arc = Arc::new(encoded_filter.clone());
        let mut rdr  = VectorReader::new(
            GCS_SER_TYPE as i32,
            GCS_SER_VERSION as i32,
            data_arc.clone(),
            0,
        );

        // 1. element count
        let n_u64 = read_compact_size(&mut rdr, None);
        let n     = u32::try_from(n_u64).expect("N must be < 2^32");
        let f     = n as u64 * *params.m() as u64;

        // 2. verify length by decoding
        let rc_rdr     = Rc::new(RefCell::new(rdr));
        let mut br     = BitStreamReader::<VectorReader>::new(rc_rdr.clone());
        for _ in 0..n {
            let _ = golomb_rice_decode(&mut br, *params.p());
        }
        if rc_rdr.borrow().size() != 0 {
            panic!("encoded_filter contains excess data");
        }

        GCSFilterBuilder::default()
            .params(params.clone())
            .n(n)
            .f(f)
            .encoded(encoded_filter)
            .build()
            .unwrap()
    }
}

impl GCSFilter {

    /// Build a filter from a concrete element set.
    pub fn new_with_element_set(params: &GcsFilterParams, elements: &GcsFilterElementSet) -> Self {
        use std::{cell::RefCell, rc::Rc};

        let n = u32::try_from(elements.len()).expect("N must be < 2^32");
        let f = n as u64 * *params.m() as u64;

        // Buffer into which the writer serialises.
        let vch_rc = Rc::new(RefCell::new(Vec::<u8>::new()));
        let mut vw = VectorWriter::new(
            GCS_SER_TYPE as i32,
            GCS_SER_VERSION as i32,
            vch_rc.clone(),
            0,
        );
        write_compact_size(&mut vw, n.into());

        // Empty filter – nothing more to do.
        if elements.is_empty() {

            let x = GCSFilterBuilder::default()
                .params(params.clone())
                .n(n)
                .f(f)
                .encoded(vch_rc.borrow().clone())
                .build()
                .unwrap();

            return x;
        }

        // BitStreamWriter over the same VectorWriter (via Rc<RefCell<…>>).
        let vw_rc  = Rc::new(RefCell::new(vw));
        let mut bw = BitStreamWriter::<VectorWriter>::new(vw_rc.clone());

        // hash, sort, encode deltas
        let mut hashed: Vec<u64> = elements
            .iter()
            .map(|e| {
                let mut h = std::hash::SipHasher::new_with_keys(
                    *params.siphash_k0(),
                    *params.siphash_k1(),
                );
                h.write(e);
                map_into_range(h.finish(), f)
            })
            .collect();
        hashed.sort_unstable();

        let mut last = 0u64;
        for v in hashed {
            let delta = v - last;
            golomb_rice_encode(&mut bw, *params.p(), delta);
            last = v;
        }
        bw.flush();

        let x = GCSFilterBuilder::default()
            .params(params.clone())
            .n(n)
            .f(f)
            .encoded(vch_rc.borrow().clone())
            .build()
            .unwrap();

        x
    }
}

impl GCSFilter {

    pub fn match_internal(&self, element_hashes: *const u64, size: usize) -> bool {
        use std::{cell::RefCell, rc::Rc, sync::Arc};

        let rdr_arc = Arc::new(self.encoded().clone());
        let mut vr  = VectorReader::new(
            GCS_SER_TYPE as i32,
            GCS_SER_VERSION as i32,
            rdr_arc,
            0,
        );

        let n_read = read_compact_size(&mut vr, None);
        assert_eq!(n_read as u32, *self.n());

        let rc_vr = Rc::new(RefCell::new(vr));
        let mut br = BitStreamReader::<VectorReader>::new(rc_vr.clone());

        let mut value = 0u64;
        let mut idx   = 0usize;

        for _ in 0..*self.n() {
            value += golomb_rice_decode(&mut br, *self.params().p());

            loop {
                if idx == size { return false; }
                unsafe {
                    let q = *element_hashes.add(idx);
                    if q == value { return true; }
                    if q >  value { break; }
                }
                idx += 1;
            }
        }
        false
    }
}

impl Default for GcsFilterParams {

    fn default() -> Self {
        Self {
            siphash_k0: 0,
            siphash_k1: 0,
            p:          0,  // C++ default
            m:          1,  // C++ default
        }
    }
}

impl GcsFilterParams {

    /// Parameter constructor mirroring the C++ `GCSFilter::Params` ctor.
    pub fn new(
        siphash_k0: Option<u64>,
        siphash_k1: Option<u64>,
        p:          Option<u8>,
        m:          Option<u32>
    ) -> Self
    {
        let params = Self {
            siphash_k0: siphash_k0.unwrap_or(0),
            siphash_k1: siphash_k1.unwrap_or(0),
            p:          p.unwrap_or(0),
            m:          m.unwrap_or(1),
        };
        trace!(target: "gcsfilter", ?params, "constructed GcsFilterParams");
        params
    }
}

impl From<Option<GcsFilterParams>> for GCSFilter {

    fn from(params: Option<GcsFilterParams>) -> Self {
        let params = params.unwrap_or_default();

        // Serialise CompactSize(0) with a `VectorWriter`.
        let buf_rc = std::rc::Rc::new(std::cell::RefCell::new(Vec::<u8>::new()));
        {
            let mut vw = VectorWriter::new(
                GCS_SER_TYPE as i32,
                GCS_SER_VERSION as i32,
                buf_rc.clone(),
                0,
            );
            write_compact_size(&mut vw, 0);
        }

        let x = Self { params, n: 0, f: 0, encoded: buf_rc.borrow().clone() };

        x
    }
}

impl GCSFilter {

    pub fn getn(&self) -> u32 {
        self.n
    }

    pub fn get_params(&self) -> &GcsFilterParams {
        &self.params
    }

    pub fn get_encoded(&self) -> &Vec<u8> {
        &self.encoded
    }

    /**
    | Hash a data element to an integer in the
    | range [0, N * M).
    |
    */
    pub fn hash_to_range(&self, element: &GcsFilterElement) -> u64 {
        let mut hasher = SipHasher::new_with_keys(
            *self.params.siphash_k0(),
            *self.params.siphash_k1(),
        );
        hasher.write(element);
        let hash = hasher.finish();
        map_into_range(hash, self.f)
    }

    pub fn build_hashed_set(&self, elements: &GcsFilterElementSet) -> Vec<u64> {
        let mut hashed_elements = Vec::<u64>::default();

        hashed_elements.reserve(elements.len());

        for element in elements.iter() {
            hashed_elements.push(self.hash_to_range(element));
        }

        hashed_elements.sort();

        hashed_elements
    }

    /**
    | Checks if the element may be in the set.
    | False positives are possible with probability
    | 1/M.
    |
    */
    pub fn match_(&self, element: &GcsFilterElement) -> bool {
        let query: u64 = self.hash_to_range(element);
        self.match_internal(&query, 1)
    }

    /**
    | Checks if any of the given elements may be
    | in the set. 
    |
    | False positives are possible with
    | probability 1/M per element checked.
    | This is more efficient that checking Match
    | on multiple elements separately.
    */
    pub fn match_any(&self, elements: &GcsFilterElementSet) -> bool {
        let queries: Vec<u64> = self.build_hashed_set(elements);
        self.match_internal(queries.as_ptr(), queries.len())
    }
}


