#[derive(Getters, MutGetters)]
#[getset(get = "pub(crate)", get_mut = "pub(crate)")]
pub struct PosixWritableFile  {
    buf:           [u8; WRITABLE_FILE_BUFFER_SIZE],
    pos:           usize,
    fd:            i32,
    is_manifest:   bool,
    filename:      String,
    dirname:       String,
    ever_valid_fd: bool,
}

impl PosixWritableFile {

    /**
    | Ensures that all the caches associated with
    | the given file descriptor's data are flushed
    | all the way to durable media, and can
    | withstand power failures.
    |
    | The path argument is only used to populate
    | the description string in the returned crate::Status
    | if an error occurs.
    */
    pub fn sync_fd(fd: i32, fd_path: &String, syncing_dir: bool) -> crate::Status {
        trace!(
            fd,
            path        = %fd_path,
            syncing_dir,
            "PosixWritableFile::sync_fd: start"
        );

        #[cfg(any(target_os = "macos", target_os = "ios"))]
        {
            // On macOS and iOS, fsync() doesn't guarantee durability past power
            // failures. fcntl(F_FULLFSYNC) is required for that purpose. Some
            // filesystems don't support fcntl(F_FULLFSYNC), and require a fallback to
            // fsync().
            let r = unsafe { libc::fcntl(fd, libc::F_FULLFSYNC) };
            if r == 0 {
                debug!(
                    fd,
                    path = %fd_path,
                    "PosixWritableFile::sync_fd: F_FULLFSYNC succeeded"
                );
                return crate::Status::ok();
            }
        }

        // Try fdatasync where available, otherwise fsync.
        #[cfg(any(
            target_os = "linux",
            target_os = "android",
            target_os = "freebsd",
            target_os = "openbsd",
            target_os = "netbsd",
            target_os = "dragonfly"
        ))]
        let sync_result = unsafe { libc::fdatasync(fd) };

        #[cfg(not(any(
            target_os = "linux",
            target_os = "android",
            target_os = "freebsd",
            target_os = "openbsd",
            target_os = "netbsd",
            target_os = "dragonfly"
        )))]
        let sync_result = unsafe { libc::fsync(fd) };

        if sync_result == 0 {
            debug!(
                fd,
                path = %fd_path,
                "PosixWritableFile::sync_fd: sync syscall succeeded"
            );
            return crate::Status::ok();
        }

        let err = std::io::Error::last_os_error()
            .raw_os_error()
            .unwrap_or(libc::EIO);

        // Do not treat EINVAL on directory sync as a hard error — some
        // filesystems can't fsync directories. This mirrors the comment in the
        // original code and Bitcoin's workaround.
        //
        //```
        // Do not crash if filesystem can't fsync directories
        // (see https://github.com/bitcoin/bitcoin/pull/10000)
        // ```
        //
        if syncing_dir && err == libc::EINVAL {
            debug!(
                fd,
                path = %fd_path,
                "PosixWritableFile::sync_fd: ignoring EINVAL on directory sync"
            );
            return crate::Status::ok();
        }

        debug!(
            fd,
            path = %fd_path,
            err,
            "PosixWritableFile::sync_fd: sync syscall failed"
        );
        posix_error(fd_path, err)
    }
}

impl PosixWritableFile {

    pub fn sync_dir_if_manifest(&mut self) -> crate::Status {
        use std::ffi::CString;

        trace!(
            file        = %self.filename(),
            is_manifest = *self.is_manifest(),
            dir         = %self.dirname(),
            "PosixWritableFile::sync_dir_if_manifest: start"
        );

        let mut status = crate::Status::ok();

        if !*self.is_manifest() {
            // Nothing to do for non-manifest files.
            return status;
        }

        let c_path = match CString::new(self.dirname().clone()) {
            Ok(c) => c,
            Err(_) => {
                let msg       = "invalid directory name (contains NUL)".to_string();
                let msg_slice = Slice::from(&msg);
                return crate::Status::io_error(&msg_slice, None);
            }
        };

        // No kOpenBaseFlags here; O_RDONLY is enough for our purposes.
        let fd = unsafe { libc::open(c_path.as_ptr(), libc::O_RDONLY) };
        if fd < 0 {
            let err = std::io::Error::last_os_error()
                .raw_os_error()
                .unwrap_or(libc::EIO);
            debug!(
                dir = %self.dirname(),
                err,
                "PosixWritableFile::sync_dir_if_manifest: open() failed"
            );
            status = posix_error(self.dirname(), err);
        } else {
            status = Self::sync_fd(fd, self.dirname(), true);
            unsafe {
                libc::close(fd);
            }
        }

        debug!(
            file = %self.filename(),
            dir  = %self.dirname(),
            ok   = status.is_ok(),
            "PosixWritableFile::sync_dir_if_manifest: completed"
        );
        status
    }
}

impl WritableFileSync for PosixWritableFile {

    fn sync(&mut self) -> crate::Status {
        trace!(
            file = %self.filename(),
            fd   = *self.fd(),
            "PosixWritableFile::sync: start"
        );

        // Ensure new files referred to by the manifest are in the filesystem
        // before we flush the manifest itself.
        let mut status = self.sync_dir_if_manifest();
        if !status.is_ok() {
            debug!(
                file       = %self.filename(),
                status_str = %status.to_string(),
                "PosixWritableFile::sync: SyncDirIfManifest failed"
            );
            return status;
        }

        status = self.flush_buffer();
        if !status.is_ok() {
            debug!(
                file       = %self.filename(),
                status_str = %status.to_string(),
                "PosixWritableFile::sync: FlushBuffer failed"
            );
            return status;
        }

        status = Self::sync_fd(*self.fd(), self.filename(), false);

        debug!(
            file = %self.filename(),
            ok   = status.is_ok(),
            "PosixWritableFile::sync: completed"
        );
        status
    }
}

impl WritableFileClose for PosixWritableFile {

    fn close(&mut self) -> Status {
        use libc::c_int;

        trace!(
            file = %self.filename(),
            fd   = *self.fd(),
            "PosixWritableFile::close: start"
        );

        if *self.fd() < 0 {
            // Distinguish "never had a valid fd" from "already closed once".
            if *self.ever_valid_fd() {
                debug!(
                    file = %self.filename(),
                    "PosixWritableFile::close: fd already < 0, treating as idempotent OK"
                );
                return Status::ok();
            } else {
                debug!(
                    file = %self.filename(),
                    "PosixWritableFile::close: fd was never valid, reporting IO error"
                );
                // Match what a real close(-1) would do: EBADF.
                return posix_error(self.filename(), libc::EBADF);
            }
        }

        let mut status = self.flush_buffer();

        let close_result = unsafe { libc::close(*self.fd() as c_int) };
        if close_result < 0 && status.is_ok() {
            let err = std::io::Error::last_os_error()
                .raw_os_error()
                .unwrap_or(libc::EIO);
            debug!(
                file = %self.filename(),
                err,
                "PosixWritableFile::close: close() failed"
            );
            status = posix_error(self.filename(), err);
        }

        *self.fd_mut() = -1;

        debug!(
            file = %self.filename(),
            ok   = status.is_ok(),
            "PosixWritableFile::close: completed"
        );
        status
    }
}

impl PosixWritableFile {

    /// Returns the directory name in a path pointing
    /// to a file.
    /// 
    /// Returns "." if the path does not contain any
    /// directory separator.
    ///
    pub fn dirname_static(filename: &String) -> String {
        trace!(file = %filename, "PosixWritableFile::dirname");

        if let Some(sep_pos) = filename.rfind('/') {
            // The filename component should not contain another path separator.
            debug_assert!(
                !filename[sep_pos + 1..].contains('/'),
                "PosixWritableFile::dirname: unexpected '/' in filename component: {}",
                filename
            );
            filename[..sep_pos].to_string()
        } else {
            ".".to_string()
        }
    }

    /// Extracts the file name from a path pointing
    /// to a file.
    /// 
    /// The returned Slice points to |filename|'s
    /// data buffer, so it is only valid while
    /// |filename| is alive and unchanged.
    ///
    pub fn basename(filename: &String) -> Slice {
        trace!(file = %filename, "PosixWritableFile::basename");

        if let Some(sep_pos) = filename.rfind('/') {
            debug_assert!(
                !filename[sep_pos + 1..].contains('/'),
                "PosixWritableFile::basename: unexpected '/' in filename component: {}",
                filename
            );

            let ptr = unsafe { filename.as_ptr().add(sep_pos + 1) };
            let len = filename.len() - sep_pos - 1;
            Slice::from_ptr_len(ptr, len)
        } else {
            Slice::from(filename)
        }
    }

    /// True if the given file is a manifest file.
    /// 
    pub fn is_manifest_static(filename: &String) -> bool {
        trace!(file = %filename, "PosixWritableFile::is_manifest");

        let base   = Self::basename(filename);
        let prefix = Slice::from("MANIFEST".as_bytes());
        base.starts_with(&prefix)
    }
}

impl WritableFile for PosixWritableFile {}

impl Drop for PosixWritableFile {

    fn drop(&mut self) {
        // Mirror the C++ destructor:
        // if (fd_ >= 0) { Close(); } ignoring any potential error.
        if *self.fd() >= 0 {
            trace!(
                file = %self.filename(),
                fd   = *self.fd(),
                "PosixWritableFile::drop: fd still open, calling close()"
            );
            let _ = self.close();
        } else {
            trace!(
                file = %self.filename(),
                fd   = *self.fd(),
                "PosixWritableFile::drop: fd already closed"
            );
        }
    }
}

impl Named for PosixWritableFile {

    fn name(&self) -> Cow<'_,str> {
        // We want a stable tag in logs / debugging.
        Cow::Borrowed("[posix-writable-file]")
    }
}

impl PosixWritableFile {

    pub fn new(filename: String, fd: i32) -> Self {
        trace!(
            file = %filename,
            fd,
            "PosixWritableFile::new"
        );

        let is_manifest   = Self::is_manifest_static(&filename);
        let dirname       = Self::dirname_static(&filename);
        let ever_valid_fd = fd >= 0;

        Self {
            buf: [0u8; WRITABLE_FILE_BUFFER_SIZE],
            pos: 0,
            fd,
            is_manifest,
            filename,
            dirname,
            ever_valid_fd,
        }
    }
}

impl WritableFileAppend for PosixWritableFile {

    fn append(&mut self, data: &Slice) -> Status {
        use std::cmp;

        let mut write_size = *data.size();

        trace!(
            file      = %self.filename(),
            write_len = write_size,
            buf_pos   = *self.pos(),
            "PosixWritableFile::append"
        );

        if write_size == 0 {
            return Status::ok();
        }

        unsafe {
            let mut write_ptr = *data.data();

            // Fit as much as possible into the buffer.
            let avail     = WRITABLE_FILE_BUFFER_SIZE.saturating_sub(*self.pos());
            let copy_size = cmp::min(write_size, avail);

            if copy_size > 0 {
                let dst = self.buf_mut().as_mut_ptr().add(*self.pos());
                std::ptr::copy_nonoverlapping(write_ptr, dst, copy_size);
                *self.pos_mut() += copy_size;
                write_ptr = write_ptr.add(copy_size);
                write_size -= copy_size;
            }

            if write_size == 0 {
                // Everything fit in the buffer.
                debug!(
                    file    = %self.filename(),
                    buf_pos = *self.pos(),
                    "PosixWritableFile::append: fully buffered"
                );
                return Status::ok();
            }

            // Can't fit in buffer, so need to do at least one write.
            let mut status = self.flush_buffer();
            if !status.is_ok() {
                debug!(
                    file       = %self.filename(),
                    status_str = %status.to_string(),
                    "PosixWritableFile::append: FlushBuffer failed"
                );
                return status;
            }

            // Small writes go to buffer, large writes are written directly.
            if write_size < WRITABLE_FILE_BUFFER_SIZE {
                let dst = self.buf_mut().as_mut_ptr();
                std::ptr::copy_nonoverlapping(write_ptr, dst, write_size);
                *self.pos_mut() = write_size;
                debug!(
                    file    = %self.filename(),
                    buf_pos = *self.pos(),
                    "PosixWritableFile::append: remaining data buffered"
                );
                return Status::ok();
            }

            // Large remaining write: bypass the buffer.
            self.write_unbuffered(write_ptr, write_size)
        }
    }
}

impl WritableFileFlush for PosixWritableFile {

    fn flush(&mut self) -> crate::Status {
        trace!(
            file    = %self.filename(),
            buf_pos = *self.pos(),
            "PosixWritableFile::flush"
        );
        self.flush_buffer()
    }
}

impl PosixWritableFile {

    pub fn flush_buffer(&mut self) -> crate::Status {
        trace!(
            file    = %self.filename(),
            buf_pos = *self.pos(),
            "PosixWritableFile::flush_buffer: start"
        );

        let status = if *self.pos() == 0 {
            crate::Status::ok()
        } else {
            unsafe { self.write_unbuffered(self.buf().as_ptr(), *self.pos()) }
        };

        // C++ code resets pos_ even if the write failed.
        *self.pos_mut() = 0;

        debug!(
            file = %self.filename(),
            ok   = status.is_ok(),
            "PosixWritableFile::flush_buffer: completed"
        );
        status
    }
}

impl PosixWritableFile {

    pub fn write_unbuffered(&mut self, mut data: *const u8, mut size: usize) -> crate::Status {
        trace!(
            file = %self.filename(),
            fd   = *self.fd(),
            size,
            "PosixWritableFile::write_unbuffered: start"
        );

        while size > 0 {
            let write_result = unsafe {
                libc::write(
                    *self.fd(),
                    data as *const libc::c_void,
                    size as libc::size_t,
                )
            };

            if write_result < 0 {
                let err = std::io::Error::last_os_error()
                    .raw_os_error()
                    .unwrap_or(libc::EIO);

                if err == libc::EINTR {
                    trace!(
                        file = %self.filename(),
                        "PosixWritableFile::write_unbuffered: EINTR, retrying"
                    );
                    continue;
                }

                debug!(
                    file = %self.filename(),
                    err,
                    "PosixWritableFile::write_unbuffered: write() failed"
                );
                return posix_error(self.filename(), err);
            }

            let written = write_result as usize;
            unsafe {
                data = data.add(written);
            }
            size -= written;
        }

        debug!(
            file = %self.filename(),
            "PosixWritableFile::write_unbuffered: completed successfully"
        );
        crate::Status::ok()
    }
}


