We have a rust crate named 'bitcoin-syscall' 0.1.2. 

Here is the interface:
pub enum SyscallSandboxPolicy {
    Initialization,
    InitializationDnsSeed,
    InitializationLoadBlocks,
    InitializationMapPort,
    MESSAGE_HANDLER,
    NET,
    NET_ADD_CONNECTION,
    NET_HTTP_SERVER,
    NET_HTTP_SERVER_WORKER,
    NET_OPEN_CONNECTION,
    SCHEDULER,
    TOR_CONTROL,
    TX_INDEX,
    VALIDATION_SCRIPT_CHECK,
    SHUTOFF,
}


pub enum SyscallSandboxAction {
    KILL_PROCESS,
    INVOKE_SIGNAL_HANDLER,
}

lazy_static!{
    static G_SYSCALL_SANDBOX_ENABLED:                          AtomicBool = AtomicBool::new(false);
    static G_SYSCALL_SANDBOX_LOG_VIOLATION_BEFORE_TERMINATING: AtomicBool = AtomicBool::new(false);
}

pub struct SeccompPolicyBuilder  {
    allowed_syscalls: HashSet<u32>,
}

/// See Linux kernel developer Kees Cook's seccomp guide at <https://outflux.net/teach-seccomp/>
/// for an accessible introduction to using seccomp.
/// 
/// This function largely follows <https://outflux.net/teach-seccomp/step-3/syscall-reporter.c> and
/// <https://outflux.net/teach-seccomp/step-3/seccomp-bpf.h>.
/// Seccomp BPF resources:
/// - Seccomp BPF documentation:
/// <https://www.kernel.org/doc/html/latest/userspace-api/seccomp_filter.html>
/// - seccomp(2) manual page:
/// <https://www.kernel.org/doc/man-pages/online/pages/man2/seccomp.2.html>
/// - Seccomp BPF demo code samples:
/// <https://git.kernel.org/pub/scm/linux/kernel/git/torvalds/linux.git/tree/samples/seccomp>
///
/// Rust port of `SyscallSandboxDebugSignalHandler`.
/// The signature, variable names and control‑flow mirror the original C++ so that a side‑by‑side
/// comparison is trivial.
#[no_mangle]
pub fn syscall_sandbox_debug_signal_handler(_0: i32, signal_info: *mut siginfo_t, void_signal_context: *mut c_void) {
    #[cfg(all(USE_SYSCALL_SANDBOX, target_os = "linux", target_arch = "x86_64"))]
    {
        trace!(target: "compat::syscall_sandbox", "SyscallSandboxDebugSignalHandler entered");

        unsafe {
            if signal_info.is_null() {
                error!(target: "compat::syscall_sandbox", "null siginfo_t pointer");
                std::process::abort();
            }
            assert_eq!((*signal_info).si_code, SYS_SECCOMP_SI_CODE);

            if void_signal_context.is_null() {
                error!(target: "compat::syscall_sandbox", "null ucontext_t pointer");
                std::process::abort();
            }
            let signal_context: &ucontext_t = &*(void_signal_context as *const ucontext_t);

            #[allow(clippy::cast_sign_loss)]
            let syscall_number: u32 = signal_context.uc_mcontext.gregs[libc::REG_RAX as usize] as u32;

            let syscall_name = get_linux_syscall_name(syscall_number);
            let thread_name = thread_get_internal_name();
            let thread_name = if thread_name.is_empty() {
                "*unnamed*".to_owned()
            } else {
                thread_name
            };

            let error_message = format!(
                "ERROR: The syscall \"{}\" (syscall number {}) is not allowed by the syscall sandbox \
                in thread \"{}\". Please report.",
                syscall_name, syscall_number, thread_name
            );

            eprintln!("{error_message}");
            error!(target: "compat::syscall_sandbox", "{error_message}");

            std::process::abort();
        }
        return;
    }

    // Unsupported platform – just terminate.
    error!(
        target: "compat::syscall_sandbox",
        "Syscall sandbox debug handler invoked on unsupported platform"
    );
    std::process::abort();
}


/// Force the current thread (and threads created from the current thread) into
/// a restricted-service operating mode where only a subset of all syscalls are
/// available.
/// 
/// Subsequent calls to this function can reduce the abilities further, but
/// abilities can never be regained.
/// This function is a no-op unless SetupSyscallSandbox(...) has been called.
/// SetupSyscallSandbox(...) is called during bitcoind initialization if Bitcoin
/// Core was compiled with seccomp-bpf support (--with-seccomp) *and* the
/// parameter -sandbox=<mode> was passed to bitcoind.
/// This experimental feature is available under Linux x86_64 only.
///
pub fn set_syscall_sandbox_policy(policy: SyscallSandboxPolicy) {
    // If the sandbox is not enabled yet, this is a no‑op – identical to C++.
    if !g_syscall_sandbox_enabled.load(atomic::Ordering::SeqCst) {
        return;
    }

    /* ----------- build the allow‑list ---------------------------------- */
    let mut b = SeccompPolicyBuilder::default();

    match policy {
        /* -------- 1. initialisation‑phase buckets ---------- */
        // SyscallSandboxPolicy::INITIALIZATION is the first policy loaded.
        //
        // Subsequently loaded policies can reduce the abilities further, but
        // abilities can never be regained.
        //
        // SyscallSandboxPolicy::INITIALIZATION must thus be a superset of all
        // other policies.
        SyscallSandboxPolicy::INITIALIZATION
            | SyscallSandboxPolicy::INITIALIZATION_DNS_SEED
            | SyscallSandboxPolicy::INITIALIZATION_MAP_PORT => {
            b.allow_file_system();
            b.allow_network();
        }
        SyscallSandboxPolicy::INITIALIZATION_LOAD_BLOCKS => {
            b.allow_file_system();
        }

        /* -------- 2. steady‑state buckets ------------------ */
        SyscallSandboxPolicy::MESSAGE_HANDLER
        | SyscallSandboxPolicy::SCHEDULER
        | SyscallSandboxPolicy::TX_INDEX => {
            b.allow_file_system();
        }
        SyscallSandboxPolicy::NET
        | SyscallSandboxPolicy::NET_ADD_CONNECTION
        | SyscallSandboxPolicy::NET_HTTP_SERVER
        | SyscallSandboxPolicy::NET_HTTP_SERVER_WORKER
        | SyscallSandboxPolicy::NET_OPEN_CONNECTION
        | SyscallSandboxPolicy::TOR_CONTROL => {
            b.allow_file_system();
            b.allow_network();
        }
        SyscallSandboxPolicy::VALIDATION_SCRIPT_CHECK => {
            /* nothing extra */
        }

        /* -------- 3. shutdown bucket ---------------------- */
        SyscallSandboxPolicy::SHUTOFF => {
            b.allow_file_system();
        }
    }

    /* ----------- compile the BPF programme ----------------------------- */
    let default_action = if g_syscall_sandbox_log_violation_before_terminating
        .load(atomic::Ordering::SeqCst)
    {
        SyscallSandboxAction::INVOKE_SIGNAL_HANDLER
    } else {
        SyscallSandboxAction::KILL_PROCESS
    };

    let filter = b.build_filter(default_action);

    let mut prog = libc::sock_fprog {
        len: filter.len() as libc::c_ushort,
        filter: filter.as_ptr() as *mut _,
    };

    /* ----------- hand the filter to the kernel ------------------------- */
    // Do not allow abilities to be regained after being dropped.
    //
    // PR_SET_NO_NEW_PRIVS documentation: <https://www.kernel.org/doc/html/latest/userspace-api/no_new_privs.html>
    unsafe {

        // Prevent regaining privileges after seccomp is active
        if libc::prctl(libc::PR_SET_NO_NEW_PRIVS, 1, 0, 0, 0) != 0 {
            panic!("Syscall sandbox enforcement failed: prctl(PR_SET_NO_NEW_PRIVS)");
        }

        // Install seccomp-bpf syscall filter itself.
        //
        // PR_SET_SECCOMP documentation: <https://www.kernel.org/doc/html/latest/userspace-api/seccomp_filter.html>
        if libc::prctl(
            libc::PR_SET_SECCOMP,
            libc::SECCOMP_MODE_FILTER,
            &mut prog as *mut _,
        ) != 0
        {
            panic!("Syscall sandbox enforcement failed: prctl(PR_SET_SECCOMP)");
        }
    }

    let thread = thread_get_internal_name();

    info!{
        target: "compat::syscall_sandbox",
        "Syscall filter installed for thread \"{}\"",
        if thread.is_empty() { "*unnamed*" } else { &thread }
    };
}


/// This function largely follows install_syscall_reporter from Kees Cook's seccomp guide:
/// <https://outflux.net/teach-seccomp/step-3/syscall-reporter.c>
///
/// Rust port of `SetupSyscallSandboxDebugHandler`.
/// Registers `SyscallSandboxDebugSignalHandler` as the SIGSYS handler and unblocks SIGSYS so the
/// kernel can deliver the signal.
#[inline]
pub fn setup_syscall_sandbox_debug_handler() -> bool {
    #[cfg(all(USE_SYSCALL_SANDBOX, target_os = "linux", target_arch = "x86_64"))]
    {
        unsafe {
            trace!(
                target: "compat::syscall_sandbox",
                "installing SIGSYS debug handler"
            );

            let mut action: sigaction = std::mem::zeroed();
            let mut mask = std::mem::zeroed();

            sigemptyset(&mut mask);
            sigaddset(&mut mask, SIGSYS);

            action.sa_sigaction = syscall_sandbox_debug_signal_handler as usize;
            action.sa_flags = SA_SIGINFO;

            if libc::sigaction(SIGSYS, &action, ptr::null_mut()) != 0 {
                error!(target: "compat::syscall_sandbox", "sigaction failed");
                return false;
            }
            if sigprocmask(SIG_UNBLOCK, &mask, ptr::null_mut()) != 0 {
                error!(target: "compat::syscall_sandbox", "sigprocmask failed");
                return false;
            }
        }
        return true;
    }

    false
}


/**
| Setup and enable the experimental syscall
| sandbox for the running process.
|
| SetSyscallSandboxPolicy(SyscallSandboxPolicy::INITIALIZATION)
| is called as part of SetupSyscallSandbox(...).
*/
pub fn setup_syscall_sandbox(log_before_terminate: bool) -> bool {
    // Only succeed on first invocation (same assert as C++).
    if G_SYSCALL_SANDBOX_ENABLED.swap(true, atomic::Ordering::SeqCst) {
        panic!("SetupSyscallSandbox(...) should only be called once.");
    }
    G_SYSCALL_SANDBOX_LOG_VIOLATION_BEFORE_TERMINATING
        .store(log_before_terminate, atomic::Ordering::SeqCst);

    if log_before_terminate && !setup_syscall_sandbox_debug_handler() {
        return false;
    }

    // Load the most permissive (initialisation) profile first.
    set_syscall_sandbox_policy(SyscallSandboxPolicy::INITIALIZATION);
    true
}


/**
| Invoke a disallowed syscall. Use for
| testing purposes.
|
*/
pub fn test_disallowed_sandbox_call() {
    // Intentionally use a syscall (getgroups) that is *not* on any allow‑list.
    //
    // The getgroups syscall is assumed NOT to be allowed by the syscall sandbox policy.
    unsafe {
        let mut buf: [libc::gid_t; 1] = [0];
        libc::getgroups(buf.len() as libc::c_int, buf.as_mut_ptr());
    }
}


/// Return the symbolic name of `syscall_number` if it
/// is present in `LINUX_SYSCALLS`; otherwise return
/// `"*unknown*"` (mirrors the C++ helper).
#[inline]
pub fn get_linux_syscall_name(syscall_number: u32) -> String {
    trace!(
        target: "compat::syscall_sandbox",
        syscall_number,
        "lookup syscall name"
    );

    LINUX_SYSCALLS
        .get(&syscall_number)
        .copied()
        .unwrap_or("*unknown*")
        .to_owned()
}


/// Return the current thread name or an empty
/// string if no name has been set (mirrors
/// `util::ThreadGetInternalName()` in the C++ code).
#[inline]
pub fn thread_get_internal_name() -> String {
    // First use std’s bookkeeping if present.
    if let Some(name) = std::thread::current().name() {
        return name.to_owned();
    }

    // Fallback to POSIX `pthread_getname_np`.
    unsafe {
        let mut buf = [0u8; 16]; // Linux limits names to 16 bytes inc. NUL
        if pthread_getname_np(pthread_self(), buf.as_mut_ptr() as *mut _, buf.len()) == 0 {
            // Strip trailing NUL and convert to UTF‑8.
            if let Ok(s) = CStr::from_ptr(buf.as_ptr() as *const _).to_str() {
                return s.to_owned();
            }
        }
    }
    String::new()
}

impl Default for SeccompPolicyBuilder {

    fn default() -> Self {
        let mut builder = Self {
            allowed_syscalls: HashSet::new(),
        };

        // ----- Populate default set --------------------------------------
        builder.allow_address_space_access();
        builder.allow_epoll();
        builder.allow_event_fd();
        builder.allow_futex();
        builder.allow_general_io();
        builder.allow_get_random();
        builder.allow_get_simple_id();
        builder.allow_get_time();
        builder.allow_global_process_environment();
        builder.allow_global_system_status();
        builder.allow_kernel_internal_api();
        builder.allow_network_socket_information();
        builder.allow_operation_on_existing_file_descriptor();
        builder.allow_pipe();
        builder.allow_prctl();
        builder.allow_process_start_or_death();
        builder.allow_scheduling();
        builder.allow_signal_handling();
        builder.allow_sleep();
        builder.allow_umask();

        builder
    }
}

impl SeccompPolicyBuilder {

    type SockFilter = libc::sock_filter;

    type SockFprog = libc::sock_fprog;

    pub fn allow_address_space_access(&mut self) {
        trace!("allow_address_space_access");

        // change data segment size
        self.insert(libc::SYS_brk);

        // give advice about use of memory
        self.insert(libc::SYS_madvise);

        // issue memory barriers on a set of threads
        self.insert(libc::SYS_membarrier);

        // check if virtual memory is in RAM
        self.insert(libc::SYS_mincore);

        // lock memory
        self.insert(libc::SYS_mlock);

        // map files or devices into memory
        self.insert(libc::SYS_mmap);

        // set protection on a region of memory
        self.insert(libc::SYS_mprotect);

        // remap a file in memory
        self.insert(libc::SYS_mremap);

        // unlock memory
        self.insert(libc::SYS_munlock);

        // unmap files or devices into memory
        self.insert(libc::SYS_munmap);
    }

    pub fn allow_epoll(&mut self) {
        trace!("allow_epoll");

        // open an epoll file descriptor
        self.insert(libc::SYS_epoll_create1);

        // control interface for an epoll file descriptor
        self.insert(libc::SYS_epoll_ctl);

        // wait for an I/O event on an epoll file descriptor
        self.insert(libc::SYS_epoll_pwait);

