pub fn do_write_string_to_file(
    env:         Rc<RefCell<dyn Env>>,
    data:        &Slice,
    fname:       &String,
    should_sync: bool
) -> Status
{
    use std::ptr;

    trace!(
        file = %fname,
        should_sync,
        data_len = *data.size(),
        "do_write_string_to_file: start"
    );

    // C++: WritableFile* file; Status s = env->NewWritableFile(fname, &file);
    let mut file_ptr: *mut Box<dyn WritableFile> = ptr::null_mut();
    let mut status = {
        let mut env_ref = env.borrow_mut();
        env_ref.new_writable_file(fname, &mut file_ptr)
    };

    if !status.is_ok() {
        debug!(
            file = %fname,
            status_str = %status.to_string(),
            "do_write_string_to_file: NewWritableFile failed"
        );
        return status;
    }

    // Take ownership of allocated file (mirrors C++ delete file).
    // We were given `*mut Box<dyn WritableFile>`, so reconstruct the outer Box
    // and then borrow the inner `Box<dyn WritableFile>` mutably.
    let mut file_holder: Box<Box<dyn WritableFile>> = unsafe {
        assert!(
            !file_ptr.is_null(),
            "Env::NewWritableFile returned a null file pointer"
        );
        Box::from_raw(file_ptr)
    };

    let file: &mut Box<dyn WritableFile> = file_holder.as_mut();

    // C++: s = file->Append(data);
    status = file.append(data);

    // C++: if (s.ok() && should_sync) { s = file->Sync(); }
    if status.is_ok() && should_sync {
        trace!(file = %fname, "do_write_string_to_file: calling Sync");
        status = file.sync();
    }

    // C++: if (s.ok()) { s = file->Close(); }
    if status.is_ok() {
        trace!(file = %fname, "do_write_string_to_file: calling Close");
        status = file.close();
    }

    // `file_holder` drops here, mirroring `delete file` in C++.

    // C++: if (!s.ok()) { env->DeleteFile(fname); }
    if !status.is_ok() {
        debug!(
            file = %fname,
            status_str = %status.to_string(),
            "do_write_string_to_file: error, deleting file"
        );
        let mut env_ref = env.borrow_mut();
        let _ = env_ref.delete_file(fname);
    }

    debug!(
        file = %fname,
        should_sync,
        ok = status.is_ok(),
        "do_write_string_to_file: completed"
    );
    status
}


/**
| Make the CURRENT file point to the descriptor
| file with the specified number.
|
*/
pub fn set_current_file(env: Rc<RefCell<dyn Env>>, dbname: &String, descriptor_number: u64) -> Status {
    trace!(
        dbname = %dbname,
        descriptor_number,
        "set_current_file: start"
    );

    // C++: std::string manifest = DescriptorFileName(dbname, descriptor_number);
    let manifest = descriptor_file_name(dbname, descriptor_number);

    // C++: Slice contents = manifest;
    let mut contents = Slice::from(&manifest);

    // C++: assert(contents.starts_with(dbname + "/"));
    let prefix_string = {
        let mut s = dbname.clone();
        s.push('/');
        s
    };
    let prefix_slice = Slice::from(&prefix_string);
    assert!(
        contents.starts_with(&prefix_slice),
        "set_current_file: manifest '{}' does not start with '{}'",
        manifest,
        prefix_string,
    );

    // C++: contents.remove_prefix(dbname.size() + 1);
    contents.remove_prefix(dbname.len() + 1);

    // C++: std::string tmp = TempFileName(dbname, descriptor_number);
    let tmp = temp_file_name(dbname, descriptor_number);

    // C++: Status s = WriteStringToFileSync(env, contents.ToString() + "\n", tmp);
    let content_string = {
        let mut s = contents.to_string();
        s.push('\n');
        s
    };
    let content_slice = Slice::from(&content_string);

    trace!(
        dbname = %dbname,
        tmp = %tmp,
        "set_current_file: writing CURRENT contents to temporary file"
    );

    let mut status = write_string_to_file_sync(env.clone(), &content_slice, &tmp);

    if status.is_ok() {
        let current_name = current_file_name(dbname);
        trace!(
            tmp = %tmp,
            current = %current_name,
            "set_current_file: renaming temporary file to CURRENT"
        );
        status = {
            let mut env_ref = env.borrow_mut();
            env_ref.rename_file(&tmp, &current_name)
        };
    }

    if !status.is_ok() {
        trace!(
            tmp = %tmp,
            status_str = %status.to_string(),
            "set_current_file: operation failed, deleting temporary file"
        );
        let mut env_ref = env.borrow_mut();
        let _ = env_ref.delete_file(&tmp);
    }

    debug!(
        dbname = %dbname,
        descriptor_number,
        ok = status.is_ok(),
        "set_current_file: completed"
    );
    status
}

