/// Securely overwrite `len` bytes at `ptr` with zeros.  
/// Uses *volatile* writes plus a compiler fence so the store
/// will **not** be removed by dead‑store or DSE passes.
#[instrument(level = "trace", skip(ptr))]
pub fn memory_cleanse(ptr: *mut c_void, len: usize) {
    if ptr.is_null() || len == 0 {
        trace!("memory_cleanse: nothing to do");
        return;
    }

    unsafe {
        let mut p = ptr as *mut u8;
        for _ in 0..len {
            core::ptr::write_volatile(p, 0);
            p = p.add(1);
        }
        /* Prevent the compiler from re‑ordering or eliding the wipes.   */
        compiler_fence(atomic::Ordering::SeqCst);
    }
}


/// Join `base` with an 8‑hex‑char random component that is *very* likely unique.
pub fn get_unique_path(base: &Path) -> PathBuf {
    // Use high‑resolution timestamp mixed with address entropy for uniqueness
    let nanos = SystemTime::now()
        .duration_since(UNIX_EPOCH)
        .expect("time went backwards")
        .as_nanos();

    // XOR with the function address to add a little extra variability
    let mix = nanos ^ get_unique_path as usize as u128;
    let name = format!("{:08x}", mix & 0xffff_ffff); // 8 hex chars

    base.join(name)
}

