/// Comparator used by `SkipList`.
///
/// This is intentionally very close to LevelDB's `Comparator` template
/// parameter: it compares two keys and returns `< 0`, `0`, or `> 0`.
pub trait SkipListComparator<K> {
    fn compare(&self, a: &K, b: &K) -> i32;
}

/// Skiplist iterator.
///
/// Intentionally copyable. It holds an immutable reference to the list
/// and a raw pointer to the current node.
pub struct SkipListIterator<'a, K, C> where
    K: core::fmt::Debug + Copy + Default,
    C: SkipListComparator<K> {
    list: &'a SkipList<K, C>,
    node: *mut SkipListNode<K>,
}


/// Internal node type used by `SkipList`.
///
/// This struct is laid out so that we can store a variable number of
/// `next` pointers in arena-allocated memory, exactly like the C++
/// implementation does with a flexible array member.
#[repr(C)]
pub struct SkipListNode<K> where
    K: Copy + Default {
    key:  K,
    next: [AtomicPtr<SkipListNode<K>>; 1],
}


/// Arena-backed skiplist, translated from LevelDB's `SkipList<Key, Comparator>`.
///
/// - `K` is the key type. It must be `Copy + Default` so that we can
///   cheaply pass keys by value and create the dummy head key.
/// - `C` is a comparator implementing `SkipListComparator<K>`.
#[derive(Getters,MutGetters)]
#[getset(get="pub",get_mut="pub")]
pub struct SkipList<K, C> where
    K: Debug + Copy + Default,
    C: SkipListComparator<K> {
    compare:    C,
    arena:      *mut Arena,
    head:       *mut SkipListNode<K>,
    max_height: AtomicI32,
    rnd:        Random,
}

impl<K, F> SkipListComparator<K> for F where
    F: Fn(&K, &K) -> i32 + Send + Sync {

    #[inline]
    fn compare(&self, a: &K, b: &K) -> i32 {
        self(a, b)
    }
}

impl<'a, K, C> Clone for SkipListIterator<'a, K, C> where
    K: core::fmt::Debug + Copy + Default,
    C: SkipListComparator<K> {

    fn clone(&self) -> Self {
        trace!(
            "SkipListIterator::clone: list={:p}, node={:p}",
            self.list,
            self.node
        );
        Self {
            list: self.list,
            node: self.node,
        }
    }
}

impl<'a, K, C> SkipListIterator<'a, K, C> where
    K: core::fmt::Debug + Copy + Default,
    C: SkipListComparator<K> {

    /// Initialize an iterator over the specified list.
    /// The returned iterator is not valid.
    pub fn new(list: &'a SkipList<K, C>) -> Self {
        trace!("SkipListIterator::new: list={:p}", list);
        Self {
            list,
            node: core::ptr::null_mut(),
        }
    }

    /// Returns true iff the iterator is positioned at a valid node.
    #[inline]
    pub fn valid(&self) -> bool {
        let v = !self.node.is_null();
        trace!("SkipListIterator::valid -> {}", v);
        v
    }

    /// Returns the key at the current position.
    /// REQUIRES: Valid()
    #[inline]
    pub fn key(&self) -> K {
        assert!(
            self.valid(),
            "SkipListIterator::key called on invalid iterator"
        );
        unsafe {
            let v = (*self.node).key_ref();
            trace!("SkipListIterator::key -> {:?}", v);
            *v
        }
    }

    /// Advances to the next position.
    /// REQUIRES: Valid()
    #[inline]
    pub fn next(&mut self) {
        assert!(
            self.valid(),
            "SkipListIterator::next called on invalid iterator"
        );
        unsafe {
            // Call `next` on a shared reference to the current node.
            let node_ref: &SkipListNode<K> = &*self.node;
            self.node = node_ref.next(0);
            trace!("SkipListIterator::next: node={:p}", self.node);
        }
    }

    /// Advances to the previous position.
    /// REQUIRES: Valid()
    #[inline]
    pub fn prev(&mut self) {
        assert!(
            self.valid(),
            "SkipListIterator::prev called on invalid iterator"
        );
        let cur = self.key();
        unsafe {
            let mut x = self.list.find_less_than(&cur);
            let head_ptr: *mut SkipListNode<K> = *self.list.head();
            if x == head_ptr {
                x = core::ptr::null_mut();
            }
            self.node = x;
            trace!(
                "SkipListIterator::prev: cur={:?}, node={:p}",
                cur,
                self.node
            );
        }
    }

    /// Advance to the first entry with a key >= target
    #[inline]
    pub fn seek(&mut self, target: &K) {
        trace!("SkipListIterator::seek: target={:?}", target);
        let x = self.list.find_greater_or_equal(target, None);
        self.node = x;
        trace!("SkipListIterator::seek: node={:p}", self.node);
    }

    /// Position at the first entry in list.
    /// Final state of iterator is Valid() iff list is not empty.
    #[inline]
    pub fn seek_to_first(&mut self) {
        trace!("SkipListIterator::seek_to_first");
        unsafe {
            let head_ptr: *mut SkipListNode<K> = *self.list.head();
            let next_ptr = match head_ptr.as_ref() {
                Some(head_node) => {
                    let next = head_node.next(0);
                    trace!(
                        "SkipListIterator::seek_to_first: head={:p}, next={:p}",
                        head_ptr,
                        next
                    );
                    next
                }
                None => {
                    warn!(
                        "SkipListIterator::seek_to_first: head pointer is null; iterator will be invalid"
                    );
                    core::ptr::null_mut()
                }
            };
            self.node = next_ptr;
            trace!("SkipListIterator::seek_to_first: node={:p}", self.node);
        }
    }

    /// Position at the last entry in list.
    /// Final state of iterator is Valid() iff list is not empty.
    #[inline]
    pub fn seek_to_last(&mut self) {
        trace!("SkipListIterator::seek_to_last");
        unsafe {
            let mut x = self.list.find_last();
            let head_ptr: *mut SkipListNode<K> = *self.list.head();
            if x == head_ptr {
                x = core::ptr::null_mut();
            }
            self.node = x;
            trace!("SkipListIterator::seek_to_last: node={:p}", self.node);
        }
    }
}

impl<K> SkipListNode<K> where
    K: Copy + Default {

    #[inline]
    pub(crate) fn write_key_at(node: *mut Self, key: K) {
        trace!("SkipListNode::write_key_at: node={:p}", node);
        core::ptr::write(&mut (*node).key, key);
    }

    #[inline]
    pub fn key_ref(&self) -> &K {
        &self.key
    }

    #[inline]
    pub fn next_base_ptr(node: *mut Self) -> *mut AtomicPtr<Self> {
        (&mut (*node).next[0]) as *mut AtomicPtr<Self>
    }

    #[inline]
    pub fn next_slot(node: *mut Self, level: i32) -> *mut AtomicPtr<Self> {
        debug_assert!(level >= 0);
        Self::next_base_ptr(node).add(level as usize)
    }

    /// Load the `next` pointer at a given level with acquire semantics.
    #[inline]
    pub fn next(&self, level: i32) -> *mut Self {
        assert!(level >= 0, "SkipListNode::next: negative level {}", level);
        unsafe {
            let slot =
                Self::next_slot(self as *const Self as *mut Self, level);

            // Use an 'acquire load' so that we observe a fully initialized
            // version of the returned SkipListNode.
            (*slot).load(atomic::Ordering::Acquire)
        }
    }

    /// Store the `next` pointer at a given level with release semantics.
    #[inline]
    pub fn set_next(&self, level: i32, node: *mut Self) {
        assert!(
            level >= 0,
            "SkipListNode::set_next: negative level {}",
            level
        );
        unsafe {
            let slot =
                Self::next_slot(self as *const Self as *mut Self, level);
            // Use a 'release store' so that anybody who reads through this
            // pointer observes a fully initialized version of the inserted node.
            (*slot).store(node, atomic::Ordering::Release);
        }
    }

    /// Load the `next` pointer at a given level with relaxed semantics.
    #[inline]
    pub fn no_barrier_next(&self, level: i32) -> *mut Self {
        assert!(
            level >= 0,
            "SkipListNode::no_barrier_next: negative level {}",
            level
        );
        unsafe {
            let slot =
                Self::next_slot(self as *const Self as *mut Self, level);
            (*slot).load(atomic::Ordering::Relaxed)
        }
    }

    /// Store the `next` pointer at a given level with relaxed semantics.
    #[inline]
    pub fn no_barrier_set_next(&self, level: i32, node: *mut Self) {
        assert!(
            level >= 0,
            "SkipListNode::no_barrier_set_next: negative level {}",
            level
        );
        unsafe {
            let slot =
                Self::next_slot(self as *const Self as *mut Self, level);
            (*slot).store(node, atomic::Ordering::Relaxed);
        }
    }
}

impl<K, C> SkipList<K, C> where
    K: Debug + Copy + Default,
    C: SkipListComparator<K> {

    /// Return the height of the skiplist (maximum tower height used so far).
    #[inline]
    pub fn get_max_height(&self) -> i32 {
        let h = self.max_height.load(atomic::Ordering::Relaxed);
        trace!("SkipList::get_max_height -> {}", h);
        h
    }

    #[inline]
    pub fn equal(&self, a: &K, b: &K) -> bool {
        let r = self.compare.compare(a, b) == 0;
        trace!("SkipList::equal({:?}, {:?}) -> {}", a, b, r);
        r
    }

    /// Allocate and initialize a new node with the given key and height.
    ///
    /// Memory is taken from the arena, matching the C++ implementation:
    ///   sizeof(Node) + sizeof(AtomicPtr<Node>) * (height - 1)
    pub fn new_node(&mut self, key: K, height: i32) -> *mut SkipListNode<K> {
        debug!("SkipList::new_node: key={:?}, height={}", key, height);
        assert!(
            height >= 1 && height <= SkipListMaxHeight as i32,
            "SkipList::new_node: invalid height {}",
            height
        );

        let extra = (height as usize - 1)
            * core::mem::size_of::<core::sync::atomic::AtomicPtr<SkipListNode<K>>>();
        let node_size = core::mem::size_of::<SkipListNode<K>>() + extra;

        unsafe {
            let arena_ref: &mut Arena = &mut *self.arena;
            let mem = arena_ref.allocate_aligned(node_size);
            let node = mem as *mut SkipListNode<K>;

            SkipListNode::<K>::write_key_at(node, key);

            for level in 0..height {
                let slot = SkipListNode::<K>::next_slot(node, level);
                core::ptr::write(
                    slot,
                    core::sync::atomic::AtomicPtr::new(core::ptr::null_mut()),
                );
            }

            node
        }
    }

    /// Randomly choose a height for a new node.
    ///
    /// This matches LevelDB's RandomHeight() with kBranching=4.
    pub fn random_height(&mut self) -> i32 {
        const K_BRANCHING: u32 = 4;

        let mut height: i32 = 1;
        while height < SkipListMaxHeight as i32
            && (self.rnd.next() % K_BRANCHING) == 0
        {
            height += 1;
        }

        trace!("SkipList::random_height -> {}", height);

        assert!(height >= 1);
        assert!(height <= SkipListMaxHeight as i32);
        height
    }

    /// Return true if `key` is greater than the key stored in node `n`.
    ///
    /// A null node is considered to be "infinite".
    pub fn key_is_after_node(&self, key: &K, n: *mut SkipListNode<K>) -> bool {
        if n.is_null() {
            return false;
        }
        unsafe {
            let n_key = (*n).key_ref();
            let cmp = self.compare.compare(n_key, key);
            let result = cmp < 0;
            trace!(
                "SkipList::key_is_after_node: n_key={:?}, key={:?}, cmp={}, result={}",
                n_key,
                key,
                cmp,
                result
            );
            result
        }
    }

    /// Return the earliest node whose key is >= `key`.
    ///
    /// If `prev` is provided, it is filled with the previous node at
    /// each level.
    pub fn find_greater_or_equal(&self, key: &K, mut prev: Option<&mut [*mut SkipListNode<K>]>) -> *mut SkipListNode<K> {
        trace!("SkipList::find_greater_or_equal: key={:?}", key);

        let mut x = self.head;
        let mut level = self.get_max_height() - 1;

        loop {
            let next = unsafe { (*x).next(level) };
            if self.key_is_after_node(key, next) {
                // Keep searching in this level.
                x = next;
            } else {
                if let Some(ref mut prev_slice) = prev {
                    prev_slice[level as usize] = x;
                }
                if level == 0 {
                    trace!(
                        "SkipList::find_greater_or_equal: found node={:p}",
                        next
                    );
                    return next;
                } else {
                    level -= 1;
                }
            }
        }
    }

    /// Return the latest node with key < `key`.
    ///
    /// Returns `head_` if there is no such node.
    pub fn find_less_than(&self, key: &K) -> *mut SkipListNode<K> {
        trace!("SkipList::find_less_than: key={:?}", key);

        let mut x = self.head;
        let mut level = self.get_max_height() - 1;

        loop {
            debug_assert!(
                x == self.head
                    || self.compare.compare(
                        unsafe { (*x).key_ref() },
                        key
                    ) < 0
            );
            let next = unsafe { (*x).next(level) };
            let done = if next.is_null() {
                true
            } else {
                let next_key = unsafe { (*next).key_ref() };
                self.compare.compare(next_key, key) >= 0
            };

            if done {
                if level == 0 {
                    trace!("SkipList::find_less_than -> {:p}", x);
                    return x;
                } else {
                    level -= 1;
                }
            } else {
                x = next;
            }
        }
    }

    /// Return the last node in the list, or `head_` if empty.
    pub fn find_last(&self) -> *mut SkipListNode<K> {
        trace!("SkipList::find_last");

        let mut x = self.head;
        let mut level = self.get_max_height() - 1;

        loop {
            let next = unsafe { (*x).next(level) };
            if next.is_null() {
                if level == 0 {
                    trace!("SkipList::find_last -> {:p}", x);
                    return x;
                } else {
                    level -= 1;
                }
            } else {
                x = next;
            }
        }
    }

    /// Create a new SkipList object that will use "compare" for comparing keys,
    /// and will allocate memory using "*arena".
    ///
    /// Objects allocated in the arena must remain allocated for the lifetime of
    /// the skiplist object.
    pub fn new(compare: C, arena: *mut Arena) -> Self {
        info!(
            "SkipList::new: arena={:p}, max_height={}",
            arena, SkipListMaxHeight
        );
        assert!(
            !arena.is_null(),
            "SkipList::new: arena pointer must not be null"
        );

        let mut list = SkipList {
            compare,
            arena,
            head: ptr::null_mut(),
            max_height: AtomicI32::new(1),
            rnd: Random::new(0xdeadbeef),
        };

        // Create the dummy head node with maximum height.
        let head = list.new_node(K::default(), SkipListMaxHeight as i32);
        list.head = head;

        for level in 0..SkipListMaxHeight as i32 {
            unsafe {
                (*head).no_barrier_set_next(level, ptr::null_mut());
            }
        }

        list
    }

    /// Insert `key` into the skiplist.
    ///
    /// REQUIRES: no existing entry compares equal to `key`.
    pub fn insert(&mut self, key: K) {
        trace!("SkipList::insert: key={:?}", key);

        // Find the insertion position and populate `prev`.
        let mut prev: [*mut SkipListNode<K>; SkipListMaxHeight] =
            [ptr::null_mut(); SkipListMaxHeight];
        let x = self.find_greater_or_equal(&key, Some(&mut prev[..]));

        // Our data structure does not allow duplicate insertion.
        if !x.is_null() {
            let x_key = unsafe { (*x).key_ref() };
            assert!(
                !self.equal(&key, x_key),
                "SkipList::insert: duplicate key inserted: {:?}",
                key
            );
        }

        // Choose a random height for the new node.
        let height = self.random_height();
        let current_max = self.get_max_height();

        if height > current_max {
            for level in current_max..height {
                prev[level as usize] = self.head;
            }

            // See comments in the C++ code for why this relaxed store is safe.
            self.max_height
                .store(height, atomic::Ordering::Relaxed);
        }

        let node = self.new_node(key, height);
        for level in 0..height {
            unsafe {
                let prev_node = prev[level as usize];
                let next = (*prev_node).no_barrier_next(level);
                (*node).no_barrier_set_next(level, next);
                (*prev_node).set_next(level, node);
            }
        }
    }

    /// Return true iff the list contains an entry equal to `key`.
    pub fn contains(&self, key: &K) -> bool {
        trace!("SkipList::contains: key={:?}", key);
        let x = self.find_greater_or_equal(key, None);
        if x.is_null() {
            false
        } else {
            let x_key = unsafe { (*x).key_ref() };
            self.equal(key, x_key)
        }
    }
}


