/**
| Encode a byte span as a base58-encoded
| string
|
*/
pub fn encode_base58(mut input: &[u8]) -> String {
    // Skip & count leading zeroes.
    let mut zeroes: usize = 0;
    let mut length: i32 = 0;

    while input.len() > 0 && input[0] == 0 {
        input = &input[1..];
        zeroes += 1;
    }

    /*
      | Allocate enough space in big-endian
      | base58 representation.
      |
      */

    // log(256) / log(58), rounded up.

    // Allocate enough space in big-endian base58 representation.
    // log(256) / log(58), rounded up.
    let size: usize = input.len() * 138 / 100 + 1;
    // IMPORTANT: we need a length-initialized buffer, not just capacity.
    let mut b58: Vec<u8> = vec![0; size];

    // Process the bytes.
    while input.len() > 0 {

        let mut carry: i32 = input[0].into();

        let mut i: i32 = 0;

        // Apply "b58 = b58 * 256 + ch".

        for it in b58.iter_mut().rev() {

            if carry == 0 && i >= length {
                break;
            }

            carry += 256 * ((*it) as i32);

            *it = (carry % 58).try_into().unwrap();

            carry /= 58;

            i += 1;
        }

        assert!(carry == 0);

        length = i;
        input  = &input[1..];
    }

    // Skip leading zeroes in base58 result.
    let mut idx = (size as i32 - length) as usize;
    while idx < b58.len() && b58[idx] == 0 {
        idx += 1;
    }

    // Translate the result into a string.
    let mut s = String::with_capacity(zeroes + (b58.len() - idx));
    s.push_str(&"1".repeat(zeroes));
    let alphabet = PSZ_BASE58.as_bytes();
    for &val in &b58[idx..] {
        s.push(alphabet[val as usize] as char);
    }

    s
}


/// Encode a byte span into a base58-encoded string, **including checksum** (double SHA‑256).
pub fn encode_base_58check(input: &[u8]) -> String {
    trace!(payload_len = input.len(), "encode_base_58check: starting");
    let mut vch: Vec<u8> = Vec::with_capacity(input.len() + 4);
    vch.extend_from_slice(input);

    // Canonical checksum: first 4 **big‑endian** bytes of SHA256(SHA256(payload))
    let chk = checksum4_sha256d(input);
    vch.extend_from_slice(&chk);

    let out = encode_base58(&vch);
    info!(encoded_len = out.len(), "encode_base_58check: success");
    out
}


/**
| Decode a base58-encoded string (str)
| into a byte vector (vchRet). return
| true if decoding is successful.
|
*/
pub fn decode_base58(input: &str, vch_ret: &mut Vec<u8>, max_ret_len: usize) -> bool {
    use nom::character::is_space;

    let input = input.trim();

    let mut chars = input.chars().peekable();

    // Count leading '1's as zero bytes
    let mut zeroes = 0;
    while matches!(chars.peek(), Some('1')) {
        zeroes += 1;
        chars.next();
        if zeroes > max_ret_len {
            return false;
        }
    }

    let base58_map = MAP_BASE58;

    // Start empty; seeding with 0 would make "" decode to [0] instead of [].
    let mut b256: Vec<u8> = Vec::new();

    while let Some(c) = chars.next() {

        if is_space(c as u8) {
            // Keep this variant strict: any whitespace is invalid.
            return false;
        }

        let carry = {
            let idx = c as usize;
            if idx >= 256 || base58_map[idx] == -1 {
                return false; // invalid base58 char
            }
            base58_map[idx] as u32
        };

        let mut carry = carry;
        for val in b256.iter_mut().rev() {
            let tmp = (*val as u32) * 58 + carry;
            *val = (tmp & 0xFF) as u8;
            carry = tmp >> 8;
        }

        while carry > 0 {
            b256.insert(0, (carry & 0xFF) as u8);
            carry >>= 8;
        }
    }

    let length = b256.len();
    if zeroes + length > max_ret_len {
        return false;
    }

    vch_ret.clear();
    vch_ret.resize(zeroes, 0);
    vch_ret.extend_from_slice(&b256);

    true
}


pub fn decode_base58_raw(mut psz: *const u8, vch: &mut Vec<u8>, max_ret_len: i32) -> bool {
    // Skip leading spaces.
    while *psz != 0 && nom::character::is_space(*psz ){
        psz = psz.add(1);
    }

    // Skip and count leading '1's.
    let mut zeroes: i32 = 0;
    let mut length: i32 = 0;

    while char::from(*psz) == '1' {

        zeroes += 1;

        if zeroes > max_ret_len {
            return false;
        }

        psz = psz.add(1);
    }

    /**
      | Allocate enough space in big-endian
      | base256 representation.
      |
      */

    // log(58) / log(256), rounded up.
    let size: usize = libc::strlen(psz as *const i8) * 733 / 1000 + 1;
    // length-initialized buffer
    let mut b256: Vec<u8> = vec![0; size];

    // Process the characters.
    //
    // guarantee not out of range
    //
    // mapBase58.len() should be 256
    const_assert!(MAP_BASE58.len() == 256); 

    while *psz != 0 && !nom::character::is_space(*psz){

        //  Decode base58 character
        let mut carry: i32 = MAP_BASE58[*psz as usize] as i32;

        if carry == -1 {
            //  Invalid b58 character
            return false;
        }

        let mut i: i32 = 0;

        for val in b256.iter_mut().rev() {

            if carry == 0 && i >= length {
                break;
            }

            carry += (58 * (*val)) as i32;

            *val   = (carry % 256).try_into().unwrap();

            carry /= 256;

            i += 1;
        }

        assert!(carry == 0);

        length = i;

        if length + zeroes > max_ret_len {
            return false
        }

        psz = psz.add(1);
    }

    // Skip trailing spaces.
    while nom::character::is_space(*psz){
        psz = psz.add(1);
    }

    if *psz != 0 {
        return false
    }

    // Skip leading zeroes in b256.
    let mut it = b256.iter();

    let offset: usize = (size - length as usize).try_into().unwrap();

    it.advance_by(offset);

    // Copy result into output vector.
    vch.reserve(zeroes as usize + it.len());

    vch[0..zeroes as usize].fill(0);

    // Skip leading zeroes in b256.
    let mut idx = size - length as usize;
    while idx < b256.len() && b256[idx] == 0 {
        idx += 1;
    }
    // Copy result into output vector.
    vch.clear();
    vch.resize(zeroes as usize, 0);
    vch.extend_from_slice(&b256[idx..]);

    return true
}


/// Return the **first 4 bytes** of `SHA256(SHA256(payload))` in digest order
/// (no endianness flips, no integer reinterpretation).
#[inline]
pub fn checksum4_sha256d(payload: &[u8]) -> [u8; 4] {
    trace!(
        payload_len = payload.len(),
        "Base58Check checksum: computing SHA256d and taking first 4 bytes"
    );
    // Use the bitcoin-hash crate's one-pass double‑SHA256 hasher directly to
    // avoid any u256 endianness concerns.
    let mut hasher = Hash256::default();
    hasher.write(payload);

    let mut digest = [0u8; Hash256::OUTPUT_SIZE];
    hasher.finalize(&mut digest); // digest = SHA256(SHA256(payload))

    let out = [digest[0], digest[1], digest[2], digest[3]];
    debug!(checksum = %hex::encode(out), "Base58Check checksum computed");
    out
}


/// Decode a base58-encoded string (psz) that includes a checksum into a byte
/// vector (vchRet). Return `true` if decoding is successful.
pub fn decode_base_58check_raw(mut psz: *const u8, vch_ret: &mut Vec<u8>, max_ret_len: i32) -> bool {
    let budget = if max_ret_len > i32::MAX - 4 { i32::MAX } else { max_ret_len + 4 };
    debug!(max_ret_len, budget, "decode_base_58check_raw: starting");

    if !decode_base58_raw(psz, vch_ret, budget) {
        warn!("decode_base_58check_raw: underlying Base‑58 decode failed");
        vch_ret.clear();
        return false;
    }
    if vch_ret.len() < 4 {
        warn!(decoded_len = vch_ret.len(), "decode_base_58check_raw: insufficient length for checksum");
        vch_ret.clear();
        return false;
    }

    let payload_len = vch_ret.len() - 4;
    trace!(payload_len, total = vch_ret.len(), "decode_base_58check_raw: verifying checksum");
    let expected = checksum4_sha256d(&vch_ret[..payload_len]);

    if vch_ret[payload_len..] != expected {
        error!(
            found = %hex::encode(&vch_ret[payload_len..]),
            expected = %hex::encode(expected),
            "decode_base_58check_raw: checksum mismatch"
        );
        vch_ret.clear();
        return false;
    }

    vch_ret.truncate(payload_len);
    info!(payload_len, "decode_base_58check_raw: success");
    true
}


/// Decode a base58-encoded string (str) that includes a checksum into a byte
/// vector (vchRet). Return `true` if decoding is successful.
pub fn decode_base_58check(input: &str, vch_ret: &mut Vec<u8>, max_ret_len: usize) -> bool {
    debug!(input_len = input.len(), max_ret_len, "decode_base_58check: starting");
    if max_ret_len > usize::MAX - 4 {
        warn!("decode_base_58check: max_ret_len overflow guard triggered");
        return false;
    }
    if !decode_base58(input, vch_ret, max_ret_len + 4) {
        warn!("decode_base_58check: underlying Base‑58 decode failed");
        vch_ret.clear();
        return false;
    }
    if vch_ret.len() < 4 {
        warn!(decoded_len = vch_ret.len(), "decode_base_58check: insufficient length for checksum");
        vch_ret.clear();
        return false;
    }

    let payload_len = vch_ret.len() - 4;
    trace!(payload_len, total = vch_ret.len(), "decode_base_58check: verifying checksum");
    let expected = checksum4_sha256d(&vch_ret[..payload_len]);

    if vch_ret[payload_len..] != expected {
        error!(
            found = %hex::encode(&vch_ret[payload_len..]),
            expected = %hex::encode(expected),
            "decode_base_58check: checksum mismatch"
        );
        vch_ret.clear();
        return false;
    }

    vch_ret.truncate(payload_len);
    info!(payload_len, "decode_base_58check: success");
    true
}



