/// Native Windows `SOCKET` handle (always `usize`‑sized).
#[cfg(target_os = "windows")]
pub type CSocket = usize;


/// POSIX file‑descriptor representing a socket.
#[cfg(not(target_os = "windows"))]
pub type CSocket = libc::c_int;


pub type SockEvent = u8;

/**
| RAII helper class that manages a socket.
| Mimics `std::unique_ptr`, but instead
| of a pointer it contains a socket and
| closes it automatically when it goes
| out of scope.
|
*/
#[derive(Debug,Getters,MutGetters)]
#[getset(get="pub",get_mut="pub")]
pub struct Sock  {
    socket: CSocket,
}

#[cfg(unix)]
#[inline]
pub fn io_error_is_permanent(err: i32) -> bool {
    use libc::{EAGAIN, EINTR, EWOULDBLOCK, EINPROGRESS};

    // Errors considered *transient* by the original C++ implementation.
    // Anything else is treated as permanent.
    let permanent = !(err == EAGAIN
        || err == EINTR
        || err == EWOULDBLOCK
        || err == EINPROGRESS);

    trace!(
        error_code = err,
        permanent,
        "Checked if I/O error is permanent (unix)"
    );
    permanent
}


#[cfg(windows)]
#[inline]
pub fn io_error_is_permanent(err: i32) -> bool {
    use winapi::um::winsock2::{WSAEAGAIN, WSAEINTR, WSAEWOULDBLOCK, WSAEINPROGRESS};

    let permanent = !(err == WSAEAGAIN
        || err == WSAEINTR
        || err == WSAEWOULDBLOCK
        || err == WSAEINPROGRESS);

    trace!(
        error_code = err,
        permanent,
        "Checked if I/O error is permanent (windows)"
    );
    permanent
}


/// Determine whether the socket `s` can safely be
/// used with `select(2)` on the current platform.
///
/// * On Windows **or** when the build is configured
///   with `USE_POLL`, every socket is considered
///   selectable (mirrors the original C++ logic).
/// * On Unix builds that rely on classic `select(2)`
///   we ensure the descriptor is `< FD_SETSIZE`.
#[inline]
pub fn is_selectable_socket(s: &CSocket) -> bool {
    trace!(
        target: "compat::socket",
        fd = *s as u64,
        "is_selectable_socket"
    );

    #[cfg(any(target_os = "windows", feature = "use_poll"))]
    {
        true
    }

    #[cfg(not(any(target_os = "windows", feature = "use_poll")))]
    {
        (*s as usize) < libc::FD_SETSIZE as usize
    }
}


/**
| Close socket and set `h_socket` to `INVALID_SOCKET`.
|
| Mirrors the original C++ logic while adding rich tracing.
*/
pub fn close_socket(h_socket: &mut CSocket) -> bool {
    if *h_socket == INVALID_SOCKET {
        return false;
    }

    // --- perform the actual close -------------------------------------------------------------
    #[cfg(target_os = "windows")]
    let ret: libc::c_int =
        unsafe { winapi::um::winsock2::closesocket(*h_socket as winapi::um::winsock2::SOCKET) };

    #[cfg(not(target_os = "windows"))]
    let ret: libc::c_int = unsafe { libc::close(*h_socket) };
    // ------------------------------------------------------------------------------------------

    if ret != 0 {
        #[cfg(target_os = "windows")]
        let err_code = unsafe { winapi::um::winsock2::WSAGetLastError() };

        #[cfg(not(target_os = "windows"))]
        let err_code = last_errno();

        warn!(
            socket = *h_socket as i64,
            ret,
            err_code,
            "Socket close failed: {}. Error: {}",
            *h_socket as i64,
            network_error_string(err_code)
        );
    }

    *h_socket = INVALID_SOCKET;

    ret != SOCKET_ERROR
}


