/// Compute the bounded wait interval until `deadline`,
/// clamped to `MAX_WAIT_FOR_IO`.
#[inline]
pub fn compute_bounded_wait(deadline: Instant) -> Duration {
    let now = Instant::now();

    // Remaining time in nanoseconds (std time API)
    let remaining_ns = deadline.0.saturating_duration_since(now.into()).as_nanos();

    // Convert MAX_WAIT_FOR_IO (a `time::Duration`) into nanoseconds
    let max_ns_time: i128 = MAX_WAIT_FOR_IO.whole_nanoseconds();
    let max_ns_u = if max_ns_time > 0 { max_ns_time as u128 } else { 0u128 };

    // Clamp remaining time to MAX_WAIT_FOR_IO
    let wait_ns_u = core::cmp::min(remaining_ns, max_ns_u);

    // Bound to i64 range and return as `time::Duration`
    let wait_ns_i64 = if wait_ns_u > i64::MAX as u128 {
        i64::MAX
    } else {
        wait_ns_u as i64
    };

    Duration::nanoseconds(wait_ns_i64)
}


#[cfg(not(target_os = "windows"))]
pub fn network_error_string(err: i32) -> String {
    const BUF_CAP: usize = 256;
    let mut buf = [0u8; BUF_CAP];

    // Two incompatible strerror_r variants exist; handle both.
    #[cfg(any(
        target_env = "gnu",     // glibc returns `char *`
        target_env = "uclibc"
    ))]
    let c_ptr = unsafe { libc::strerror_r(err, buf.as_mut_ptr() as *mut _, BUF_CAP) };

    #[cfg(not(any(
        target_env = "gnu",
        target_env = "uclibc"
    )))]
    let ret_val = unsafe { libc::strerror_r(err, buf.as_mut_ptr() as *mut _, BUF_CAP) };

    #[cfg(any(
        target_env = "gnu",
        target_env = "uclibc"
    ))]
    let c_str = unsafe { CStr::from_ptr(c_ptr) };

    #[cfg(not(any(
        target_env = "gnu",
        target_env = "uclibc"
    )))]
    let c_str = {
        if ret_val != 0 {
            // Unknown error – empty buffer.
            &CStr::from_bytes_with_nul(b"\0").unwrap()
        } else {
            unsafe { CStr::from_ptr(buf.as_ptr() as *const _) }
        }
    };

    let msg = c_str.to_string_lossy();
    let full = format!("{msg} ({err})");
    debug!(err, %full, "network_error_string (unix)");
    full
}


/// Return a printable description for the given network error code.
///
/// Mirrors the C++ reference exactly: _“`%s (%d)`”_ where the first
/// field is the platform’s error text and the second is the integer
/// error value.
#[cfg(target_os = "windows")]
pub fn network_error_string(err: i32) -> String {
    use std::ptr;
    use winapi::shared::minwindef::DWORD;
    use winapi::um::{
        winbase::{
            FormatMessageW, FORMAT_MESSAGE_FROM_SYSTEM, FORMAT_MESSAGE_IGNORE_INSERTS,
            FORMAT_MESSAGE_MAX_WIDTH_MASK,
        },
        winnt::{LANG_NEUTRAL, SUBLANG_DEFAULT},
    };

    const BUF_CAP: usize = 256;
    let mut buf: [u16; BUF_CAP] = [0; BUF_CAP];

    // SAFETY: `FormatMessageW` writes at most BUF_CAP elements.
    let len: DWORD = unsafe {
        FormatMessageW(
            FORMAT_MESSAGE_FROM_SYSTEM
                | FORMAT_MESSAGE_IGNORE_INSERTS
                | FORMAT_MESSAGE_MAX_WIDTH_MASK,
            ptr::null(),
            err as DWORD,
            (LANG_NEUTRAL as DWORD) << 10 | (SUBLANG_DEFAULT as DWORD),
            buf.as_mut_ptr(),
            BUF_CAP as DWORD,
            ptr::null_mut(),
        )
    };

    let msg = if len != 0 {
        // Trim trailing CR/LF added by Windows.
        let slice = &buf[..len as usize];
        let mut s = String::from_utf16_lossy(slice);
        while s.ends_with(['\r', '\n']) {
            s.pop();
        }
        s
    } else {
        String::from("Unknown error")
    };

    let full = format!("{msg} ({err})");
    trace!(err, %full, "network_error_string (windows)");
    full
}

impl Sock {

    /// Send the entire buffer, retrying on transient errors.
    ///
    /// On failure this panics with a message identical in spirit to the
    /// C++ `std::runtime_error`.
    pub fn send_complete(
        &self,
        data:      &String,
        timeout:   chrono::Duration,
        interrupt: &mut ThreadInterrupt
    )
    {
        let deadline = Instant::now()
            + timeout
                .to_std()
                .unwrap_or_else(|_| std::time::Duration::from_secs(u64::MAX));

        let bytes = data.as_bytes();
        let mut sent = 0;

        loop {
            if sent == bytes.len() {
                debug!(total = sent, "send_complete – finished");
                return;
            }

            let ret = self.send(
                bytes[sent..].as_ptr() as *const c_void,
                bytes.len() - sent,
                msg_nosignal_const(),
            );

            if ret > 0 {
                sent += ret as usize;
                continue;
            }

            // Error handling
            let err = last_socket_error();
            if io_error_is_permanent(err) {
                panic!("send(): {}", network_error_string(err));
            }

            // Timeout?
            if Instant::now() >= deadline {
                panic!(
                    "Send timeout (sent only {} of {} bytes before that)",
                    sent,
                    bytes.len()
                );
            }

            // Interrupt?
            if interrupt.as_bool() {
                panic!(
                    "Send interrupted (sent only {} of {} bytes before that)",
                    sent,
                    bytes.len()
                );
            }

            // Wait a bounded amount before retrying.
            let wait_dur = compute_bounded_wait(deadline);
            let _ = self.wait(wait_dur, SOCK_SEND as u8, core::ptr::null_mut());
        }
    }
}

impl Sock {

    /// Block until the socket becomes ready for the requested events.
    ///
    /// *Uses `poll(2)` on Linux when `USE_POLL` is enabled, otherwise
    /// falls back to a classic `select(2)` implementation that honours
    /// the `is_selectable_socket` constraint.*
    pub fn wait(
        &self,
        timeout:   Duration,
        requested: SockEvent,
        occurred:  *mut SockEvent
    ) -> bool
    {
        #[cfg(all(target_os = "linux", feature = "use_poll"))]
        {
            use libc::{poll, pollfd, POLLIN, POLLOUT};

            let mut fd = pollfd {
                fd: self.socket(),
                events: 0,
                revents: 0,
            };
            if requested & SOCK_RECV as u8 != 0 {
                fd.events |= POLLIN;
            }
            if requested & SOCK_SEND as u8 != 0 {
                fd.events |= POLLOUT;
            }

            let ms = timeout.as_millis() as libc::c_int;
            let ret = unsafe { poll(&mut fd, 1, ms) };

            if ret == SOCKET_ERROR {
                return false;
            }

            unsafe {
                if !occurred.is_null() {
                    *occurred = 0;
                    if fd.revents & POLLIN != 0 {
                        *occurred |= SOCK_RECV as u8;
                    }
                    if fd.revents & POLLOUT != 0 {
                        *occurred |= SOCK_SEND as u8;
                    }
                }
            }

            true
        }

        #[cfg(not(all(target_os = "linux", feature = "use_poll")))]
        {
            use libc::{
                fd_set, select, timeval, FD_ISSET, FD_SET, FD_SETSIZE, FD_ZERO, timeval as TimeVal,
            };

            if !is_selectable_socket(self.socket()) {
                return false;
            }

            unsafe {
                let mut recv_set: fd_set = std::mem::zeroed();
                let mut send_set: fd_set = std::mem::zeroed();
                FD_ZERO(&mut recv_set);
                FD_ZERO(&mut send_set);

                if requested & SOCK_RECV as u8 != 0 {
                    FD_SET(*self.socket(), &mut recv_set);
                }
                if requested & SOCK_SEND as u8 != 0 {
                    FD_SET(*self.socket(), &mut send_set);
                }

                let mut tv = TimeVal {
                    tv_sec: timeout.as_seconds_f32() as libc::time_t,
                    tv_usec: (timeout.subsec_microseconds()) as libc::suseconds_t,
                };

                let ret = select(
                    *self.socket() + 1,
                    &mut recv_set,
                    &mut send_set,
                    std::ptr::null_mut(),
                    &mut tv,
                );

                if ret == SOCKET_ERROR {
                    return false;
                }

                if !occurred.is_null() {
                    *occurred = 0;
                    if FD_ISSET(*self.socket(), &mut recv_set) {
                        *occurred |= SOCK_RECV as u8;
                    }
                    if FD_ISSET(*self.socket(), &mut send_set) {
                        *occurred |= SOCK_SEND as u8;
                    }
                }

                true
            }
        }
    }
}

impl Sock {

    /// Read until `terminator` without consuming past it.
    pub fn recv_until_terminator(
        &self,
        terminator: u8,
        timeout:    chrono::Duration,
        interrupt:  &mut ThreadInterrupt,
        max_data:   usize
    ) -> String
    {
        let deadline = Instant::now()
            + timeout
                .to_std()
                .unwrap_or_else(|_| std::time::Duration::from_secs(u64::MAX));

        let mut data = Vec::<u8>::new();
        let mut terminator_found = false;

        // We must not consume any bytes past the terminator from the socket.
        //
        // One option is to read one byte at a time and check if we have read
        // a terminator.
        //
        // However that is very slow. 
        //
        // Instead, we peek at what is in the socket and only read as many bytes
        // as possible without crossing the terminator.
        //
        // Reading 64 MiB of random data with 262526 terminator chars takes 37
        // seconds to read one byte at a time VS 0.71 seconds with the "peek"
        // solution below. 
        //
        // Reading one byte at a time is about 50 times slower.

        while !terminator_found {
            if data.len() >= max_data {
                panic!(
                    "Received too many bytes without a terminator ({})",
                    data.len()
                );
            }

            let mut buf = [0u8; 512];
            let peek_len = min(buf.len(), max_data - data.len());
            let peek_ret =
                self.recv(buf.as_mut_ptr() as *mut c_void, peek_len, msg_peek_const());

            match peek_ret {
                -1 => {
                    let err = last_socket_error();
                    if io_error_is_permanent(err) {
                        panic!("recv(): {}", network_error_string(err));
                    }
                }
                0 => panic!("Connection unexpectedly closed by peer"),
                n if n > 0 => {
                    let slice = &buf[..n as usize];
                    if let Some(pos) = slice.iter().position(|&b| b == terminator) {
                        // Terminator is within slice -> read up to *and including* it.
                        self.read_exact(slice, pos + 1, &mut data);
                        terminator_found = true;
                    } else {
                        self.read_exact(slice, slice.len(), &mut data);
                    }
                }
                _ => unreachable!(),
            }

            // Timeout or interrupt?
            if Instant::now() >= deadline {
                panic!(
                    "Receive timeout (received {} bytes without terminator before that)",
                    data.len()
                );
            }
            if interrupt.as_bool() {
                panic!(
                    "Receive interrupted (received {} bytes without terminator before that)",
                    data.len()
                );
            }

            // Short bounded wait before retrying.
            let wait_dur = compute_bounded_wait(deadline);
            let _ = self.wait(wait_dur, SOCK_RECV as u8, core::ptr::null_mut());
        }

        // Terminator consumed – omit it in the returned string.
        if terminator_found && data.last() == Some(&terminator) {
            data.pop();
        }
        let s = String::from_utf8(data).expect("socket stream must be valid UTF‑8");
        trace!(len = s.len(), "recv_until_terminator – finished");
        s
    }
}

impl Sock {

    /// Determine whether the socket is still connected.
    ///
    /// Mirrors the original C++ logic by issuing a one‑byte `MSG_PEEK`
    /// read and classifying the outcome.
    pub fn is_connected(&self, errmsg: &mut String) -> bool {
        // Empty holder ⇒ definitely disconnected.
        if *self.socket() == INVALID_SOCKET {
            errmsg.clear();
            errmsg.push_str("not connected");
            return false;
        }

        let mut byte: u8 = 0;
        let ret = self.recv(
            &mut byte as *mut _ as *mut c_void,
            1,
            msg_peek_const(),
        );

        match ret {
            -1 => {
                let err = last_socket_error();
                if io_error_is_permanent(err) {
                    *errmsg = network_error_string(err);
                    return false;
                }
                true // transient ⇒ assume still connected
            }
            0 => {
                errmsg.clear();
                errmsg.push_str("closed");
                false
            }
            _ => true, // any positive value ⇒ data ready ⇒ connected
        }
    }
}

impl Drop for Sock {

    /// Destructor, close the socket or do nothing if empty.
    fn drop(&mut self) {
        trace!("Sock::drop");
        self.reset();
    }
}

impl Default for Sock {

    /// Create an empty object that does nothing when destroyed.
    ///
    /// Default constructor, creates an empty object that does nothing when destroyed.
    fn default() -> Self {
        trace!("Sock::default – constructing empty Sock");
        Self {
            socket: INVALID_SOCKET,
        }
    }
}

impl From<CSocket> for Sock {

    fn from(s: CSocket) -> Self {
        trace!(socket = s, "Sock::from – taking ownership");
        Self { socket: s }
    }
}

impl Sock {

    /**
    | Move‑assignment analogue: grab the socket from `other`
    | and close ours (if set), **exactly** mirroring C++.
    */
    pub fn assign_from(&mut self, mut other: Sock) -> &mut Self {
        use std::mem::{forget, ManuallyDrop};

        self.reset();

        // Preserve C++ semantics: steal the descriptor and leave
        // `other` inert (so that its Drop closes nothing).
        let mut other = ManuallyDrop::new(other);
        self.socket = other.socket;
        other.socket = INVALID_SOCKET;

        trace!(
            new_socket = self.socket,
            "Sock::assign_from – took ownership from peer"
        );
        self
    }

    pub fn get(&self) -> CSocket {
        trace!(socket = self.socket, "Sock::get");
        self.socket
    }

    /// relinquish ownership to caller.
    pub fn release(&mut self) -> CSocket {
        let s = self.socket;
        self.socket = INVALID_SOCKET;
        trace!(released = s, "Sock::release");
        s
    }

    /// Close if non‑empty.
    pub fn reset(&mut self) {
        if self.socket != INVALID_SOCKET {
            debug!(socket = self.socket, "Sock::reset – closing");
            close_socket(&mut self.socket); // **fixed: pass `&mut`**
            // `close_socket` already sets the descriptor to `INVALID_SOCKET`.
        } else {
            trace!("Sock::reset – already empty");
        }
    }

    /// `send(2)`/`send()` thin wrapper.
    pub fn send(
        &self,
        data:  *const c_void,
        len:   usize,
        flags: i32
    ) -> isize
    {
        #[cfg(target_os = "windows")]
        let ret = unsafe {
            winapi::um::winsock2::send(
                self.socket as winapi::um::winsock2::SOCKET,
                data as *const i8,
                len as i32,
                flags,
            )
        };

        #[cfg(not(target_os = "windows"))]
        let ret = unsafe { libc::send(self.socket, data, len, flags) };

        trace!(ret, bytes = len, "Sock::send");
        ret as isize
    }

    /// `recv(2)`/`recv()` thin wrapper.
    pub fn recv(
        &self,
        buf:   *mut c_void,
        len:   usize,
        flags: i32
    ) -> isize
    {
        #[cfg(target_os = "windows")]
        let ret = unsafe {
            winapi::um::winsock2::recv(
                self.socket as winapi::um::winsock2::SOCKET,
                buf as *mut i8,
                len as i32,
                flags,
            )
        };

        #[cfg(not(target_os = "windows"))]
        let ret = unsafe { libc::recv(self.socket, buf, len, flags) };

        trace!(ret, bytes = len, "Sock::recv");
        ret as isize
    }

    /// `connect(2)`/`connect()` thin wrapper.
    pub fn connect(&self, addr: *const SocketAddr, addr_len: libc::socklen_t) -> i32 {
        #[cfg(target_os = "windows")]
        let ret = unsafe {
            winapi::um::winsock2::connect(
                self.socket as winapi::um::winsock2::SOCKET,
                addr as *const _,
                addr_len,
            )
        };

        #[cfg(not(target_os = "windows"))]
        let ret = unsafe { libc::connect(self.socket, addr as *const _, addr_len) };

        trace!(ret, "Sock::connect");
        ret
    }

    /// `getsockopt(2)`/`getsockopt()` thin wrapper.
    pub fn get_sock_opt(
        &self,
        level:    i32,
        opt_name: i32,
        opt_val:  *mut c_void,
        opt_len:  *mut libc::socklen_t
    ) -> i32
    {
        #[cfg(target_os = "windows")]
        let ret = unsafe {
            winapi::um::winsock2::getsockopt(
                self.socket as winapi::um::winsock2::SOCKET,
                level,
                opt_name,
                opt_val as *mut i8,
                opt_len,
            )
        };

        #[cfg(not(target_os = "windows"))]
        let ret =
            unsafe { libc::getsockopt(self.socket, level, opt_name, opt_val, opt_len) };

        trace!(ret, opt = opt_name, "Sock::get_sock_opt");
        ret
    }
}


