#[derive(Getters)]
#[getset(get="pub")]
pub struct PosixLogger  {
    fp: *const libc::FILE,
}

impl PosixLogger {

    pub fn build_log_body_from_format_and_arguments(&self, format: *const u8, arguments: &[&str]) -> Option<String> {
        if format.is_null() {
            error!(
                "PosixLogger::build_log_body_from_format_and_arguments: null format pointer"
            );
            return None;
        }

        unsafe {

            let c_format = std::ffi::CStr::from_ptr(format as *const libc::c_char);
            let template = c_format.to_string_lossy();

            trace!(
                "PosixLogger::build_log_body_from_format_and_arguments: template='{}'",
                template
            );

            let mut body = String::new();
            let mut arg_index: usize = 0;
            let chars: Vec<char> = template.chars().collect();
            let mut i: usize = 0;

            while i < chars.len() {
                if chars[i] == '%' && i + 1 < chars.len() {
                    let next = chars[i + 1];
                    if next == 's' && arg_index < arguments.len() {
                        body.push_str(arguments[arg_index]);
                        arg_index += 1;
                        i += 2;
                        continue;
                    } else if next == '%' {
                        body.push('%');
                        i += 2;
                        continue;
                    }
                }
                body.push(chars[i]);
                i += 1;
            }

            trace!(
                "PosixLogger::build_log_body_from_format_and_arguments: body_len={} args_consumed={}",
                body.len(),
                arg_index
            );

            Some(body)
        }
    }
}

impl PosixLogger {

    pub fn build_thread_identifier_label(&self) -> String {
        // Record the thread ID.
        let mut label = format!("{:?}", std::thread::current().id());

        if label.len() > Self::MAX_THREAD_ID_SIZE {
            label.truncate(Self::MAX_THREAD_ID_SIZE);
        }

        trace!(
            "PosixLogger::build_thread_identifier_label: label_len={}",
            label.len()
        );

        label
    }
}

impl PosixLogger {

    pub fn layout_log_line_in_buffer(
        &self,
        header_bytes:             &[u8],
        body_bytes:               &[u8],
        buffer_ptr:               *mut u8,
        buffer_size:              usize,
        required_without_newline: usize,
        is_first_iteration:       bool
    ) -> Result<usize, usize>
    {
        if buffer_size == 0 {
            error!(
                "PosixLogger::layout_log_line_in_buffer: buffer_size is zero"
            );
            return Ok(0);
        }

        if header_bytes.len() >= buffer_size {
            error!(
                "PosixLogger::layout_log_line_in_buffer: header size {} exceeds buffer size {}",
                header_bytes.len(),
                buffer_size
            );
            return Ok(0);
        }

        if required_without_newline >= buffer_size.saturating_sub(1) {
            if is_first_iteration {
                let dynamic_size = required_without_newline.saturating_add(2);
                trace!(
                    "PosixLogger::layout_log_line_in_buffer: stack buffer too small; dynamic_size={}",
                    dynamic_size
                );
                return Err(dynamic_size);
            }

            debug_assert!(
                false,
                "PosixLogger::layout_log_line_in_buffer: dynamic buffer incorrectly sized"
            );

            let offset = self.copy_truncated_log_line_into_buffer(
                header_bytes,
                body_bytes,
                buffer_ptr,
                buffer_size,
            );
            Ok(offset)
        } else {
            let offset = self.copy_full_log_line_into_buffer(
                header_bytes,
                body_bytes,
                buffer_ptr,
                buffer_size,
            );
            Ok(offset)
        }
    }
}

impl PosixLogger {

    pub fn compute_required_log_bytes(&self, header_bytes: &[u8], body_bytes: &[u8]) -> usize {
        let total = header_bytes
            .len()
            .saturating_add(body_bytes.len());

        trace!(
            "PosixLogger::compute_required_log_bytes: header_len={} body_len={} total={}",
            header_bytes.len(),
            body_bytes.len(),
            total
        );

        total
    }
}

impl PosixLogger {

    /// Helps print the header into the buffer.
    pub fn construct_log_header_prefix(
        &self,
        now_timeval:    &libc::timeval,
        now_components: &libc::tm,
        thread_id:      &str
    ) -> String
    {
        let header = format!(
            "{:04}/{:02}/{:02}-{:02}:{:02}:{:02}.{:06} {} ",
            now_components.tm_year + 1900,
            now_components.tm_mon + 1,
            now_components.tm_mday,
            now_components.tm_hour,
            now_components.tm_min,
            now_components.tm_sec,
            now_timeval.tv_usec as i32,
            thread_id
        );

        trace!(
            "PosixLogger::construct_log_header_prefix: header_len={}",
            header.len()
        );

        header
    }
}

impl PosixLogger {

    pub fn copy_full_log_line_into_buffer(
        &self,
        header_bytes: &[u8],
        body_bytes:   &[u8],
        buffer_ptr:   *mut u8,
        buffer_size:  usize
    ) -> usize
    {
        unsafe {
            let mut offset: usize = 0;

            if !header_bytes.is_empty() {
                std::ptr::copy_nonoverlapping(
                    header_bytes.as_ptr(),
                    buffer_ptr,
                    header_bytes.len(),
                );
                offset = header_bytes.len();
            }

            if !body_bytes.is_empty() {
                let dest = buffer_ptr.add(offset);
                std::ptr::copy_nonoverlapping(
                    body_bytes.as_ptr(),
                    dest,
                    body_bytes.len(),
                );
                offset = offset.saturating_add(body_bytes.len());
            }

            let final_offset = self.ensure_trailing_newline_for_buffer(
                buffer_ptr,
                buffer_size,
                offset,
            );

            debug!(
                "PosixLogger::copy_full_log_line_into_buffer: final_offset={}",
                final_offset
            );

            final_offset
        }
    }
}

impl PosixLogger {

    pub fn ensure_trailing_newline_for_buffer(
        &self,
        buffer_ptr:         *mut u8,
        buffer_size:        usize,
        mut current_offset: usize
    ) -> usize
    {
        unsafe {
            if buffer_size == 0 {
                warn!(
                    "PosixLogger::ensure_trailing_newline_for_buffer: buffer_size is zero"
                );
                return 0;
            }

            if current_offset == 0 {
                *buffer_ptr = b'\n';
                trace!(
                    "PosixLogger::ensure_trailing_newline_for_buffer: inserted newline at position 0"
                );
                return 1;
            }

            if current_offset > buffer_size {
                warn!(
                    "PosixLogger::ensure_trailing_newline_for_buffer: current_offset {} > buffer_size {}; clamping",
                    current_offset,
                    buffer_size
                );
                current_offset = buffer_size;
            }

            let last_index = current_offset.saturating_sub(1);
            if last_index < buffer_size {
                let last_byte = *buffer_ptr.add(last_index);
                if last_byte != b'\n' {
                    if current_offset < buffer_size {
                        *buffer_ptr.add(current_offset) = b'\n';
                        current_offset += 1;
                        trace!(
                            "PosixLogger::ensure_trailing_newline_for_buffer: appended newline at position {}",
                            current_offset - 1
                        );
                    } else {
                        warn!(
                            "PosixLogger::ensure_trailing_newline_for_buffer: no room for newline (current_offset={}, buffer_size={})",
                            current_offset,
                            buffer_size
                        );
                    }
                } else {
                    trace!(
                        "PosixLogger::ensure_trailing_newline_for_buffer: buffer already ends with newline"
                    );
                }
            }

            current_offset
        }
    }
}

impl PosixLogger {

    pub fn flush_buffer_to_log_file(
        &self,
        buffer_ptr:    *mut u8,
        buffer_size:   usize,
        buffer_offset: usize
    )
    {
        if self.fp().is_null() {
            error!(
                "PosixLogger::flush_buffer_to_log_file: fp is null; dropping log bytes"
            );
            return;
        }

        if buffer_ptr.is_null() {
            error!(
                "PosixLogger::flush_buffer_to_log_file: buffer_ptr is null"
            );
            return;
        }

        if buffer_offset == 0 {
            trace!(
                "PosixLogger::flush_buffer_to_log_file: nothing to write (buffer_offset=0)"
            );
            return;
        }

        if buffer_offset > buffer_size {
            warn!(
                "PosixLogger::flush_buffer_to_log_file: buffer_offset {} > buffer_size {}; clamping",
                buffer_offset,
                buffer_size
            );
        }

        unsafe {
            let write_len = std::cmp::min(buffer_offset, buffer_size);
            let written = libc::fwrite(
                buffer_ptr as *const libc::c_void,
                1,
                write_len,
                *self.fp() as *mut libc::FILE,
            );

            if written != write_len {
                warn!(
                    "PosixLogger::flush_buffer_to_log_file: fwrite wrote {} of {} bytes",
                    written,
                    write_len
                );
            } else {
                trace!(
                    "PosixLogger::flush_buffer_to_log_file: wrote {} bytes",
                    write_len
                );
            }

            libc::fflush(*self.fp() as *mut libc::FILE);
        }
    }
}

impl PosixLogger {

    /// We first attempt to print into a stack-allocated buffer. 
    ///
    /// If this attempt fails, we make a second attempt with a dynamically
    /// allocated buffer.
    pub fn emit_log_line_with_two_phase_buffering(&self, header_bytes: &[u8], body_bytes: &[u8]) {
        debug!(
            "PosixLogger::emit_log_line_with_two_phase_buffering: header_len={} body_len={}",
            header_bytes.len(),
            body_bytes.len()
        );

        debug_assert!(
            header_bytes.len()
                <= Self::MAX_HEADER_PREFIX + Self::MAX_THREAD_ID_SIZE,
            "PosixLogger::emit_log_line_with_two_phase_buffering: header larger than expected bound"
        );
        debug_assert!(
            Self::MAX_HEADER_PREFIX + Self::MAX_THREAD_ID_SIZE
                < Self::STACK_BUFFER_SIZE,
            "PosixLogger::emit_log_line_with_two_phase_buffering: stack buffer may not fit header"
        );

        let required_without_newline =
            self.compute_required_log_bytes(header_bytes, body_bytes);

        // Computed in the first iteration.
        let mut dynamic_buffer_size: usize = 0;

        for iteration in 0..2 {
            let buffer_size: usize = if iteration == 0 {
                Self::STACK_BUFFER_SIZE
            } else {
                dynamic_buffer_size
            };

            if buffer_size == 0 {
                error!(
                    "PosixLogger::emit_log_line_with_two_phase_buffering: non-positive buffer_size in iteration {}",
                    iteration
                );
                return;
            }

            let mut stack_buffer = [0u8; Self::STACK_BUFFER_SIZE];
            let mut heap_buffer: Option<Vec<u8>> = None;

            let buffer_ptr: *mut u8 = if iteration == 0 {
                stack_buffer.as_mut_ptr()
            } else {
                let mut v = vec![0u8; buffer_size];
                let ptr = v.as_mut_ptr();
                heap_buffer = Some(v);
                ptr
            };

            let layout_result = self.layout_log_line_in_buffer(
                header_bytes,
                body_bytes,
                buffer_ptr,
                buffer_size,
                required_without_newline,
                iteration == 0,
            );

            match layout_result {
                Ok(offset) => {
                    self.flush_buffer_to_log_file(buffer_ptr, buffer_size, offset);
                    if let Some(buf) = heap_buffer {
                        drop(buf);
                    }
                    break;
                }
                Err(new_size) => {
                    dynamic_buffer_size = new_size;
                    trace!(
                        "PosixLogger::emit_log_line_with_two_phase_buffering: retrying with dynamic buffer size {}",
                        dynamic_buffer_size
                    );
                    if let Some(buf) = heap_buffer {
                        drop(buf);
                    }
                    continue;
                }
            }
        }
    }
}

impl PosixLogger {

    pub fn copy_truncated_log_line_into_buffer(
        &self,
        header_bytes: &[u8],
        body_bytes:   &[u8],
        buffer_ptr:   *mut u8,
        buffer_size:  usize
    ) -> usize
    {
        unsafe {
            let mut remaining = buffer_size.saturating_sub(1);
            let mut offset: usize = 0;

            if remaining > 0 && !header_bytes.is_empty() {
                let header_copy_len = header_bytes.len().min(remaining);
                std::ptr::copy_nonoverlapping(
                    header_bytes.as_ptr(),
                    buffer_ptr,
                    header_copy_len,
                );
                offset = header_copy_len;
                remaining = remaining.saturating_sub(header_copy_len);
            }

            if remaining > 0 && !body_bytes.is_empty() {
                let dest = buffer_ptr.add(offset);
                let body_copy_len = body_bytes.len().min(remaining);
                std::ptr::copy_nonoverlapping(
                    body_bytes.as_ptr(),
                    dest,
                    body_copy_len,
                );
                offset = offset.saturating_add(body_copy_len);
                remaining = remaining.saturating_sub(body_copy_len);
            }

            let final_offset = self.ensure_trailing_newline_for_buffer(
                buffer_ptr,
                buffer_size,
                offset,
            );

            debug!(
                "PosixLogger::copy_truncated_log_line_into_buffer: truncated_final_offset={}",
                final_offset
            );

            final_offset
        }
    }
}

