impl PosixLogger {

    /// Creates a logger that writes to the given
    /// file.
    /// 
    /// The PosixLogger instance takes ownership of
    /// the file handle.
    ///
    pub fn new(fp: *mut libc::FILE) -> Self {
        assert!(!fp.is_null(), "PosixLogger::new: fp must not be null");
        info!("PosixLogger::new: creating logger");
        PosixLogger { fp }
    }
}

impl Drop for PosixLogger {

    fn drop(&mut self) {
        trace!("PosixLogger::drop: closing log file");
        unsafe {
            if !self.fp.is_null() {
                libc::fclose(self.fp as *mut libc::FILE);
            }
        }
    }
}

impl PosixLogger {

    pub fn capture_current_time_components(&self) -> (libc::timeval, libc::tm) {
        unsafe {
            // Record the time as close to the Logv() call as possible.
            let mut now_timeval: libc::timeval = std::mem::zeroed();
            if libc::gettimeofday(
                &mut now_timeval as *mut libc::timeval,
                std::ptr::null_mut(),
            ) != 0
            {
                error!(
                    "PosixLogger::capture_current_time_components: gettimeofday failed"
                );
            }

            let mut now_seconds: libc::time_t = now_timeval.tv_sec;
            let mut now_components: libc::tm = std::mem::zeroed();
            if libc::localtime_r(
                &mut now_seconds as *mut libc::time_t,
                &mut now_components as *mut libc::tm,
            )
            .is_null()
            {
                error!(
                    "PosixLogger::capture_current_time_components: localtime_r failed"
                );
            }

            trace!(
                "PosixLogger::capture_current_time_components: sec={} usec={}",
                now_timeval.tv_sec,
                now_timeval.tv_usec
            );

            (now_timeval, now_components)
        }
    }
}

impl Logv for PosixLogger {

    fn logv(&mut self, format: *const u8, arguments: &[&str]) {
        trace!(
            "PosixLogger::logv: enter format_ptr={:?} arg_count={}",
            format,
            arguments.len()
        );

        if self.fp().is_null() {
            error!("PosixLogger::logv: fp is null; dropping log message");
            return;
        }

        if format.is_null() {
            error!(
                "PosixLogger::logv: format pointer is null; dropping log message"
            );
            return;
        }

        let (now_timeval, now_components) = self.capture_current_time_components();
        let thread_id = self.build_thread_identifier_label();

        let body =
            match self.build_log_body_from_format_and_arguments(format, arguments) {
                Some(body) => body,
                None => {
                    warn!(
                        "PosixLogger::logv: failed to build log body from format; skipping log line"
                    );
                    return;
                }
            };

        let header = self.construct_log_header_prefix(
            &now_timeval,
            &now_components,
            &thread_id,
        );

        debug!(
            "PosixLogger::logv: header_len={} body_len={}",
            header.len(),
            body.len()
        );

        self.emit_log_line_with_two_phase_buffering(
            header.as_bytes(),
            body.as_bytes(),
        );

        trace!("PosixLogger::logv: exit");
    }
}


