We have a rust crate named 'bitcoin-vectorstream' 0.1.19. 

Here is the interface:
/**
| Minimal stream for reading from an existing
| vector by reference
|
*/
#[derive(Debug, Getters, Builder)]
pub struct VectorReader  {
    ty:      i32,
    version: i32,
    data:    Arc<Vec<u8>>,
    pos:     usize,
}


/**
| Minimal stream for overwriting and/or
| appending to an existing byte vector.
|
| The referenced vector will grow as necessary.
*/
#[derive(Debug, Getters, Builder)]
pub struct VectorWriter  {
    n_type:    i32,
    n_version: i32,
    vch_data:  Rc<RefCell<Vec<u8>>>,
    n_pos:     usize,
}

impl Default for VectorReader {

    fn default() -> Self {
        // A trivially empty reader is sufficient to satisfy the `Default`
        // bound required by `BitStreamReader`.
        Self::new(0, 0, std::sync::Arc::new(Vec::new()), 0)
    }
}

impl<T> Shr<&mut T> for VectorReader where
    T: bitcoin_serialize::BtcUnserialize<VectorReader> + ?Sized {

    type Output = VectorReader;

    #[inline]
    fn shr(mut self, rhs: &mut T) -> Self::Output {
        trace!("VectorReader >> {:?}", std::any::type_name::<T>());
        rhs.unserialize(&mut self); // trait call
        self
    }
}

impl VectorReader {

    /// @param[in] type
    /// 
    /// Serialization Type
    /// ----------
    /// @param[in] version
    /// Serialization Version (including
    /// any flags)
    /// @param[in] data
    /// Referenced byte vector to overwrite/append
    /// @param[in] pos
    /// Starting position. Vector index where
    /// reads should start.
    /// Create a new `VectorReader`.
    ///
    /// # Panics
    /// Panics when `pos` is greater than `data.len()`.
    pub fn new(
        ty:      i32,
        version: i32,
        data:    Arc<Vec<u8>>,
        pos:     usize
    ) -> Self
    {
        trace!(ty, version, pos, len = data.len(), "VectorReader::new");

        if pos > data.len() {
            error!(
                pos,
                len = data.len(),
                "VectorReader(...): end of data (pos > data.len())"
            );
            panic!("VectorReader(...): end of data (pos > data.len())");
        }

        Self { ty, version, data, pos }
    }

    /// Create a new `VectorReader` and immediately deserialize a sequence of
    /// objects supplied in `args`.
    pub fn new_with_args<Args>(
        ty:       i32,
        version:  i32,
        data:     Arc<Vec<u8>>,
        pos:      usize,
        mut args: Args
    ) -> Self where
            Args: bitcoin_serialize::UnserializeMany<VectorReader>
    {
        let mut reader = Self::new(ty, version, data, pos);
        args.unserialize_many(&mut reader);
        reader
    }

    /// Remaining unread bytes.
    pub fn size(&self) -> usize {
        self.data.len() - self.pos
    }

    /// `true` when all data have been consumed.
    pub fn empty(&self) -> bool {
        self.pos == self.data.len()
    }
}

impl Read for VectorReader {

    #[inline]
    fn read(&mut self, buf: &mut [u8]) -> std::io::Result<usize> {
        if buf.is_empty() {
            return Ok(0);
        }
        let available = self.size();
        if available == 0 {
            return Ok(0);
        }
        let to_copy = std::cmp::min(buf.len(), available);
        // SAFETY: bounds checked above; regions do not overlap
        unsafe {
            std::ptr::copy_nonoverlapping(
                self.data.as_ptr().add(self.pos),
                buf.as_mut_ptr(),
                to_copy,
            );
        }
        self.pos += to_copy;
        Ok(to_copy)
    }
}

impl Default for VectorWriter {

    fn default() -> Self {
        // Empty buffer held inside an `Rc<RefCell<…>>`, matching the
        // interior‑mutability pattern used by `BitStreamWriter`.
        Self::new(0, 0, std::rc::Rc::new(std::cell::RefCell::new(Vec::new())), 0)
    }
}

impl<T> Shl<&T> for VectorWriter where
    T: bitcoin_serialize::BtcSerialize<VectorWriter> + ?Sized {

    type Output = VectorWriter;

    #[inline]
    fn shl(mut self, rhs: &T) -> Self::Output {
        trace!("VectorWriter << {:?}", std::any::type_name::<T>());
        rhs.serialize(&mut self); // trait call
        self
    }
}

impl Write for VectorWriter {

    #[inline]
    fn write(&mut self, buf: &[u8]) -> std::io::Result<usize> {
        // call inherent pointer‑based writer
        VectorWriter::write(self, buf.as_ptr(), buf.len());
        Ok(buf.len())
    }

    #[inline]
    fn flush(&mut self) -> std::io::Result<()> {
        Ok(()) 
    }
}

impl VectorWriter {

    /// Create a new `VectorWriter`.
    ///
    /// @param[in] nTypeIn
    /// 
    /// Serialization Type
    /// ----------
    /// @param[in] nVersionIn
    /// Serialization Version (including
    /// any flags)
    /// @param[in] vchDataIn
    /// Referenced byte vector to overwrite/append
    /// @param[in] nPosIn
    /// Starting position. Vector index where
    /// writes should start. The vector will
    /// initially grow as necessary to max(nPosIn,
    /// vec.size()). So to append, use vec.size().
    pub fn new(
        n_type:    i32,
        n_version: i32,
        vch_data:  Rc<RefCell<Vec<u8>>>,
        n_pos:     usize
    ) -> Self
    {
        trace!(n_type, n_version, n_pos, "VectorWriter::new");

        {
            let mut vec_ref = vch_data.borrow_mut();
            if n_pos > vec_ref.len() {
                debug!(
                    "resizing buffer from {} to {} bytes to satisfy n_pos",
                    vec_ref.len(),
                    n_pos
                );
                vec_ref.resize(n_pos, 0);
            }
        }

        Self { n_type, n_version, vch_data, n_pos }
    }

    /// Create a new `VectorWriter` and immediately serialize a sequence of
    /// objects supplied in `args`.
    pub fn new_with_args<Args>(
        n_type:    i32,
        n_version: i32,
        vch_data:  Rc<RefCell<Vec<u8>>>,
        n_pos:     usize,
        args:      Args
    ) -> Self where
            Args: bitcoin_serialize::SerializeMany<VectorWriter>
    {
        let mut writer = Self::new(n_type, n_version, vch_data, n_pos);
        args.serialize_many(&mut writer);
        writer
    }

    /// Write `n_size` bytes from `pch` into the stream.
    pub fn write(&mut self, pch: *const u8, n_size: usize) {
        trace!(bytes = n_size, pos_before = self.n_pos, "VectorWriter::write");

        let mut vec_ref = self.vch_data.borrow_mut();
        assert!(
            self.n_pos <= vec_ref.len(),
            "n_pos ({}) exceeds vec.len() ({})",
            self.n_pos,
            vec_ref.len()
        );

        let overwrite = min(n_size, vec_ref.len() - self.n_pos);

        // SAFETY: regions checked for bounds; they do not overlap.
        unsafe {
            if overwrite != 0 {
                std::ptr::copy_nonoverlapping(
                    pch,
                    vec_ref.as_mut_ptr().add(self.n_pos),
                    overwrite,
                );
            }

            if overwrite < n_size {
                let extra = n_size - overwrite;
                let start_ptr = pch.add(overwrite);
                vec_ref.reserve(extra);
                let len_before = vec_ref.len();
                vec_ref.set_len(len_before + extra);
                std::ptr::copy_nonoverlapping(
                    start_ptr,
                    vec_ref.as_mut_ptr().add(len_before),
                    extra,
                );
            }
        }

        self.n_pos += n_size;
        trace!(pos_after = self.n_pos, "VectorWriter::write finished");
    }
}


