}


/// Append the bytes referenced by `source` into `destination`,
/// using the underlying raw pointer from `Slice`.
pub fn append_slice_bytes(source: &Slice, destination: &mut Vec<u8>) {
    let len = *source.size();

    if len == 0 {
        trace!(
            "append_slice_bytes: source slice length is zero; nothing to append"
        );
        return;
    }

    let data_ptr = *source.data();
    if data_ptr.is_null() {
        warn!(
            "append_slice_bytes: source slice has null data pointer (len={})",
            len
        );
        return;
    }

    unsafe {
        let bytes = std::slice::from_raw_parts(data_ptr, len);
        destination.extend_from_slice(bytes);
    }

    trace!(
        appended = len,
        total    = destination.len(),
        "append_slice_bytes: appended slice bytes to destination Vec"
    );
}


pub fn init_type_crc(type_crc: *mut u32) {
    trace!("init_type_crc: initializing type_crc table");

    if type_crc.is_null() {
        error!("init_type_crc: null type_crc pointer");
        return;
    }

    let max_type = LOG_MAX_RECORD_TYPE as usize;

    for i in 0..=max_type {
        let t: u8 = i as u8;
        let crc = unsafe { crc32c_value(&t as *const u8, 1) };
        unsafe {
            *type_crc.add(i) = crc;
        }
        trace!(
            "init_type_crc: precomputed crc for record_type={} crc={:#010x}",
            i,
            crc
        );
    }
}



