/**
| Enumeration of reason for returned
| fee estimate
|
*/
pub enum FeeReason {
    NONE,
    HALF_ESTIMATE,
    FULL_ESTIMATE,
    DOUBLE_ESTIMATE,
    CONSERVATIVE,
    MEMPOOL_MIN,
    PAYTXFEE,
    FALLBACK,
    REQUIRED,
}


/**
| Identifier for each of the 3 different
| TxConfirmStats which will track history
| over different time horizons.
|
*/
#[derive(Clone, Copy, Debug, PartialEq, Eq)]
pub enum FeeEstimateHorizon {
    SHORT_HALFLIFE,
    MED_HALFLIFE,
    LONG_HALFLIFE,
}


/**
| Used to determine type of fee estimation
| requested
|
*/
#[derive(Debug,Copy,Clone,PartialEq,Eq)]
pub enum FeeEstimateMode {
    UNSET,
    ECONOMICAL,
    CONSERVATIVE,
    BTC_KVB,
    SAT_VB,
}

pub struct FeeFilterRounder  {
    feeset:        BTreeSet<FloatOrd<f64>>,
    insecure_rand: FastRandomContext,
}


/// Fee rate in satoshis per kilobyte (¹/₁₀₀₀ of a megabyte).
///
/// Internally this is stored exactly as integer satoshis per **1000 bytes**
/// ( **k** = 1000, matching the historical behaviour in Bitcoin Core).
#[derive(Serialize,Deserialize,Getters, Debug, Clone, Copy, PartialEq, Eq)]
#[getset(get = "pub")]
pub struct FeeRate  {
    n_satoshis_perk: Amount,
}


pub struct FeeCalculation  {
    est:             FeeRateEstimationResult,
    reason:          FeeReason,
    desired_target:  i32,
    returned_target: i32,
}

pub fn string_for_fee_reason(reason: FeeReason) -> String {
    match reason {
        FeeReason::NONE           => "None",
        FeeReason::HALF_ESTIMATE  => "Half Target 60% Threshold",
        FeeReason::FULL_ESTIMATE  => "Target 85% Threshold",
        FeeReason::DOUBLE_ESTIMATE=> "Double Target 95% Threshold",
        FeeReason::CONSERVATIVE   => "Conservative Double Target longer horizon",
        FeeReason::MEMPOOL_MIN    => "Mempool Min Fee",
        FeeReason::PAYTXFEE       => "PayTxFee set",
        FeeReason::FALLBACK       => "Fallback fee",
        FeeReason::REQUIRED       => "Minimum Required Fee",
    }.to_string()
}


pub fn string_for_fee_estimate_horizon(horizon: FeeEstimateHorizon) -> String {
    // switch (…) { … } + assert(false) in Core
    match horizon {
        FeeEstimateHorizon::SHORT_HALFLIFE => "short".to_string(),
        FeeEstimateHorizon::MED_HALFLIFE   => "medium".to_string(),
        FeeEstimateHorizon::LONG_HALFLIFE  => "long".to_string(),
    }
}


pub fn fee_mode_map() -> &'static Vec<(String, FeeEstimateMode)> {
    use once_cell::sync::Lazy;
    static MODES: Lazy<Vec<(String, FeeEstimateMode)>> = Lazy::new(|| {
        vec![
            ("unset".to_string(),       FeeEstimateMode::UNSET),
            ("economical".to_string(),  FeeEstimateMode::ECONOMICAL),
            ("conservative".to_string(),FeeEstimateMode::CONSERVATIVE),
        ]
    });
    &MODES
}


pub fn fee_modes(delimiter: &str) -> String {
    fee_mode_map()
        .iter()
        .map(|(s, _)| s.as_str())
        .collect::<Vec<_>>()
        .join(delimiter)
}


pub fn invalid_estimate_mode_error_message() -> String {
    // "Invalid estimate_mode parameter, must be one of: \"" + FeeModes("\", \"") + "\""
    format!(
        "Invalid estimate_mode parameter, must be one of: \"{}\"",
        fee_modes(&"\", \"".to_string())
    )
}


pub fn fee_mode_from_string(mode_string: &str, fee_estimate_mode: &mut FeeEstimateMode) -> bool {
    // auto searchkey = ToUpper(mode_string); match against FeeModeMap
    let key = mode_string.to_ascii_lowercase();
    for (name, mode) in fee_mode_map().iter() {
        if name.eq_ignore_ascii_case(&key) {
            *fee_estimate_mode = *mode;
            return true;
        }
    }
    false
}

impl FeeFilterRounder {

    /// Create new FeeFilterRounder
    pub fn new(min_incremental_fee: &FeeRate) -> Self {
        use fee_filter_rounder::{FEE_FILTER_SPACING, MAX_FILTER_FEERATE};

        let mut feeset: BTreeSet<FloatOrd<f64>> = BTreeSet::new();

        // CAmount minFeeLimit = std::max(CAmount(1), minIncrementalFee.GetFeePerK() / 2);
        let min_fee_limit: Amount = std::cmp::max(1, min_incremental_fee.get_fee_perk() / 2);

        // Always include 0
        feeset.insert(FloatOrd(0.0));

        // Geometric series from min_fee_limit up to MAX_FILTER_FEERATE
        let mut bucket = FloatOrd(min_fee_limit as f64);
        while bucket <= MAX_FILTER_FEERATE {
            feeset.insert(bucket);
            bucket = FloatOrd(bucket.0 * FEE_FILTER_SPACING.0);
        }

        Self {
            feeset,
            insecure_rand: FastRandomContext::new(false),
        }
    }

    /// Quantize a minimum fee for privacy purpose before broadcast.
    /// Not thread-safe due to use of FastRandomContext
    pub fn round(&mut self, current_min_fee: Amount) -> Amount {
        // lower_bound(current_min_fee)
        let cur = FloatOrd(current_min_fee as f64);
        let mut iter = self.feeset.range(cur..);
        let lower = iter.next().copied();
        let at_end = lower.is_none();

        // If end(), choose last element (we will step back unconditionally below).
        let mut chosen = lower
            .unwrap_or_else(|| *self.feeset.iter().next_back().expect("feeset not empty"));

        // If ((it != begin && rand%3!=0) || it == end) it--;
        let at_begin = self
            .feeset
            .iter()
            .next()
            .map(|&first| first == chosen)
            .unwrap_or(true);
        let rand_step_back = (self.insecure_rand.rand32() % 3) != 0;

        if at_end || (!at_begin && rand_step_back) {
            if let Some(prev) = self.feeset.range(..chosen).next_back().copied() {
                chosen = prev;
            }
        }

        chosen.0 as Amount
    }
}

impl Default for FeeRate {

    /**
    | Fee rate of 0 satoshis per kB
    |
    */
    fn default() -> Self {
        Self {
            n_satoshis_perk: 0,
        }
    }
}

impl Ord for FeeRate {

    #[inline]
    fn cmp(&self, other: &Self) -> Ordering {
        self.n_satoshis_perk.cmp(&other.n_satoshis_perk)
    }
}

impl PartialOrd for FeeRate {

    #[inline]
    fn partial_cmp(&self, other: &Self) -> Option<Ordering> {
        Some(self.cmp(other))
    }
}

impl AddAssign<&FeeRate> for FeeRate {

    #[inline]
    fn add_assign(&mut self, other: &FeeRate) {
        self.n_satoshis_perk += other.n_satoshis_perk;
    }
}

impl fmt::Display for FeeRate {

    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        f.write_str(&self.to_string(None))
    }
}

impl FeeRate {

    /// Construct a fee‑rate from an explicit **sat/kvB** integer.
    #[inline]
    pub fn new<I>(n_satoshis_perk: I) -> Self where
            I: Into<Amount> + Copy
    {
        // NOTE: in the C++, we've previously had bugs creep in from silent double->int conversion...
        // Please keep this in mind.
        //
        let value = n_satoshis_perk.into();
        trace!(value, "FeeRate::new");
        Self {
            n_satoshis_perk: value,
        }
    }

    /// Fee for **1000 bytes** (one *kvB*).
    ///
    /// Return the fee in satoshis for a size of 1000 bytes
    #[inline]
    pub fn get_fee_perk(&self) -> Amount {
        self.get_fee(1000)
    }

    /// Construct from a *paid* fee and the serialized *size* in bytes.
    ///
    /// Constructor for a fee rate in satoshis per kvB (sat/kvB).
    /// When `num_bytes == COIN (1 × 10⁸)`, the resulting instance represents
    /// sat/vB instead of sat/kvB (this is identical to Core’s dual‑unit ctor).
    /// Passing a num_bytes value of COIN (1e8) returns a fee rate in satoshis per vB (sat/vB),
    /// e.g. (nFeePaid * 1e8 / 1e3) == (nFeePaid / 1e5), where 1e5 is the ratio to convert from
    /// BTC/kvB to sat/vB.
    pub fn new_with_fee_paid(n_fee_paid: &Amount, num_bytes: u32) -> Self {
        trace!(
            n_fee_paid = *n_fee_paid,
            num_bytes,
            "FeeRate::new_with_fee_paid"
        );

        let n_satoshis_perk = if num_bytes != 0 {
            // Cast to i128 to avoid intermediate overflow then back.
            ((*n_fee_paid as i128) * 1000 / num_bytes as i128) as Amount
        } else {
            0
        };

        Self { n_satoshis_perk }
    }

    /// Fee for an arbitrary serialization size (in *bytes*).
    ///
    /// The algorithm intentionally **truncates toward 0** to replicate the
    /// original behaviour; if that would yield `0` while the rate is non‑zero,
    /// the result is bumped to ±1 sat so that the direction of the fee is
    /// retained and never hidden.
    pub fn get_fee(&self, num_bytes: u32) -> Amount {
        trace!(rate = self.n_satoshis_perk, num_bytes, "FeeRate::get_fee");

        if num_bytes == 0 {
            return 0;
        }

        let n_size = num_bytes as i64;
        let mut n_fee: Amount =
            ((self.n_satoshis_perk as i128) * n_size as i128 / 1000) as Amount;

        if n_fee == 0 {
            if self.n_satoshis_perk > 0 {
                n_fee = 1;
            } else if self.n_satoshis_perk < 0 {
                n_fee = -1;
            }
        }

        n_fee
    }

    /// Human‑readable representation.
    ///
    /// Defaults to `FeeEstimateMode::BTC_KVB` when no mode is supplied.
    pub fn to_string(&self, fee_estimate_mode: Option<&FeeEstimateMode>) -> String {
        let mode = fee_estimate_mode.unwrap_or(&FeeEstimateMode::BTC_KVB);

        match mode {
            FeeEstimateMode::SAT_VB => {
                let whole = self.n_satoshis_perk / 1000;
                let frac = (self.n_satoshis_perk.abs() % 1000) as u32;
                format!("{whole}.{frac:03} {CURRENCY_ATOM}/vB")
            }
            _ => {
                let whole = self.n_satoshis_perk / COIN;
                let frac = (self.n_satoshis_perk.abs() % COIN) as u32;
                format!("{whole}.{frac:08} {CURRENCY_UNIT}/kvB")
            }
        }
    }
}




