impl SemaphoreGrant {

    /// Acquire a permit, blocking if none available.
    pub fn acquire(&mut self) {
        trace!("SemaphoreGrant::acquire");
        if self.have_grant {
            return;
        }
        self.sem.wait();
        self.have_grant = true;
    }

    /// Release the owned permit, if any.
    pub fn release(&mut self) {
        trace!("SemaphoreGrant::release");
        if !self.have_grant {
            return;
        }
        self.sem.post();
        self.have_grant = false;
    }

    /// Attempt to acquire without blocking.
    ///
    /// Returns `true` on success.
    pub fn try_acquire(&mut self) -> bool {
        trace!("SemaphoreGrant::try_acquire");
        if !self.have_grant && self.sem.try_wait() {
            self.have_grant = true;
        }
        self.have_grant
    }

    /// Transfer ownership of a permit to another `SemaphoreGrant`.
    pub fn move_to(&mut self, target: &mut SemaphoreGrant) {
        trace!("SemaphoreGrant::move_to");
        target.release();
        target.sem        = Arc::clone(&self.sem);
        target.have_grant = self.have_grant;
        self.have_grant   = false;
    }

    /// Construct a new `SemaphoreGrant`.
    ///
    /// If `try_ == true`, attempts a non‑blocking acquire;
    /// otherwise blocks until a permit is obtained.
    pub fn new(sema: Arc<Semaphore>, try_: Option<bool>) -> Self {
        let mut grant = Self {
            sem: sema,
            have_grant: false,
        };
        if try_.unwrap_or(false) {
            grant.try_acquire();
        } else {
            grant.acquire();
        }
        grant
    }
}

impl<'a, M: LockApi + ?Sized> UniqueLock<'a, M> {

    /// Construct; lock immediately or try‑lock depending on `try_`.
    pub fn new(
        mutex: &'a M,
        name:  &'static str,
        file:  &'static str,
        line:  u32,
        try_:  Option<bool>
    ) -> Self
    {
        let mut ul = Self {
            mutex,
            owns: false,
            name,
            file,
            line,
        };
        if try_.unwrap_or(false) {
            ul.owns = mutex.try_lock();
            trace!(
                "UniqueLock::new TRY — owns = {} ({name} @ {file}:{line})",
                ul.owns
            );
        } else {
            trace!("UniqueLock::new LOCK — ({name} @ {file}:{line})");
            mutex.lock();
            ul.owns = true;
        }
        ul
    }

    #[inline]
    pub fn enter(&mut self) {
        if !self.owns {
            trace!("UniqueLock::enter — ({})", self.name);
            self.mutex.lock();
            self.owns = true;
        }
    }

    #[inline]
    pub fn try_enter(&mut self) -> bool {
        if !self.owns {
            self.owns = self.mutex.try_lock();
            trace!(
                "UniqueLock::try_enter — success = {} ({})",
                self.owns, self.name
            );
        }
        self.owns
    }

    #[inline]
    pub fn unlock(&mut self) {
        if self.owns {
            trace!("UniqueLock::unlock — ({})", self.name);
            self.mutex.unlock();
            self.owns = false;
        }
    }

    /// Mirror `std::unique_lock::owns_lock`.
    #[inline]
    pub fn owns_lock(&self) -> bool {
        self.owns 
    }
}

impl<'a, M: LockApi + ?Sized> Drop for UniqueLock<'a, M> {

    fn drop(&mut self) {
        if self.owns {
            debug!("UniqueLock::drop — unlocking ({})", self.name);
            self.mutex.unlock();
        }
    }
}

