/// Trait used in the Bitcoin code‑base to expose
/// iterator and size types without leaking the
/// concrete map implementation.
pub trait HasIndirectMapTypes<'a> {
    type Iterator:       Iterator<Item = (&'a Arc<Self::Key>, &'a Self::Value)> + 'a;
    type ConstIterator:  Iterator<Item = (&'a Arc<Self::Key>, &'a Self::Value)> + 'a;
    type SizeType;
    type Key;
    type Value;
}

/// Key wrapper that orders **by the dereferenced value** of the `Arc`.
#[derive(Clone)]
pub struct IndirectKey<K: Ord>  (
    Arc<K>,
);


/// Map whose keys are `Arc<K>` pointers but are **compared
/// by the dereferenced `K` value**.
///
/// Invariants:
/// * `K: Ord` so the dereferenced value has a total ordering.
/// * Once inserted, a `K` **must not** mutate in a way that
///   changes its ordering.
#[derive(Builder, Getters, MutGetters)]
#[builder(pattern = "owned")]
pub struct IndirectMap<K, V> where
    K: Ord {
    map: BTreeMap<IndirectKey<K>, V>,
}

impl<K: Ord, V> DynamicUsage for IndirectMap<K, V> {

    #[inline]
    fn dynamic_usage(&self) -> usize {
        let entry_bytes = mem::size_of::<IndirectKey<K>>() + mem::size_of::<V>();
        let total       = entry_bytes * self.map().len();
        debug!(entries = self.map().len(), entry_bytes, total, "Computed dynamic usage for IndirectMap");
        total
    }
}

impl<K: Ord, V> IncrementalDynamicUsage for IndirectMap<K, V> {

    #[inline]
    fn incremental_dynamic_usage(&self) -> usize {
        mem::size_of::<IndirectKey<K>>() + mem::size_of::<V>()
    }
}

impl<K: Ord> IndirectKey<K> {

    /// Construct a new wrapper around an `Arc<K>`.
    #[inline]
    pub(crate) fn new(key: Arc<K>) -> Self {
        trace!("Creating new `IndirectKey`");
        Self(key)
    }

    /// Borrow the inner `Arc` (needed by the map façade).
    #[inline]
    pub(crate) fn arc(&self) -> &Arc<K> {
        &self.0
    }
}

impl<K: Ord> Borrow<K> for IndirectKey<K> {

    #[inline]
    fn borrow(&self) -> &K {
        &*self.0 
    }
}

impl<K: Ord> PartialEq for IndirectKey<K> {

    #[inline]
    fn eq(&self, o: &Self) -> bool {
        *self.0 == *o.0 
    }
}

impl<K: Ord> Eq for IndirectKey<K> {}

impl<K: Ord> PartialOrd for IndirectKey<K> {

    #[inline]
    fn partial_cmp(&self, o: &Self) -> Option<Ordering> {
        Some(self.cmp(o)) 
    }
}

impl<K: Ord> Ord for IndirectKey<K> {

    #[inline]
    fn cmp(&self, o: &Self) -> Ordering {
        (*self.0).cmp(&*o.0) 
    }
}

impl<'a, K, V> HasIndirectMapTypes<'a> for IndirectMap<K, V> where
    K: Ord + 'a,
    V: 'a {

    type Iterator = Box<dyn Iterator<Item = (&'a Arc<K>, &'a V)> + 'a>;

    type ConstIterator = Box<dyn Iterator<Item = (&'a Arc<K>, &'a V)> + 'a>;

    type SizeType = usize;

    type Key = K;

    type Value = V;
}

impl<K: Ord, V> Default for IndirectMap<K, V> {

    #[inline]
    fn default() -> Self {
        trace!("Creating default `IndirectMap`");
        Self {
            map: BTreeMap::default(),
        }
    }
}

impl<K: Ord, V> IndirectMap<K, V> {

    /// Insert a new `(Arc<K>, V)` pair.  
    /// Returns `true` if the key was newly inserted.
    #[inline]
    pub fn insert(&mut self, key: Arc<K>, value: V) -> bool {
        let inserted = self.map.insert(IndirectKey::new(key), value).is_none();
        info!(inserted, "Insert into IndirectMap");
        inserted
    }

    /// Immutable lookup by **value** of `K`.
    #[inline]
    pub fn find(&self, k: &K) -> Option<(&Arc<K>, &V)> {
        self.map.get_key_value(k).map(|(key, val)| (key.arc(), val))
    }

    /// Mutable lookup by **value** of `K`.
    ///  
    /// Because the value is returned mutably, we must avoid
    /// holding an immutable reference into the same map
    /// entry at the same time.  
    /// We therefore:
    /// 1. clone the `Arc<K>` **before** taking the mutable borrow,
    /// 2. fetch the mutable reference in a second step.
    #[inline]
    pub fn find_mut(&mut self, k: &K) -> Option<(Arc<K>, &mut V)> {
        // Step‑1: grab and clone the key **immutably**.
        let key_clone = self
            .map
            .get_key_value(k)
            .map(|(ind_key, _)| ind_key.arc().clone())?;

        // Step‑2: only now obtain the mutable reference.
        let val_mut = self.map.get_mut(k).expect("value present after key fetch");
        Some((key_clone, val_mut))
    }

    /// Maximum theoretical size (bounded by `usize::MAX`).
    #[inline]
    pub fn max_size(&self) -> usize {
        usize::MAX
    }

    /// Immutable lookup by **value** of `K`.
    #[inline]
    pub fn get(&self, k: &K) -> Option<(&Arc<K>, &V)> {
        self.map.get(k).map(|v| (self.map.get_key_value(k).unwrap().0.arc(), v))
    }

    /// Convenience wrapper identical to `find_mut`.
    #[inline]
    pub fn get_mut(&mut self, k: &K) -> Option<(Arc<K>, &mut V)> {
        self.find_mut(k)
    }

    /// Remove by **value** of `K`.  Returns `true` if something was removed.
    #[inline]
    pub fn erase(&mut self, k: &K) -> bool {
        let removed = self.map.remove(k).is_some();
        debug!(removed, "Erase from IndirectMap");
        removed
    }

    /// Number of entries equal to `k` (0 or 1 in a map).
    #[inline]
    pub fn count(&self, k: &K) -> usize {
        usize::from(self.map.contains_key(k))
    }

    /// Return an iterator pointing at the first element
    /// *not* less than `k`.  Equivalent to C++ `lower_bound`.
    #[inline]
    pub fn lower_bound<'a>(&'a self, k: &K) -> Box<dyn Iterator<Item = (&'a Arc<K>, &'a V)> + 'a> {
        Box::new(
            self.map
                .range::<K, _>(k..)
                .map(|(indirect, v)| (indirect.arc(), v)),
        )
    }

    #[inline]
    pub fn is_empty(&self) -> bool {
        self.map.is_empty()
    }

    #[inline]
    pub fn size(&self) -> usize {
        self.map.len()
    }

    #[inline]
    pub fn clear(&mut self) {
        info!("Clearing IndirectMap ({} entries)", self.size());
        self.map.clear();
    }

    #[inline]
    pub fn iter<'a>(&'a self) -> Box<dyn Iterator<Item = (&'a Arc<K>, &'a V)> + 'a> {
        Box::new(self.map.iter().map(|(k, v)| (k.arc(), v)))
    }

    #[inline]
    pub fn iter_mut<'a>(&'a mut self) -> Box<dyn Iterator<Item = (&'a Arc<K>, &'a mut V)> + 'a> {
        Box::new(self.map.iter_mut().map(|(k, v)| (k.arc(), v)))
    }
}


