/**
| A hasher class for HMAC-SHA-256.
|
*/
#[derive(Getters,MutGetters,Setters)]
#[getset(get="pub",get_mut="pub",set="pub")]
pub struct HmacSha256  {
    outer: Sha256,
    inner: Sha256,
}


#[derive(Builder,Getters,Setters,MutGetters)]
#[getset(get="pub",set="pub",get_mut="pub")]
#[builder(setter(into))]
pub struct Rfc6979HmacSha256  {
    v:     [u8; 32],
    k:     [u8; 32],
    retry: i32,
}

pub fn hmac_sha256_initialize(hash: *mut HmacSha256, key: *const u8, keylen: usize) {
    unsafe {
        let out = &mut *hash;
        let key_slice = if keylen == 0 { &[] } else { std::slice::from_raw_parts(key, keylen) };
        // Move a freshly initialised context into the caller's storage.
        *out = HmacSha256::from_slice(key_slice);
    }
}


pub fn hmac_sha256_write(hash: *mut HmacSha256, data: *const u8, size: usize) {
    unsafe {
        let h = &mut *hash;
        let data_slice = if size == 0 { &[] } else { std::slice::from_raw_parts(data, size) };
        h.write_ref(data_slice);
    }
}


pub fn hmac_sha256_finalize(hash: *mut HmacSha256, out32: *mut u8) {
    unsafe {
        debug_assert!(!out32.is_null());
        // SAFETY: caller guarantees space for 32 bytes
        let out: &mut [u8; 32] = &mut *(out32 as *mut [u8; 32]);
        (&mut *hash).finalize_into(out);
    }
}


pub fn rfc6979_hmac_sha256_initialize(rng: *mut Rfc6979HmacSha256, key: *const u8, keylen: usize) {
    // C version preserved, step-for-step with RFC6979 §3.2.
    unsafe {
        let rng = &mut *rng;
        const ZERO: [u8; 1] = [0x00];
        const ONE: [u8; 1] = [0x01];

        // RFC6979 3.2.b and 3.2.c
        rng.v.fill(0x01);
        rng.k.fill(0x00);

        // RFC6979 3.2.d
        let mut hmac: HmacSha256 = core::mem::zeroed();
        hmac_sha256_initialize(&mut hmac, rng.k.as_ptr(), 32);
        hmac_sha256_write(&mut hmac, rng.v.as_ptr(), 32);
        hmac_sha256_write(&mut hmac, ZERO.as_ptr(), 1);
        hmac_sha256_write(&mut hmac, key, keylen);
        hmac_sha256_finalize(&mut hmac, rng.k.as_mut_ptr());

        hmac_sha256_initialize(&mut hmac, rng.k.as_ptr(), 32);
        hmac_sha256_write(&mut hmac, rng.v.as_ptr(), 32);
        hmac_sha256_finalize(&mut hmac, rng.v.as_mut_ptr());

        // RFC6979 3.2.f
        hmac_sha256_initialize(&mut hmac, rng.k.as_ptr(), 32);
        hmac_sha256_write(&mut hmac, rng.v.as_ptr(), 32);
        hmac_sha256_write(&mut hmac, ONE.as_ptr(), 1);
        hmac_sha256_write(&mut hmac, key, keylen);
        hmac_sha256_finalize(&mut hmac, rng.k.as_mut_ptr());

        hmac_sha256_initialize(&mut hmac, rng.k.as_ptr(), 32);
        hmac_sha256_write(&mut hmac, rng.v.as_ptr(), 32);
        hmac_sha256_finalize(&mut hmac, rng.v.as_mut_ptr());

        rng.retry = 0;
    }
}


pub fn rfc6979_hmac_sha256_generate(rng: *mut Rfc6979HmacSha256, mut out: *mut u8, mut outlen: usize) {
    // C version preserved (RFC6979 3.2.h).
    unsafe {
        let rng = &mut *rng;
        const ZERO: [u8; 1] = [0x00];

        if rng.retry != 0 {
            let mut hmac: HmacSha256 = core::mem::zeroed();
            hmac_sha256_initialize(&mut hmac, rng.k.as_ptr(), 32);
            hmac_sha256_write(&mut hmac, rng.v.as_ptr(), 32);
            hmac_sha256_write(&mut hmac, ZERO.as_ptr(), 1);
            hmac_sha256_finalize(&mut hmac, rng.k.as_mut_ptr());

            hmac_sha256_initialize(&mut hmac, rng.k.as_ptr(), 32);
            hmac_sha256_write(&mut hmac, rng.v.as_ptr(), 32);
            hmac_sha256_finalize(&mut hmac, rng.v.as_mut_ptr());
        }

        while outlen > 0 {
            let mut hmac: HmacSha256 = core::mem::zeroed();
            hmac_sha256_initialize(&mut hmac, rng.k.as_ptr(), 32);
            hmac_sha256_write(&mut hmac, rng.v.as_ptr(), 32);
            hmac_sha256_finalize(&mut hmac, rng.v.as_mut_ptr());

            let now = if outlen > 32 { 32 } else { outlen };
            ptr::copy_nonoverlapping(rng.v.as_ptr(), out, now);
            out = out.add(now);
            outlen -= now;
        }

        rng.retry = 1;
    }
}


pub fn rfc6979_hmac_sha256_finalize(rng: *mut Rfc6979HmacSha256) {
    // C version preserved.
    unsafe {
        let rng = &mut *rng;
        ptr::write_bytes(rng.k.as_mut_ptr(), 0, 32);
        ptr::write_bytes(rng.v.as_mut_ptr(), 0, 32);
        rng.retry = 0;
    }
}

impl HmacSha256 {

    pub fn new(key: *const u8, keylen: usize) -> Self {
        // Delegate to the safe constructor
        let slice = unsafe { if keylen == 0 { &[] } else { std::slice::from_raw_parts(key, keylen) } };
        Self::from_slice(slice)
    }

    #[inline]
    pub fn new_with_key(key: &[u8]) -> Self {
        Self::from_slice(key) 
    }

    pub fn write(&mut self, data: *const u8, len: usize) -> &mut HmacSha256 {
        // Delegate to the safe method
        let slice = unsafe { if len == 0 { &[] } else { std::slice::from_raw_parts(data, len) } };
        self.write_ref(slice)
    }

    /// Feed message bytes (safe slice).
    #[inline]
    pub fn write_ref(&mut self, data: &[u8]) -> &mut HmacSha256 {
        if !data.is_empty() {
            self.inner_mut().write_ptr(data.as_ptr(), data.len());
        }
        self
    }

    /// Construct an HMAC-SHA256 context from a key slice.
    pub fn from_slice(key: &[u8]) -> Self {
        let mut rkey = [0u8; 64];

        if key.len() <= 64 {
            if !key.is_empty() {
                rkey[..key.len()].copy_from_slice(key);
            }
            if key.len() < 64 {
                // trailing bytes already zeroed
            }
        } else {
            // rkey = SHA256(key), then zero-pad to 64
            let mut sh = Sha256::new();
            sh.write_ptr(key.as_ptr(), key.len());
            sh.finalize((&mut rkey[..32]).try_into().unwrap());
            // rkey[32..] already zero
        }

        // ipad/opad init
        let mut outer = Sha256::new();
        let mut inner = Sha256::new();

        // opad = 0x5c
        for b in &mut rkey { *b ^= 0x5c; }
        outer.write_ptr(rkey.as_ptr(), rkey.len());

        // ipdad: toggle ^0x5c back and ^0x36 → net ^= 0x36
        for b in &mut rkey { *b ^= 0x5c ^ 0x36; }
        inner.write_ptr(rkey.as_ptr(), rkey.len());

        HmacSha256 { outer, inner }
    }

    /// Finalize into `out[32]` (safe slice).
    pub fn finalize_into(&mut self, out: &mut [u8; HMAC_SHA256_OUTPUT_SIZE]) {
        let mut temp = [0u8; 32];
        self.inner_mut().finalize(&mut temp);
        self.outer_mut().write_ptr(temp.as_ptr(), temp.len());
        self.outer_mut().finalize(out);
    }

    /// Legacy signature (kept for symmetry with the C++ layout).
    /// Note: takes `hash` **by value**; prefer `finalize_into(&mut [u8;32])`.
    pub fn finalize(&mut self, mut hash: [u8; HMAC_SHA256_OUTPUT_SIZE]) {
        self.finalize_into(&mut hash);
    }
}


