#[derive(Copy,Clone,Debug,PartialEq,Eq,Hash)]
pub enum JTokenType {
    JTOK_ERR,
    JTOK_NONE,
    JTOK_OBJ_OPEN,
    JTOK_OBJ_CLOSE,
    JTOK_ARR_OPEN,
    JTOK_ARR_CLOSE,
    JTOK_COLON,
    JTOK_COMMA,
    JTOK_KW_NULL,
    JTOK_KW_TRUE,
    JTOK_KW_FALSE,
    JTOK_NUMBER,
    JTOK_STRING,
}

/// Return `true` when the byte (expressed as `i32`)
/// is one of the four JSON‑defined whitespace
/// characters: space, horizontal‑tab, line‑feed, or carriage‑return.
///
/// See RFC 7159 §2.
#[instrument(level = "trace", skip_all)]
pub fn json_isspace(ch: i32) -> bool {
    let is_space = matches!(ch, 0x20 | 0x09 | 0x0A | 0x0D);
    trace!(chr = ch, is_space);
    is_space
}


/// Lightweight helper – returns **true** iff `s` is *exactly* one
/// syntactically valid JSON number.
#[instrument(level = "trace", skip_all)]
pub fn valid_num_str(s: &str) -> bool {
    let mut tok_val  = String::new();
    let mut consumed = 0u32;
    let tok = get_json_token(
        &mut tok_val,
        &mut consumed,
        s.as_ptr(),
        unsafe { s.as_ptr().add(s.len()) },
    );

    let ok = tok == JTokenType::JTOK_NUMBER && consumed as usize == s.len();
    trace!(input = s, ?tok, consumed, ok, "valid_num_str verdict");
    ok
}


/**
| Convert a hexadecimal string (given by
| *first* … *last*) into an unsigned `u32`.
| The function writes the parsed value into
| *out* and returns a pointer to the first
| byte that was **not** consumed.
*/
#[instrument(level = "trace", skip_all)]
pub fn hatoui(first: *const u8, last: *const u8, out: &mut u32) -> *const u8 {
    let mut result: u32 = 0;
    let mut ptr = first;

    unsafe {
        while ptr != last {
            let ch = *ptr as char;
            let digit_opt = match ch {
                '0'..='9' => Some(ch as u32 - '0' as u32),
                'a'..='f' => Some(ch as u32 - 'a' as u32 + 10),
                'A'..='F' => Some(ch as u32 - 'A' as u32 + 10),
                _ => None,
            };

            match digit_opt {
                Some(d) => {
                    result = result.wrapping_mul(16).wrapping_add(d);
                    ptr = ptr.add(1);
                }
                None => break,
            }
        }

        *out = result;
        ptr
    }
}


#[instrument(level = "trace", skip(token_val, consumed))]
pub fn get_json_token(
    token_val: &mut String,
    consumed:  &mut u32,
    raw:       *const u8,
    end:       *const u8
) -> JTokenType
{
    unsafe {
        token_val.clear();
        *consumed = 0;

        /* ---------- 1 / 5  skip leading whitespace / NUL ---------- */
        let mut p     = skip_ws_nul(raw, end);
        let raw_start = p;

        // number of bytes skipped before seeing the token
        let skipped = (raw_start as usize - raw as usize) as u32;
        if skipped > 0 {
            trace!(
                skipped,
                "get_json_token: skipped leading JSON whitespace / NUL padding"
            );
        }

        /* ---------- 2 / 5  try structural ---------- */
        if let Some((tok, after, n)) = lex_structural(p, end) {
            *consumed = n; // count only the token itself, not leading whitespace
            trace!(
                ?tok,
                consumed = *consumed,
                "get_json_token: structural token recognised"
            );
            return tok;
        }

        /* ---------- 3 / 5  try keyword ---------- */
        if let Some((tok, after, n)) = lex_keyword(p, end) {
            *consumed = n;
            trace!(
                ?tok,
                consumed = *consumed,
                "get_json_token: keyword token recognised"
            );
            return tok;
        }

        /* ---------- 4 / 5  try number ---------- */
        if let Some((tok, after, n)) = lex_number(token_val, p, end) {
            *consumed = n;
            trace!(
                ?tok,
                consumed = *consumed,
                value = %token_val,
                "get_json_token: number token recognised"
            );
            return tok;
        }

        /* ---------- 5 / 5  try string ---------- */
        if let Some((tok, after, n)) = lex_string(token_val, p, end) {
            *consumed = n;
            trace!(
                ?tok,
                consumed = *consumed,
                value = %token_val,
                "get_json_token: string token recognised"
            );
            return tok;
        }

        /* ---------- no match ⇒ error / eof ---------- */
        if p >= end {
            trace!("get_json_token: reached end of input ⇒ JTOK_NONE");
            JTokenType::JTOK_NONE
        } else {
            trace!(byte = *p, "get_json_token: unrecognised input ⇒ JTOK_ERR");
            JTokenType::JTOK_ERR
        }
    }
}


pub fn json_isdigit(ch: i32) -> bool {
    let ch: u8 = ch.try_into().unwrap();

    let ch = char::from(ch);

    (ch >= '0') && (ch <= '9')
}


#[inline]
pub fn parse_string_escape(backslash: *const u8, end: *const u8) -> Option<(String, *const u8)> {
    //  backslash points at the '\', so we need the char _after_ it
    let mut cur = backslash.add(1);
    if cur >= end {
        return None;
    }
    let mut out = String::new();
    match *cur as char {
        '"'  => out.push('"'),
        '\\' => out.push('\\'),
        '/'  => out.push('/'),
        'b'  => out.push('\u{0008}'),
        'f'  => out.push('\u{000C}'),
        'n'  => out.push('\n'),
        'r'  => out.push('\r'),
        't'  => out.push('\t'),
        'u'  => {
            /* ensure at least uXXXX available */
            if cur.add(5) > end {
                return None;
            }
            let mut cp = 0u32;
            let next = hatoui(cur.add(1), cur.add(5), &mut cp);
            if next != cur.add(5) {
                return None;
            }
            /* -------- surrogate pair handling (unchanged) -------- */
            match cp {
                0xD800..=0xDBFF => {
                    if cur.add(11) > end || *cur.add(5) != b'\\' || *cur.add(6) != b'u' {
                        return None;
                    }
                    let mut low = 0u32;
                    let next2 = hatoui(cur.add(7), cur.add(11), &mut low);
                    if next2 != cur.add(11) || !(0xDC00..=0xDFFF).contains(&low) {
                        return None;
                    }
                    let full = 0x10000 + ((cp - 0xD800) << 10) + (low - 0xDC00);
                    out.push(char::from_u32(full)?);
                    cur = next2;
                }
                0xDC00..=0xDFFF => return None,
                _ => {
                    out.push(char::from_u32(cp)?);
                    cur = next;
                }
            }
            cur = cur.sub(1);          // compensate for main loop’s +1
        }
        _ => return None,
    }
    cur = cur.add(1);
    Some((out, cur))
}


/// Return `true` when *jtt* represents one of the five
/// terminal JSON value token types (null / true / false / number / string).
#[inline]
#[instrument(level = "trace", skip_all)]
pub fn json_token_is_value(jtt: JTokenType) -> bool {
    matches!(
        jtt,
        JTokenType::JTOK_KW_NULL
            | JTokenType::JTOK_KW_TRUE
            | JTokenType::JTOK_KW_FALSE
            | JTokenType::JTOK_NUMBER
            | JTokenType::JTOK_STRING
    )
}


/// Try to lex a single‑byte structural token.  On success returns
/// `(token, ptr_after, consumed)`.
#[inline]
#[instrument(level = "trace", skip_all)]
pub fn lex_structural(p: *const u8, end: *const u8) -> Option<(JTokenType, *const u8, u32)> {
    if p >= end {
        return None;
    }
    let t = match *p as char {
        '{' => JTokenType::JTOK_OBJ_OPEN,
        '}' => JTokenType::JTOK_OBJ_CLOSE,
        '[' => JTokenType::JTOK_ARR_OPEN,
        ']' => JTokenType::JTOK_ARR_CLOSE,
        ':' => JTokenType::JTOK_COLON,
        ',' => JTokenType::JTOK_COMMA,
        _   => return None,
    };
    let after = p.add(1);
    Some((t, after, 1))
}


/// Try to lex one of the three JSON keywords (`null / true / false`).
#[inline]
#[instrument(level = "trace", skip_all)]
pub fn lex_keyword(p: *const u8, end: *const u8) -> Option<(JTokenType, *const u8, u32)> {
    if p.add(4) <= end {
        let lit4 = std::slice::from_raw_parts(p, 4);
        if lit4 == b"null" {
            return Some((
                JTokenType::JTOK_KW_NULL,
                p.add(4),
                4,
            ));
        }
        if lit4 == b"true" {
            return Some((
                JTokenType::JTOK_KW_TRUE,
                p.add(4),
                4,
            ));
        }
    }
    if p.add(5) <= end && std::slice::from_raw_parts(p, 5) == b"false" {
        return Some((
            JTokenType::JTOK_KW_FALSE,
            p.add(5),
            5,
        ));
    }
    None
}


/// Try to lex a JSON number.  Returns `(token, after, consumed, literal)`.
#[inline]
#[instrument(level = "trace", skip(token_val))]
pub fn lex_number(token_val: &mut String, p: *const u8, end: *const u8) -> Option<(JTokenType, *const u8, u32)> {
    use std::ptr::copy_nonoverlapping;

    /* ---------- 1/7  basic pre‑filter ----------------------------------- */
    if p >= end {
        return None;
    }
    let first = *p as char;
    if !matches!(first, '-' | '0'..='9') {
        return None;
    }

    /* ---------- 2/7  “‑” MUST be followed by a digit -------------------- */
    if first == '-' {
        if p.add(1) >= end || !json_isdigit(*p.add(1) as i32) {
            //  lone “‑”, “‑e…”, “‑.…” ⇒ *not* a valid number
            return None;
        }
    }

    /* ---------- 3/7  fast scan over the integer part ------------------- */
    let start = p;
    let mut cur = p.add(1);                       // we already looked at the 1st char
    while cur < end && json_isdigit(*cur as i32) {
        cur = cur.add(1);
    }

    /* ---------- 4/7  reject leading‑zero violations -------------------- */
    {
        let len   = (cur as usize - start as usize) as usize;
        let slice = std::slice::from_raw_parts(start, len);
        if (slice[0] == b'0' && len > 1 && slice[1].is_ascii_digit())
            || (slice[0] == b'-'
                && len > 2
                && slice[1] == b'0'
                && slice[2].is_ascii_digit())
        {
            return None;
        }
    }

    /* ---------- 5/7  optional fraction --------------------------------- */
    if cur < end && *cur as char == '.' {
        cur = cur.add(1);
        if cur >= end || !json_isdigit(*cur as i32) {
            return None;                          // “1.” is illegal
        }
        while cur < end && json_isdigit(*cur as i32) {
            cur = cur.add(1);
        }
    }

    /* ---------- 6/7  optional exponent ---------------------------------- */
    if cur < end && matches!(*cur as char, 'e' | 'E') {
        cur = cur.add(1);
        if cur < end && matches!(*cur as char, '+' | '-') {
            cur = cur.add(1);
        }
        if cur >= end || !json_isdigit(*cur as i32) {
            return None;                          // “1e”, “1e+” …
        }
        while cur < end && json_isdigit(*cur as i32) {
            cur = cur.add(1);
        }
    }

    /* ---------- 7/7  materialise literal into `token_val` --------------- */
    let len = (cur as usize - start as usize) as usize;
    token_val.clear();
    token_val.reserve(len);
    token_val.as_mut_vec().resize(len, 0);
    copy_nonoverlapping(start, token_val.as_mut_ptr(), len);
    token_val.as_mut_vec().set_len(len);

    let consumed = bytes_consumed(start, cur);
    trace!(value = %token_val, consumed, "number token");
    Some((JTokenType::JTOK_NUMBER, cur, consumed))
}


/// Try to lex a JSON string.  Returns `(token, after, consumed, literal)`.
#[inline]
#[instrument(level = "trace", skip(token_val))]
pub fn lex_string(token_val: &mut String, p: *const u8, end: *const u8) -> Option<(JTokenType, *const u8, u32)> {
    if p >= end || *p as char != '"' {
        return None;
    }
    let start = p;
    let mut cur = p.add(1);            // after opening quote
    token_val.clear();

    while cur < end {
        let ch = *cur;
        if ch < 0x20 {
            return None;
        }
        if ch == b'"' {
            cur = cur.add(1);          // position after closing quote
            break;
        }
        if ch == b'\\' {
            /*  Re‑use existing escape handling by delegating to the
                original (now private) code – moved into its own helper
                for reuse.                                                */
            if let Some((parsed, after)) = parse_string_escape(cur, end) {
                token_val.push_str(&parsed);
                cur = after;
                continue;
            } else {
                return None;
            }
        }
        token_val.push(ch as char);
        cur = cur.add(1);
    }

    if cur > end {
        return None;                   // unterminated
    }

    let consumed = bytes_consumed(start, cur);
    trace!(value = %token_val, consumed, "string token");
    Some((JTokenType::JTOK_STRING, cur, consumed))
}


#[inline]
#[instrument(level = "trace", skip(p, consumed))]
pub fn single_byte_token(
    p:        &mut *const u8,
    start:    *const u8,
    consumed: &mut u32,
    kind:     JTokenType
) -> JTokenType
{
    *p = (*p).add(1);                                       // advance cursor
    *consumed = (*p as usize - start as usize) as u32;
    trace!(?kind, consumed, "single_byte_token emitted");
    kind
}


/// Skip JSON whitespace (`SP`, `HT`, `LF`, `CR`) **and** trailing NUL
/// padding.  Returns the first non‑whitespace, non‑NUL position.
#[inline]
#[instrument(level = "trace", skip_all)]
pub fn skip_ws_nul(mut p: *const u8, end: *const u8) -> *const u8 {
    while p < end && (json_isspace(*p as i32) || *p == 0) {
        p = p.add(1);
    }
    p
}


/// Helper used by *all* sub‑lexers to compute the byte count that was
/// consumed for the current token.
#[inline]
pub fn bytes_consumed(start: *const u8, after: *const u8) -> u32 {
    (after as usize - start as usize) as u32
}



