Here is the interface:
/**
| Implements random read access in a file using
| mmap().
|
| Instances of this class are thread-safe, as
| required by the RandomAccessFile API. Instances
| are immutable and Read() only calls thread-safe
| library functions.
*/
#[derive(Getters, Builder, Debug)]
#[getset(get = "pub")]
pub struct PosixMmapReadableFile  {
    mmap_base:    *const u8,
    length:       usize,
    mmap_limiter: *const Limiter,
    filename:     String,
}

impl PosixMmapReadableFile {

    /**
    | `mmap_base` [0, length-1] points to the
    | memory-mapped contents of the file. It must
    | be the result of a successful call to
    | mmap(). This instance takes over the
    | ownership of the region.
    |
    | `mmap_limiter` must outlive this instance.
    | The caller must have already acquired the
    | right to use one mmap region, which will be
    | released when this instance is destroyed.
    */
    pub fn new(
        filename:     String,
        mmap_base:    *mut u8,
        length:       usize,
        mmap_limiter: *mut Limiter
    ) -> Self
    {
        trace!(
            file    = %filename,
            base    = ?mmap_base,
            len     = length,
            limiter = ?mmap_limiter,
            "PosixMmapReadableFile::new"
        );

        // We *allow* null base with zero length so tests can create degenerate
        // instances safely. Production code should only pass a null base when
        // `length == 0`.
        if length > 0 && mmap_base.is_null() {
            warn!(
                file = %filename,
                "PosixMmapReadableFile::new: non-zero length with null base"
            );
        }

        PosixMmapReadableFileBuilder::default()
            .mmap_base(mmap_base as *const u8)
            .length(length)
            .mmap_limiter(mmap_limiter as *const Limiter)
            .filename(filename)
            .build()
            .unwrap()
    }
}

impl RandomAccessFileRead for PosixMmapReadableFile {

    fn read(
        &self,
        offset:   u64,
        n:        usize,
        result:   *mut Slice,
        _scratch: *mut u8
    ) -> Status
    {
        trace!(
            file   = %self.filename(),
            offset,
            n,
            len    = self.length(),
            "PosixMmapReadableFile::read: entry"
        );

        // Compute offset+n in a wide type to avoid overflow.
        let length_u128 = *self.length() as u128;
        let offset_u128 = offset as u128;
        let n_u128      = n as u128;

        let end_ok = offset_u128
            .checked_add(n_u128)
            .map(|end| end <= length_u128)
            .unwrap_or(false);

        if !end_ok {
            // Out‑of‑range read: return empty slice and an IO error
            // (original C++ uses PosixError(fname, EINVAL)).
            unsafe {
                *result = Slice::default();
            }

            let fname = self.filename().clone();
            let fname_slice = Slice::from(&fname);
            let status = Status::io_error(&fname_slice, None);

            debug!(
                file       = %self.filename(),
                offset,
                n,
                len        = self.length(),
                status_str = %status.to_string(),
                "PosixMmapReadableFile::read: range error"
            );
            return status;
        }

        // Safe now to cast offset to usize, since end <= length <= usize::MAX.
        let offset_usize = offset as usize;

        unsafe {
            let ptr = self.mmap_base().add(offset_usize);
            *result = Slice::from_ptr_len(ptr, n);
        }

        let status = Status::ok();
        debug!(
            file   = %self.filename(),
            offset,
            n,
            "PosixMmapReadableFile::read: success"
        );
        status
    }
}

impl Drop for PosixMmapReadableFile {

    fn drop(&mut self) {
        use libc::{munmap, c_void};

        trace!(
            file = %self.filename(),
            base = ?self.mmap_base(),
            len  = self.length(),
            limiter = ?self.mmap_limiter(),
            "PosixMmapReadableFile::drop: unmapping region and releasing mmap token"
        );

        // Unmap the region if it looks valid.
        if !self.mmap_base().is_null() && *self.length() > 0 {
            let rc = unsafe {
                munmap(*self.mmap_base() as *mut c_void, *self.length())
            };
            if rc != 0 {
                // We can't recover here, but log for diagnostics.
                let err = std::io::Error::last_os_error();
                warn!(
                    file = %self.filename(),
                    rc,
                    %err,
                    "PosixMmapReadableFile::drop: munmap failed"
                );
            }
        }

        // Release our slot in the mmap limiter, if present.
        if !self.mmap_limiter().is_null() {
            // Safety: the caller guaranteed that `mmap_limiter` outlives this
            // object and that we successfully "acquired" exactly one mmap slot
            // at construction time.
            unsafe {
                let limiter: &Limiter = &*(*self.mmap_limiter());
                limiter.release();
            }
        }

        debug!(
            file = %self.filename(),
            "PosixMmapReadableFile::drop: completed"
        );
    }
}

impl RandomAccessFile for PosixMmapReadableFile {}

impl Named for PosixMmapReadableFile {

    fn name(&self) -> Cow<'_,str> {
        Cow::Borrowed(&self.filename)
    }
}


