/// Equivalent to the original C++ `ROTL` macro
/// from Bitcoin Core (wrap‑around semantics).
#[macro_export]
macro_rules! rotl { {
    ($x:expr, $b:expr) => {
        ($x).rotate_left($b)
    };
} }


/// SipHash compression round (`SIPROUND`) exactly as in the
/// Bitcoin Core reference implementation.
///
/// Instead of assuming `v0 … v3` are in scope, the identifiers are
/// now passed in as macro arguments, restoring proper macro hygiene.
#[macro_export]
macro_rules! sipround { {
    ($v0:ident, $v1:ident, $v2:ident, $v3:ident) => {{
        ::tracing::trace!(
            "enter SIPROUND: {:016x} {:016x} {:016x} {:016x}",
            $v0, $v1, $v2, $v3
        );

        $v0 = $v0.wrapping_add($v1); $v1 = $v1.rotate_left(13); $v1 ^= $v0;
        $v0 = $v0.rotate_left(32);
        $v2 = $v2.wrapping_add($v3); $v3 = $v3.rotate_left(16); $v3 ^= $v2;
        $v0 = $v0.wrapping_add($v3); $v3 = $v3.rotate_left(21); $v3 ^= $v0;
        $v2 = $v2.wrapping_add($v1); $v1 = $v1.rotate_left(17); $v1 ^= $v2;
        $v2 = $v2.rotate_left(32);

        ::tracing::trace!(
            "exit  SIPROUND: {:016x} {:016x} {:016x} {:016x}",
            $v0, $v1, $v2, $v3
        );
    }};
} }

/// SipHash-2-4
#[derive(Debug,Clone,CopyGetters,MutGetters,Getters)]
pub struct BitcoinSipHasher  {
    v:     [u64; 4],
    tmp:   u64,
    count: u8,
}

/// Bitcoin‑Core‑compatible SipHash‑2‑4 of a 256‑bit value
/// with an additional 32‑bit tag (`extra`).  
///
/// This is the exact translation of `SipHashUint256Extra`
/// from the C++ reference code.
#[inline]
pub fn sip_hash_uint_256extra(
    k0:    u64,
    k1:    u64,
    val:   &u256,
    extra: u32
) -> u64
{
    trace!(
        "sip_hash_uint_256extra(k0={:016x}, k1={:016x}, extra={:08x})",
        k0,
        k1,
        extra
    );

    let mut d  = val.get_uint64(0);
    let mut v0 = 0x736f6d6570736575u64 ^ k0;
    let mut v1 = 0x646f72616e646f6du64 ^ k1;
    let mut v2 = 0x6c7967656e657261u64 ^ k0;
    let mut v3 = 0x7465646279746573u64 ^ k1 ^ d;

    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    d = val.get_uint64(1);
    v3 ^= d;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    d = val.get_uint64(2);
    v3 ^= d;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    d = val.get_uint64(3);
    v3 ^= d;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    d = ((36u64) << 56) | extra as u64; // 32‑byte input + 4‑byte tag = 36
    v3 ^= d;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    v2 ^= 0xFF;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);

    let out = v0 ^ v1 ^ v2 ^ v3;
    trace!("sip_hash_uint_256extra result {:016x}", out);
    out
}


/// Optimized SipHash-2-4 implementation for uint256.
/// 
/// It is identical to:
///   BitcoinSipHasher(k0, k1)
///     .Write(val.GetUint64(0))
///     .Write(val.GetUint64(1))
///     .Write(val.GetUint64(2))
///     .Write(val.GetUint64(3))
///     .Finalize()
/// Constant‑time, branch‑free SipHash‑2‑4 for a `u256`.
#[inline]
pub fn sip_hash_uint256(k0: u64, k1: u64, val: &u256) -> u64 {
    trace!(
        "sip_hash_uint256(k0={:016x}, k1={:016x})",
        k0,
        k1
    );

    let mut d  = val.get_uint64(0);
    let mut v0 = 0x736f6d6570736575u64 ^ k0;
    let mut v1 = 0x646f72616e646f6du64 ^ k1;
    let mut v2 = 0x6c7967656e657261u64 ^ k0;
    let mut v3 = 0x7465646279746573u64 ^ k1 ^ d;

    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    d = val.get_uint64(1);
    v3 ^= d;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    d = val.get_uint64(2);
    v3 ^= d;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    d = val.get_uint64(3);
    v3 ^= d;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= d;

    v3 ^= (4u64) << 59; // 32‑byte message length tag
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    v0 ^= (4u64) << 59;

    v2 ^= 0xFF;
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);
    sipround!(v0, v1, v2, v3);

    let out = v0 ^ v1 ^ v2 ^ v3;
    trace!("sip_hash_uint256 result {:016x}", out);
    out
}


/// Rotate‑left for 64‑bit values.  
/// Kept as a `const fn` so the compiler can evaluate
/// it at compile‑time whenever the arguments are constant.
#[inline(always)]
pub fn rotl64(x: u64, b: u32) -> u64 {
    (x << b) | (x >> (64 - b))
}

impl BitcoinSipHasher {

    /// Return the 64‑bit SipHash‑2‑4 of the data written so far *without*
    /// consuming or mutating `self`.
    ///
    #[inline]
    pub fn finalize(&self) -> u64 {
        let mut v0 = self.v()[0];
        let mut v1 = self.v()[1];
        let mut v2 = self.v()[2];
        let mut v3 = self.v()[3];

        let t = self.tmp() | ((self.count() as u64) << 56);

        v3 ^= t;
        sipround!(v0, v1, v2, v3);
        sipround!(v0, v1, v2, v3);
        v0 ^= t;

        v2 ^= 0xFF;
        sipround!(v0, v1, v2, v3);
        sipround!(v0, v1, v2, v3);
        sipround!(v0, v1, v2, v3);
        sipround!(v0, v1, v2, v3);

        v0 ^ v1 ^ v2 ^ v3
    }
}

impl BitcoinSipHasher {

    /// Hash a 64-bit integer worth of data
    /// 
    /// It is treated as if this was the little-endian interpretation of 8 bytes.
    /// This function can only be used when a multiple of 8 bytes have been written so far.
    ///
    /// Append exactly 8 bytes (as a little‑endian `u64`) to the stream.
    #[inline]
    pub fn write_u64(&mut self, data: u64) -> &mut Self {
        debug!("write_u64 {:016x}", data);
        assert_eq!(self.count() % 8, 0, "write_u64 misaligned");

        let (mut v0, mut v1, mut v2, mut v3) = {
            let v = self.v();
            (v[0], v[1], v[2], v[3])
        };

        v3 ^= data;
        sipround!(v0, v1, v2, v3);
        sipround!(v0, v1, v2, v3);
        v0 ^= data;

        *self.v_mut() = [v0, v1, v2, v3];
        *self.count_mut() = self.count().wrapping_add(8);
        *self.tmp_mut() = 0;
        self
    }

    /// Hash arbitrary bytes.
    ///
    /// Append an arbitrary byte slice to the stream.
    #[inline]
    pub fn write(&mut self, data: &[u8]) -> &mut Self {
        let (mut v0, mut v1, mut v2, mut v3) = {
            let v = self.v();
            (v[0], v[1], v[2], v[3])
        };
        let mut t = self.tmp();
        let mut c = self.count();

        for &byte in data {
            t |= (byte as u64) << (8 * (c as u64 & 7));
            c = c.wrapping_add(1);
            if (c & 7) == 0 {
                v3 ^= t;
                sipround!(v0, v1, v2, v3);
                sipround!(v0, v1, v2, v3);
                v0 ^= t;
                t = 0;
            }
        }

        *self.v_mut()     = [v0, v1, v2, v3];
        *self.count_mut() = c;
        *self.tmp_mut()   = t;
        self
    }
}

impl BitcoinSipHasher {

    /// Initialise a SipHash‑2‑4 state with the 128‑bit key `(k0,k1)`.
    #[inline]
    pub fn new(k0: u64, k1: u64) -> Self {
        debug!("BitcoinSipHasher::new(k0={:016x}, k1={:016x})", k0, k1);
        Self {
            v: [
                0x736f6d6570736575u64 ^ k0,
                0x646f72616e646f6du64 ^ k1,
                0x6c7967656e657261u64 ^ k0,
                0x7465646279746573u64 ^ k1,
            ],
            tmp:   0,
            count: 0,
        }
    }
}


