/// Return value constants for TokenWrite and TokenRead.
#[cfg(not(windows))]
#[repr(i32)]
#[derive(Debug, Copy, Clone, Eq, PartialEq)]
pub enum TokenPipeEndStatus {
    TS_ERR,
    TS_EOS,
}

/// An interprocess or interthread pipe for sending tokens (one-byte values) over.
#[derive(Debug, Getters, Builder)]
#[getset(get = "pub(crate)")]
pub struct TokenPipe  {
    fds: [i32; 2],
}


/// One end of a token pipe.
#[derive(Debug, Getters, Builder)]
#[getset(get = "pub(crate)")]
pub struct TokenPipeEnd  {
    fd: i32,
}

impl Drop for TokenPipe {

    fn drop(&mut self) {
        trace!(fds = ?self.fds, "TokenPipe::drop");
        self.close();
    }
}

impl TokenPipe {

    /// Construct directly from raw FDs.
    pub fn new(fds: [i32; 2]) -> Self {
        trace!(fds = ?fds, "TokenPipe::new");
        Self { fds }
    }

    /// Create a fresh pipe.
    ///
    /// * `Some(pipe)` on success  
    /// * `None`       on error
    pub fn make() -> Option<Self> {
        trace!("TokenPipe::make → start");

        //―――― POSIX (non‑Windows) ――――
        #[cfg(not(windows))]
        {
            let mut fds = [-1; 2];
            if unsafe { libc::pipe(fds.as_mut_ptr()) } != 0 {
                error!(errno = last_errno(), "pipe(2) failed");
                return None;
            }
            info!(fds = ?fds, "pipe(2) created");
            return Some(Self { fds });
        }

        //―――― Windows (msvcrt `_pipe`) ――――
        #[cfg(windows)]
        {
            let mut fds = [-1; 2];
            const BUFSZ: libc::c_int = 4096;
            const O_BINARY: libc::c_int = 0x8000; // _O_BINARY
            if unsafe { libc::_pipe(fds.as_mut_ptr(), BUFSZ, O_BINARY) } != 0 {
                error!(errno = last_errno(), "_pipe failed");
                return None;
            }
            info!(fds = ?fds, "_pipe created");
            return Some(Self { fds });
        }
    }

    /// Move out the read end (may be called once).
    pub fn take_read_end(&mut self) -> TokenPipeEnd {
        trace!(fd = self.fds[0], "TokenPipe::take_read_end");
        let fd = self.fds[0]; 
        self.fds[0] = -1; 
        TokenPipeEnd::new(Some(fd))
    }

    /// Move out the write end (may be called once).
    pub fn take_write_end(&mut self) -> TokenPipeEnd {
        trace!(fd = self.fds[1], "TokenPipe::take_write_end");
        let fd = self.fds[1]; 
        self.fds[1] = -1; 
        TokenPipeEnd::new(Some(fd))
    }

    /// Close any still‑owned endpoints.
    pub fn close(&mut self) {
        trace!(fds = ?self.fds, "TokenPipe::close");
        for fd in &mut self.fds {
            if *fd != -1 {
                #[cfg(windows)]
                unsafe { libc::_close(*fd); }
                #[cfg(not(windows))]
                unsafe { libc::close(*fd); }
                *fd = -1;
            }
        }
    }

    pub fn new_from_other(mut other: Self) -> Self {
        trace!(fds = ?other.fds, "TokenPipe::new_from_other");
        let mut fds = [-1; 2];
        for (d, s) in fds.iter_mut().zip(other.fds.iter_mut()) { 
            *d = *s; 
            *s = -1; 
        }
        Self { fds }
    }

    pub fn assign_from(&mut self, mut other: Self) -> &mut Self {
        trace!(self_fds = ?self.fds, other_fds = ?other.fds, "TokenPipe::assign_from");
        self.close();
        for (d, s) in self.fds.iter_mut().zip(other.fds.iter_mut()) { 
            *d = *s; 
            *s = -1; 
        }
        self
    }
}

impl Drop for TokenPipeEnd {

    fn drop(&mut self) {
        trace!(fd = self.fd, "TokenPipeEnd::drop");
        self.close();
    }
}

#[cfg(not(windows))]
impl TokenPipeEnd {

    /// Construct a new endpoint (`‑1` ⇒ closed).
    pub fn new(fd: Option<i32>) -> Self {
        let fd = fd.unwrap_or(-1);
        trace!(fd, "TokenPipeEnd::new");
        Self { fd }
    }

    /// Write a single token.
    ///
    /// * `0`  on success  
    /// * `<0` on error (`TS_ERR` / `TS_EOS`)
    pub fn token_write(&mut self, token: u8) -> i32 {
        trace!(fd = self.fd, token, "token_write → start");
        if self.fd == -1 {
            warn!("token_write on closed fd");
            return TokenPipeEndStatus::TS_ERR as i32;
        }

        loop {
            let rc = unsafe {
                sys_write(self.fd, &token as *const _ as *const c_void, 1)
            };
            match rc {
                1 => { trace!("token_write success"); return 0; }
                0 => { info!("peer closed (EOS)");   return TokenPipeEndStatus::TS_EOS as i32; }
                _ if rc < 0 => {
                    let e = last_errno();
                    if e == libc::EINTR { debug!("retry after EINTR"); continue; }
                    error!(e, "token_write error");
                    return TokenPipeEndStatus::TS_ERR as i32;
                }
                _ => unreachable!("write returned unexpected count"),
            }
        }
    }

    /// Read a single token.
    ///
    /// * `≥0` token value on success  
    /// * `<0`  on error (`TS_ERR` / `TS_EOS`)
    pub fn token_read(&mut self) -> i32 {
        trace!(fd = self.fd, "token_read → start");
        if self.fd == -1 {
            warn!("token_read on closed fd");
            return TokenPipeEndStatus::TS_ERR as i32;
        }

        let mut token: u8 = 0;
        loop {
            let rc = unsafe {
                sys_read(self.fd, &mut token as *mut _ as *mut c_void, 1)
            };
            match rc {
                1 => { trace!(token, "token_read success"); return token as i32; }
                0 => { info!("end‑of‑stream");               return TokenPipeEndStatus::TS_EOS as i32; }
                _ if rc < 0 => {
                    let e = last_errno();
                    if e == libc::EINTR { debug!("retry after EINTR"); continue; }
                    error!(e, "token_read error");
                    return TokenPipeEndStatus::TS_ERR as i32;
                }
                _ => unreachable!("read returned unexpected count"),
            }
        }
    }

    /// Explicitly close this endpoint.
    pub fn close(&mut self) {
        trace!(fd = self.fd, "TokenPipeEnd::close");
        if self.fd != -1 {
            // SAFETY: valid fd / ignored if already closed.
            unsafe { sys_close(self.fd); }
            self.fd = -1;
        }
    }

    /// Is the endpoint still open?
    pub fn is_open(&mut self) -> bool {
        self.fd != -1
    }

    /// Move‑construct from `other`, leaving `other` closed.
    pub fn new_from_other(mut other: TokenPipeEnd) -> Self {
        trace!(fd = other.fd, "TokenPipeEnd::new_from_other");
        let fd = other.fd;
        other.fd = -1;
        Self { fd }
    }

    /// Assign from `other`, closing the current endpoint first.
    pub fn assign_from(&mut self, mut other: TokenPipeEnd) -> &mut TokenPipeEnd {
        trace!(old_fd = self.fd, new_fd = other.fd, "TokenPipeEnd::assign_from");
        self.close();
        self.fd = other.fd;
        other.fd = -1;
        self
    }
}


