/// Autodetect and configure the best available SHA‑256 backend.
///
/// Returns a human‑readable description mirroring Bitcoin Core’s string.
pub fn sha256auto_detect() -> String {
    if let Some(b) = BACKEND.get() {
        return b.desc.into();
    }

    let mut desc = "standard";
    let mut b = Sha256Backend {
        transform: sha256_transform_wrapper,
        d64:       transform_d64_scalar,
        d64_2:     None,
        d64_4:     None,
        d64_8:     None,
        desc,
    };

    #[cfg(target_arch = "x86_64")]
    {
        use std::arch::is_x86_feature_detected;

        #[cfg(feature = "enable-shani")]
        if is_x86_feature_detected!("sha") {
            b.transform = sha256_shani::Transform;
            // FIX: single-lane should be the 1-way function, not 2-way
            b.d64       = sha256d64_shani::Transform;      // ← was Transform_2way
            b.d64_2     = Some(sha256d64_shani::Transform_2way);
            desc = "shani(1way,2way)";
        }

        #[cfg(feature = "enable-avx2")]
        if is_x86_feature_detected!("avx2") && is_x86_feature_detected!("avx") {
            b.d64_8 = Some(sha256d64_avx2::Transform_8way);
            desc = if desc == "standard" { "avx2(8way)" } else { "shani(1way,2way),avx2(8way)" };
        }
    }

    b.desc = desc;
    let _ = BACKEND.set(b);

    // ← NEW: publish the selection to the global pointers used elsewhere
    unsafe {
        use crate::transforms::{
            TRANSFORM, TRANSFORM_D64,
            TRANSFORM_D64_2WAY, TRANSFORM_D64_4WAY, TRANSFORM_D64_8WAY
        };
        TRANSFORM          = backend().transform;
        TRANSFORM_D64      = backend().d64;
        TRANSFORM_D64_2WAY = backend().d64_2;
        TRANSFORM_D64_4WAY = backend().d64_4;
        TRANSFORM_D64_8WAY = backend().d64_8;
    }

    // This now self-tests the *installed* backend
    assert!(crate::self_test(), "backend failed self‑test");
    desc.into()
}


/// FFI‑compatible front‑end to [`Sha256::write_ptr`].
///
/// # Safety
/// * **`hash`** must be a valid, writable pointer to a live [`Sha256`] value.
/// * **`data`** must be either  
///   * a valid, readable pointer to at least `len` bytes **or**  
///   * *null* **iff** `len == 0`.
/// These pre‑conditions mirror those enforced by the original C reference
/// implementation and are **not** re‑validated internally (aside from a
/// debug‑assert).  Violating them results in *undefined behaviour*.
/// # Behaviour
/// Delegates the entire write operation to [`Sha256::write_ptr`], providing a
/// thin, zero‑copy bridge for C‑style callers while preserving all batching,
/// buffering, and block‑processing semantics implemented in Rust.
/// The function logs its invocation and completion via the `tracing` crate
/// under the `sha256` target.
#[inline]
pub fn sha256_write(hash: *mut Sha256, data: *const u8, len: usize) {
    use tracing::{debug, trace};

    debug!(
        target: "sha256",
        ptr = ?hash,
        len,
        "sha256_write: entering (FFI call)"
    );

    debug_assert!(
        !hash.is_null(),
        "sha256_write: `hash` must be non‑null (debug build assert)"
    );
    debug_assert!(
        !data.is_null() || len == 0,
        "sha256_write: `data` is null while `len` ≠ 0 (debug build assert)"
    );

    // Convert the raw pointer into a mutable reference and forward the call.
    // SAFETY: Caller guarantees validity of `hash` for writes and of `data`
    // for reads (see function‑level safety contract above).
    (&mut *hash).write_ptr(data, len);

    trace!(target: "sha256", "sha256_write: leaving (FFI call)");
}


/// Choice function: `z ^ (x & (y ^ z))`
#[inline(always)]
pub fn sha256_ch(x: u32, y: u32, z: u32) -> u32 {
    let res = z ^ (x & (y ^ z));
    trace!(target: "sha256", x, y, z, res, "sha256_ch");
    res
}


/// Majority function: `(x & y) | (z & (x | y))`
#[inline(always)]
pub fn sha256_maj(x: u32, y: u32, z: u32) -> u32 {
    let res = (x & y) | (z & (x | y));
    trace!(target: "sha256", x, y, z, res, "sha256_maj");
    res
}


/// Double‑SHA256 of **one** 64‑byte message (scalar reference path).
///
/// SAFETY: `out32` must point to 32 writable bytes; `in64` to 64 readable bytes.
#[inline]
pub fn transform_d64_scalar(out32: *mut u8, in64: *const u8) {
    // ── First hash: H1 = SHA256( in64[0..64] ) ─────────────────────────────
    let mut s1 = [0u32; 8];
    sha256_initialize(s1.as_mut_ptr());

    // message block
    sha256_transform_block(s1.as_mut_ptr(), in64);

    // padding block for a 64‑byte message: 0x80 || zeros || len=512 bits
    let mut pad1 = [0u8; 64];
    pad1[0] = 0x80;
    // 512 bits length, big‑endian
    pad1[56..64].copy_from_slice(&512u64.to_be_bytes());
    sha256_transform_block(s1.as_mut_ptr(), pad1.as_ptr());

    // Serialize H1 to **big‑endian bytes**.
    let mut h1_be = [0u8; 32];
    for (i, w) in s1.iter().enumerate() {
        h1_be[i * 4..i * 4 + 4].copy_from_slice(&w.to_be_bytes());
    }

    // ── Second hash: H2 = SHA256( h1_be[0..32] ) ───────────────────────────
    let mut s2 = [0u32; 8];
    sha256_initialize(s2.as_mut_ptr());

    // Build the single block: (32 bytes data) || 0x80 || zeros || len=256 bits
    let mut blk2 = [0u8; 64];
    blk2[..32].copy_from_slice(&h1_be);
    blk2[32] = 0x80;
    blk2[56..64].copy_from_slice(&256u64.to_be_bytes());

    sha256_transform_block(s2.as_mut_ptr(), blk2.as_ptr());

    // Write H2 as 32 big‑endian bytes to out32.
    for (i, w) in s2.iter().enumerate() {
        let be = w.to_be_bytes();
        core::ptr::copy_nonoverlapping(be.as_ptr(), out32.add(i * 4), 4);
    }
}


/// Perform *`blocks`* SHA‑256 transformations over consecutive 64‑byte chunks.
///
/// # Safety
/// * `s` **must** point to **≥ 8** writable `u32` words.
/// * `chunk` **must** point to **64 × `blocks`** readable bytes.
/// * The two regions must not overlap.
#[inline]
pub fn sha256_transform(s: *mut u32, mut chunk: *const u8, mut blocks: usize) {
    trace!(
        target: "sha256",
        total_blocks = blocks,
        "sha256_transform: starting batch"
    );

    while blocks != 0 {
        sha256_transform_block(s, chunk);
        chunk = chunk.add(64);
        blocks -= 1;

        trace!(
            target: "sha256",
            remaining_blocks = blocks,
            "sha256_transform: completed block"
        );
    }
}


/// Fallback block‑transform stub; used only when no optimised
