    schedule_and_round!(0xefbe4786, w1,  w15, w10, w2,  h, a, b, c, d, e, f, g);
    schedule_and_round!(0x0fc19dc6, w2,  w0,  w11, w3,  g, h, a, b, c, d, e, f);
