pub fn lock_or_unlock(fd: i32, lock: bool) -> i32 {
    trace!(
        fd,
        lock,
        "lock_or_unlock: preparing POSIX file lock operation"
    );

    unsafe {
        // SAFETY: We zero-initialize the flock structure before use, then
        // populate the fields required by fcntl(F_SETLK, ...).
        let mut file_lock_info: libc::flock = std::mem::zeroed();

        file_lock_info.l_type = if lock {
            libc::F_WRLCK as libc::c_short
        } else {
            libc::F_UNLCK as libc::c_short
        };
        file_lock_info.l_whence = libc::SEEK_SET as libc::c_short;
        file_lock_info.l_start = 0;
        file_lock_info.l_len = 0; // Lock/unlock entire file.

        let result = libc::fcntl(fd, libc::F_SETLK, &file_lock_info);

        if result == 0 {
            debug!(
                fd,
                lock,
                "lock_or_unlock: fcntl(F_SETLK) succeeded"
            );
        } else {
            warn!(
                fd,
                lock,
                result,
                "lock_or_unlock: fcntl(F_SETLK) failed"
            );
        }

        result
    }
}


pub fn posix_error(context: &String, error_number: i32) -> crate::Status {
    use std::ffi::CStr;

    trace!(
        context = %context,
        error_number,
        "posix_error: building Status from POSIX errno"
    );

    let message = unsafe {
        let ptr = libc::strerror(error_number);
        if ptr.is_null() {
            // Fallback when strerror returns null, which should be rare.
            "<unknown-posix-error>".to_string()
        } else {
            CStr::from_ptr(ptr)
                .to_string_lossy()
                .into_owned()
        }
    };

    let ctx_slice = Slice::from(context);
    let msg_slice = Slice::from(&message);

    let status = if error_number == libc::ENOENT {
        Status::not_found(&ctx_slice, Some(&msg_slice))
    } else {
        Status::io_error(&ctx_slice, Some(&msg_slice))
    };

    debug!(
        context = %context,
        error_number,
        status_str = %status.to_string(),
        "posix_error: created status"
    );

    status
}


/// Return the maximum number of read-only
/// files to keep open.
/// 
pub fn max_open_files() -> i32 {
    use std::sync::atomic::Ordering;

    trace!("max_open_files: start");

    let cached = OPEN_READ_ONLY_FILE_LIMIT.load(Ordering::SeqCst);
    if cached >= 0 {
        debug!(
            value = cached,
            "max_open_files: using cached open-file limit"
        );
        return cached;
    }

    let mut computed: i32;

    unsafe {
        let mut rlim: libc::rlimit = libc::rlimit {
            rlim_cur: 0,
            rlim_max: 0,
        };

        let rc = libc::getrlimit(libc::RLIMIT_NOFILE, &mut rlim as *mut libc::rlimit);

        if rc != 0 {
            // getrlimit failed, fall back to a conservative default.
            computed = 50;
            warn!(
                rc,
                "max_open_files: getrlimit(RLIMIT_NOFILE) failed, using fallback"
            );
        } else if rlim.rlim_cur == libc::RLIM_INFINITY {
            computed = i32::MAX;
            debug!(
                rlim_cur = rlim.rlim_cur as u64,
                "max_open_files: RLIM_INFINITY, using i32::MAX"
            );
        } else {
            let cur = rlim.rlim_cur;
            // Allow use of 20% of available descriptors for read-only files.
            let twenty_percent = cur / 5;
            if twenty_percent == 0 {
                computed = 50;
                debug!(
                    rlim_cur = cur as u64,
                    "max_open_files: RLIMIT_NOFILE too small, using fallback"
                );
            } else if twenty_percent > i32::MAX as u64 {
                computed = i32::MAX;
                debug!(
                    rlim_cur = cur as u64,
                    computed,
                    "max_open_files: capped computed limit at i32::MAX"
                );
            } else {
                computed = twenty_percent as i32;
                debug!(
                    rlim_cur = cur as u64,
                    computed,
                    "max_open_files: computed open-file limit from RLIMIT_NOFILE"
                );
            }
        }
    }

    OPEN_READ_ONLY_FILE_LIMIT.store(computed, Ordering::SeqCst);

    debug!(
        value = computed,
        "max_open_files: finalized open-file limit"
    );
    computed
}

impl<EnvType> Default for SingletonEnv<EnvType> where
    EnvType: Env + Default + 'static {

    fn default() -> Self {
        #[cfg(debug_assertions)]
        {
            SINGLETON_ENV_INITIALIZED.store(
                true,
                std::sync::atomic::Ordering::Relaxed,
            );
        }

        trace!("SingletonEnv::default: constructing EnvType instance");

        let env_impl: EnvType = EnvType::default();
        let env_rc: Rc<RefCell<dyn Env>> = Rc::new(RefCell::new(env_impl));

        SingletonEnv {
            env_rc,
            _marker: std::marker::PhantomData,
        }
    }
}

impl<EnvType> SingletonEnv<EnvType> where
    EnvType: Env + Default + 'static {

    /// Return the underlying Env as a shared handle.
    pub fn env(&self) -> Rc<RefCell<dyn Env>> {
        trace!("SingletonEnv::env: cloning Env Rc");
        self.env_rc.clone()
    }

    /// Assert that no SingletonEnv has been constructed yet.
    /// This is only enforced in debug builds to mirror the
    /// original NDEBUG-gated checks.
    pub fn assert_env_not_initialized() {
        #[cfg(debug_assertions)]
        {
            let already = SINGLETON_ENV_INITIALIZED
                .load(std::sync::atomic::Ordering::Relaxed);

            trace!(
                already_initialized = already,
                "SingletonEnv::assert_env_not_initialized (debug build)"
            );

            assert!(
                !already,
                "SingletonEnv::assert_env_not_initialized: Env already initialized"
            );
        }

        #[cfg(not(debug_assertions))]
        {
            trace!(
                "SingletonEnv::assert_env_not_initialized: no-op in release builds"
            );
        }
    }
}

impl PosixLockTable {

    #[LOCKS_EXCLUDED(mu_)]
    pub fn insert(&mut self, fname: &str) -> bool {
        trace!(
            file = fname,
            "PosixLockTable::insert: acquiring mutex"
        );

        let mut guard = self.mu.lock();

        let inserted = guard.locked_files.insert(fname.to_string());

        debug!(
            file = fname,
            inserted,
            "PosixLockTable::insert: updated locked file set"
        );

        inserted
    }

    #[LOCKS_EXCLUDED(mu_)]
    pub fn remove(&mut self, fname: &str) {
        trace!(
            file = fname,
            "PosixLockTable::remove: acquiring mutex"
        );

        let mut guard = self.mu.lock();

        let removed = guard.locked_files.remove(fname);

        debug!(
            file = fname,
            removed,
            "PosixLockTable::remove: updated locked file set"
        );
    }
}

impl Default for PosixLockTable {

    fn default() -> Self {
        trace!("PosixLockTable::default: creating new lock table");
        PosixLockTable {
            mu: Mutex::new(PosixLockTableInner::default()),
        }
    }
}

