#[derive(    Clone,    Debug,    Getters,    Setters,    Builder,)]
#[getset(get = "pub", set = "pub")]
pub struct BloomFilterPolicy  {
    bits_per_key: usize,
    k:            usize,
}


#[derive(    Debug,    Getters,    Setters,    Builder)]
#[getset(get = "pub", set = "pub")]
pub struct BloomTest  {
    policy: BloomFilterPolicy,
    filter: Vec<u8>,
    keys:   Vec<Vec<u8>>,
}

pub fn new_bloom_filter_policy(bits_per_key_: i32) -> Box<dyn FilterPolicy> {
    tracing::info!(
        bits_per_key = bits_per_key_,
        "new_bloom_filter_policy: constructing boxed BloomFilterPolicy"
    );
    Box::new(BloomFilterPolicy::new(bits_per_key_))
}


pub fn bloom_hash(key_: &Slice) -> u32 {
    let data_ptr: *const u8 = *key_.data();
    let len: usize          = *key_.size();

    debug!(
        key_length = len,
        "bloom_hash: computing LevelDB Bloom hash for key Slice"
    );

    if data_ptr.is_null() || len == 0 {
        debug!("bloom_hash: key is empty, hashing empty slice");
        return leveldb_hash(std::ptr::null(), 0, 0xbc9f1d34);
    }

    let result = leveldb_hash(data_ptr, len, 0xbc9f1d34);

    trace!(hash = result, "bloom_hash: returning hash value");
    result
}


pub fn next_length(length: i32) -> i32 {
    let mut len = length;

    if len < 10 {
        len += 1;
    } else if len < 100 {
        len += 10;
    } else if len < 1_000 {
        len += 100;
    } else {
        len += 1_000;
    }

    len
}


pub fn encode_fixed32_to_bytes(value: u32) -> [u8; 4] {
    trace!(
        value,
        "encode_fixed32_to_bytes: encoding u32 to little-endian bytes"
    );
    value.to_le_bytes()
}


pub fn encode_fixed32_into(value: u32, buffer: &mut [u8; 4]) {
    let bytes = encode_fixed32_to_bytes(value);
    buffer.copy_from_slice(&bytes);
}


pub fn key(i: i32, buffer: *mut u8) -> Slice {
    debug!(
        i,
        raw_buffer_ptr = ?buffer,
        "key(): constructing Slice key from encoded i32"
    );

    let encoded = encode_fixed32_to_bytes(i as u32);

    if buffer.is_null() {
        error!(
            "key(): buffer pointer is null; returning empty Slice via null pointer/zero length"
        );
        unsafe { Slice::from_ptr_len(std::ptr::null(), 0) }
    } else {
        unsafe {
            std::ptr::copy_nonoverlapping(encoded.as_ptr(), buffer, encoded.len());
            Slice::from_ptr_len(buffer as *const u8, encoded.len())
        }
    }
}

impl BloomFilterPolicy {

    /// Helper that operates entirely on byte slices. Used by the KeyMayMatch trait impl
    /// and by tests.
    pub fn key_may_match_bytes(&self, key: &[u8], bloom_filter: &[u8]) -> bool {
        trace!(
            key_len = key.len(),
            filter_len = bloom_filter.len(),
            "BloomFilterPolicy::key_may_match_bytes called"
        );

        let len = bloom_filter.len();
        if len < 2 {
            debug!(
                "BloomFilterPolicy::key_may_match_bytes: filter too short (len < 2), returning false"
            );
            return false;
        }

        let bits: usize = (len - 1) * 8;
        let k: usize = bloom_filter[len - 1] as usize;

        // The encoded k allows us to read filters generated with different parameters.
        if k > 30 {
            warn!(
                k,
                "BloomFilterPolicy::key_may_match_bytes: k > 30 reserved for new encodings; treating as match"
            );
            return true;
        }

        let mut h: u32 = leveldb_hash(
            key.as_ptr(),
            key.len(),
            0xbc9f1d34,
        );

        // Rotate right 17 bits
        let delta: u32 = (h >> 17) | (h << 15);

        for probe in 0..k {
            let bitpos: usize = (h as u64 % bits as u64) as usize;
            let byte_index = bitpos / 8;
            let bit_mask = 1u8 << (bitpos % 8);

            if (bloom_filter[byte_index] & bit_mask) == 0 {
                trace!(
                    probe,
                    bitpos,
                    byte_index,
                    bit_mask,
                    "BloomFilterPolicy::key_may_match_bytes: missing bit -> definite non-match"
                );
                return false;
            }

            h = h.wrapping_add(delta);
        }

        trace!(
            "BloomFilterPolicy::key_may_match_bytes: all bits set -> possible match"
        );
        true
    }
}

impl Named for BloomFilterPolicy {

    fn name(&self) -> std::borrow::Cow<'_, str> {
        // Keep the original LevelDB name for cross-language compatibility.
        std::borrow::Cow::Borrowed("leveldb.BuiltinBloomFilter2")
    }
}

impl FilterPolicy for BloomFilterPolicy {}

impl BloomFilterPolicy {

    pub fn new(bits_per_key_: i32) -> Self {
        let mut bits_per_key = if bits_per_key_ < 0 {
            warn!(
                bits_per_key = bits_per_key_,
                "BloomFilterPolicy::new received negative bits_per_key; treating as zero"
            );
            0usize
        } else {
            bits_per_key_ as usize
        };

        if bits_per_key == 0 {
            // Having at least one bit per key is more sensible than a degenerate filter.
            bits_per_key = 1;
        }

        // We intentionally round down to reduce probing cost a little bit.
        // 0.69 =~ ln(2)
        let mut k = (bits_per_key as f64 * 0.69_f64) as usize;
        if k < 1 {
            k = 1;
        }
        if k > 30 {
            k = 30;
        }

        info!(
            bits_per_key,
            k,
            "BloomFilterPolicy::new constructed policy"
        );

        BloomFilterPolicy {
            bits_per_key,
            k,
        }
    }
}

impl CreateFilter for BloomFilterPolicy {

    fn create_filter(
        &self,
        keys:  *const Slice,
        n:     i32,
        dst:   &mut Vec<u8>
    )
    {
        debug!(
            raw_keys_ptr = ?keys,
            n,
            current_len = dst.len(),
            "BloomFilterPolicy::CreateFilter::create_filter invoked"
        );

        let n_usize: usize = if n <= 0 { 0 } else { n as usize };

        if n_usize == 0 {
            // Still generate a minimal filter (64 bits) for consistency with
            // the original C++ implementation.
            self.create_filter_from_bytes(&[], dst);
            return;
        }

        if keys.is_null() {
            error!(
                "BloomFilterPolicy::CreateFilter::create_filter: non-zero n but keys pointer is null; aborting"
            );
            return;
        }

        let key_slices: &[Slice] =
            unsafe { std::slice::from_raw_parts(keys, n_usize) };
        let mut key_refs: Vec<&[u8]> = Vec::with_capacity(n_usize);

        for (index, slice) in key_slices.iter().enumerate() {
            let data_ptr: *const u8 = *slice.data();
            let len: usize          = *slice.size();

            if data_ptr.is_null() || len == 0 {
                trace!(
                    index,
                    "BloomFilterPolicy::CreateFilter::create_filter: encountered empty key slice"
                );
                key_refs.push(&[]);
            } else {
                let bytes: &[u8] =
                    unsafe { std::slice::from_raw_parts(data_ptr, len) };
                key_refs.push(bytes);
            }
        }

        self.create_filter_from_bytes(&key_refs, dst);
    }
}

impl KeyMayMatch for BloomFilterPolicy {

    fn key_may_match(&self, key_: &Slice, bloom_filter: &Slice) -> bool {
        let key_ptr: *const u8 = *key_.data();
        let key_len: usize     = *key_.size();
        let key_bytes: &[u8] = if key_ptr.is_null() || key_len == 0 {
            &[]
        } else {
            unsafe { std::slice::from_raw_parts(key_ptr, key_len) }
        };

        let filter_ptr: *const u8 = *bloom_filter.data();
        let filter_len: usize     = *bloom_filter.size();
        let filter_bytes: &[u8] = if filter_ptr.is_null() || filter_len == 0 {
            &[]
        } else {
            unsafe { std::slice::from_raw_parts(filter_ptr, filter_len) }
        };

        self.key_may_match_bytes(key_bytes, filter_bytes)
    }
}

impl Default for BloomTest {

    fn default() -> Self {
        info!(
            "BloomTest::default: initializing fixture with bits_per_key = 10"
        );
        BloomTest {
            policy: BloomFilterPolicy::new(10),
            filter: Vec::new(),
            keys:   Vec::new(),
        }
    }
}

impl Drop for BloomTest {

    fn drop(&mut self) {
        debug!(
            remaining_keys = self.keys.len(),
            filter_size    = self.filter.len(),
            "Dropping BloomTest fixture"
        );
    }
}

impl BloomTest {

    pub fn reset(&mut self) {
        debug!(
            existing_keys        = self.keys.len(),
            existing_filter_size = self.filter.len(),
            "BloomTest::reset called"
        );
        self.keys.clear();
        self.filter.clear();
    }

    pub fn add_key_slice(&mut self, key: &[u8]) {
        trace!(
            key_len = key.len(),
            "BloomTest::add_key_slice: adding raw key bytes"
        );
        self.keys.push(key.to_vec());
    }

    pub fn add_key_str(&mut self, key: &str) {
        trace!(
            key,
            "BloomTest::add_key_str: adding string key"
        );
        self.add_key_slice(key.as_bytes());
    }

    pub fn add_key_slice_object(&mut self, s: &Slice) {
        let data_ptr: *const u8 = *s.data();
        let len: usize          = *s.size();

        if data_ptr.is_null() || len == 0 {
            trace!(
                "BloomTest::add_key_slice_object: empty or null Slice provided"
            );
            self.keys.push(Vec::new());
        } else {
            let bytes: &[u8] =
                unsafe { std::slice::from_raw_parts(data_ptr, len) };
            self.keys.push(bytes.to_vec());
        }
    }

    pub fn add(&mut self, s: &Slice) {
        self.add_key_slice_object(s);
    }

    pub fn build(&mut self) {
        debug!(
            num_keys = self.keys.len(),
            "BloomTest::build: constructing bloom filter from accumulated keys"
        );

        let mut key_refs: Vec<&[u8]> = Vec::with_capacity(self.keys.len());
        for key in &self.keys {
            key_refs.push(key.as_slice());
        }

        self.filter.clear();
        self.policy
            .create_filter_from_bytes(&key_refs, &mut self.filter);
        self.keys.clear();

        #[cfg(test)]
        {
            use crate::bloom_test_key::VERBOSE;
            if VERBOSE >= 2 {
                self.dump_filter();
            }
        }

        debug!(
            filter_size = self.filter.len(),
            "BloomTest::build finished"
        );
    }

    pub fn filter_size(&self) -> usize {
        self.filter.len()
    }

    pub fn dump_filter(&self) {
        if self.filter.is_empty() {
            debug!(
                "BloomTest::dump_filter: filter is empty; nothing to dump"
            );
            return;
        }

        let mut repr = String::new();
        repr.push('F');
        repr.push('(');

        // Skip the last byte (k marker) for the visual representation.
        for (idx, byte) in self
            .filter
            .iter()
            .enumerate()
            .take(self.filter.len().saturating_sub(1))
        {
            for bit in 0..8 {
                let bit_set = (byte & (1u8 << bit)) != 0;
                repr.push(if bit_set { '1' } else { '.' });
            }
            if idx < self.filter.len().saturating_sub(2) {
                repr.push(' ');
            }
        }

        repr.push(')');

        debug!(
            filter_visual = %repr,
            "BloomTest::dump_filter: current bloom filter layout"
        );
    }

    pub fn matches_slice(&mut self, key: &[u8]) -> bool {
        if !self.keys.is_empty() {
            self.build();
        }

        let result = self.policy.key_may_match_bytes(key, &self.filter);

        trace!(
            key_len = key.len(),
            result,
            "BloomTest::matches_slice evaluated"
        );

        result
    }

    pub fn matches_str(&mut self, key: &str) -> bool {
        self.matches_slice(key.as_bytes())
    }

    pub fn false_positive_rate(&mut self) -> f64 {
        let mut buffer = [0u8; 4];
        let mut result_count: usize = 0;

        for i in 0..10_000_i32 {
            crate::bloom_test_key::encode_fixed32_into(
                (i + 1_000_000_000) as u32,
                &mut buffer,
            );
            if self.matches_slice(&buffer) {
                result_count += 1;
            }
        }

        let rate = result_count as f64 / 10_000.0_f64;

        info!(
            false_positives = result_count,
            rate,
            "BloomTest::false_positive_rate computed"
        );

        rate
    }
}

impl BloomFilterPolicy {

    /// Core implementation operating on raw byte slices and a mutable filter buffer.
    pub fn create_filter_from_bytes(&self, keys: &[&[u8]], dst: &mut Vec<u8>) {
        let num_keys = keys.len();

        debug!(
            num_keys,
            current_dst_len = dst.len(),
            bits_per_key = *self.bits_per_key(),
            k = *self.k(),
            "BloomFilterPolicy::create_filter_from_bytes called"
        );

        // Compute bloom filter size (in both bits and bytes).
        // For small n, enforce a minimum bloom filter length to avoid high FP rates.
        let mut bits: usize = num_keys.saturating_mul(*self.bits_per_key());
        if bits < 64 {
            bits = 64;
        }

        let bytes: usize = (bits + 7) / 8;
        bits = bytes * 8;

        let init_size = dst.len();
        dst.resize(init_size + bytes, 0u8);

        // Remember number of probes in filter.
        dst.push(*self.k() as u8);

        // The actual filter bytes are the newly-extended region (without the k trailer).
        {
            let array: &mut [u8] = &mut dst[init_size..init_size + bytes];

            for (index, key) in keys.iter().enumerate() {
                trace!(
                    key_index = index,
                    key_len = key.len(),
                    "BloomFilterPolicy::create_filter_from_bytes hashing key"
                );

                // Kirsch–Mitzenmacher double hashing.
                let mut h: u32 = leveldb_hash(
                    key.as_ptr(),
                    key.len(),
                    0xbc9f1d34,
                );
                let delta: u32 = (h >> 17) | (h << 15); // Rotate right 17 bits.

                for probe in 0..*self.k() {
                    let bitpos: usize = (h as u64 % bits as u64) as usize;
                    let byte_index = bitpos / 8;
                    let bit_mask = 1u8 << (bitpos % 8);

                    array[byte_index] |= bit_mask;

                    trace!(
                        key_index = index,
                        probe,
                        bitpos,
                        byte_index,
                        bit_mask,
                        "BloomFilterPolicy::create_filter_from_bytes set bit"
                    );

                    h = h.wrapping_add(delta);
                }
            }
        }

        debug!(
            total_bits = bits,
            total_bytes = bytes,
            final_dst_len = dst.len(),
            "BloomFilterPolicy::create_filter_from_bytes finished"
        );
    }
}


